insert into helpcenter_topic(topicid, description, content, context, active, type, external_link, createdby, createdate) values (5, 'What is a Table Summary Page?', '<p>The table summary page makes it easier for OvalEdge users to go through each table and find relevant information. The table summary page displays statistics like top values, null count, distinct count, minimum&nbsp;and maximum values for a selected table.</p><p><br><!--{cke_protected}%3Cmeta%20charset%3D%22utf-8%22%20%2F%3E--><img data-cke-saved-src="oecss/images/helpcenter/pointer_hand.png" src="oecss/images/helpcenter/pointer_hand.png" style="width: 20px; height: 20px;"><strong>&nbsp;Click the <img src="oecss/images/helpcenter/ninedots1.png" id="photo-id" style="width: 25px; height: 25px;" data-cke-saved-src="oecss/images/helpcenter/ninedots1.png">&nbsp;icon to see the list of tasks&nbsp;on the Table Summary page.</strong></p><p><!--{cke_protected}%3Cmeta%20charset%3D%22utf-8%22%20%2F%3E--></p><p dir="ltr"><!--{cke_protected}%3Cmeta%20charset%3D%22utf-8%22%20%2F%3E--></p><p dir="ltr"><!--{cke_protected}%3Cmeta%20charset%3D%22utf-8%22%20%2F%3E--></p>', 'table', true, '', '', 'admin', now());
