insert into helpcenter_topic(topicid, description, content, context, active, type, external_link, createdby, createdate) values (6, 'What is a File Summary Page?', '<p>File Summary Page is similar to the database and table summary page. It makes the users to navigate through different file systems and provide statistics on folders and files.</p><p dir="ltr"><b id="docs-internal-guid-ebabf4ad-7fff-078d-172b-4f73e5bf96aa"><img style="width: 20px; height: 20px;" src="oecss/images/helpcenter/pointer_hand.png" data-cke-saved-src="oecss/images/helpcenter/pointer_hand.png"></b><b>&nbsp;OvalEdge supports the following file systems:</b></p><p dir="ltr"><b>Comma Separated Value (.csv),&nbsp;Microsoft Excel (.xlsx, .xls), Optimized Row Columnar (.orc),&nbsp;</b><strong>Simple JSON, Nested JSON,&nbsp;XML, Parquet, AVRO</strong></p>', 'file', true, '', '', 'admin', now());
