insert into helpcenter_topic(topicid, description, content, context, active, type, external_link, createdby, createdate) values (7, 'What is a Report View?', '<p>A report tab is a summary&nbsp;page that lists all the reports and dashboards created both&nbsp;in&nbsp;the OvalEdge application and from other visualization tools.&nbsp;<br></p><p><br>A report search allows users to search for a particular report and/or dashboard stored in the entire data catalog. A user can perform a search by selecting a specific report&nbsp;name if known or filter all the contents within a specific report group.</p><p><!--{cke_protected}%3Cmeta%20charset%3D%22utf-8%22%20%2F%3E--></p>', 'report', true, '', '', 'admin', now());
