insert into helpcenter_topic(topicid, description, content, context, active, type, external_link, createdby, createdate) values (8, 'What is a Data Story?', '<p>Data story enables users to create, modify, and organize the business knowledge within&nbsp;OvalEdge in real-time. This makes all tribal knowledge neatly documented and available for data discovery and re-use&nbsp;within an organization.</p><p><!--{cke_protected}%3Cmeta%20charset%3D%22utf-8%22%20%2F%3E--></p><p dir="ltr"><!--{cke_protected}%3Cmeta%20charset%3D%22utf-8%22%20%2F%3E--><b id="docs-internal-guid-e3405892-7fff-5a51-2ba0-a09f157c2258"><img data-cke-saved-src="oecss/images/helpcenter/pointer_hand.png" src="oecss/images/helpcenter/pointer_hand.png" style="width: 20px; height: 20px;"></b><b>&nbsp;Create a Story Zone&nbsp;before writing a data story.</b></p>', 'story', true, '', '', 'admin', now());
