insert into helpcenter_topic(topicid, description, content, context, active, type, external_link, createdby, createdate) values (9, 'What is a Query interface?', '<p>The Query Interface&nbsp;allows users to perform additional operations on a cataloged query.<br>You can use the query editor on this page for the following purposes:</p><ul><li>Create, execute, and save new queries.</li><li>Build reports&nbsp;and visualize query results</li><li>Get the recommendations on query lineage and references.</li><li>Uncatalog the saved queries</li></ul><p><!--{cke_protected}%3Cmeta%20charset%3D%22utf-8%22%20%2F%3E--><b id="docs-internal-guid-19efa6e9-7fff-b0c1-c85e-53afa31e63db"><img data-cke-saved-src="oecss/images/helpcenter/clap-hands.png" src="oecss/images/helpcenter/clap-hands.png" style="width: 20px; height: 20px;"></b><b>&#8203;&#8203;H</b><strong>ey! I located the Query editor through the query search page.</strong></p>', 'query', true, '', '', 'admin', now());
