insert into helpcenter_topic(topicid, description, content, context, active, type, external_link, createdby, createdate) values (14, 'What is a Domain?', '<p>A domain is a high-level business unit of an organization. In an Organization there can be multiple domains in which business entities like Data assets, Business Glossary, PII&#8217;s are defined. Each domain can implement its own hierarchy that is independent of structures in other domains.</p><p><strong><span style="font-size:11pt"><span style="line-height:107%"><span style="font-family:Calibri,sans-serif"><span style="font-size:12.0pt"><span style="line-height:107%"><span style="font-family:&quot;Times New Roman&quot;,serif"><img src="oecss/images/helpcenter/pointer_hand.png" id="photo-id" style="width: 25px; height: 25px;" data-cke-saved-src="oecss/images/helpcenter/pointer_hand.png">&nbsp;Each domain should have a default Data Owner, Data Steward&nbsp;and a Publisher.</span></span></span></span></span></span></strong></p><p><br></p>', 'domains', true, '', '', 'admin', now());
