insert into helpcenter_topic(topicid, description, content, context, active, type, external_link, createdby, createdate) values (15, 'What is a Business Glossary?', '<p><!--{cke_protected}%3Cmeta%20charset%3D%22utf-8%22%20%2F%3E-->The Glossary of the terms is a collection of terms that has a standardized definition. The term and its definition&nbsp;used in your organization serve as a reference for the business context of the data at the enterprise level. Each term can be associated with other valuable metadata like physical metadata, lineage, data metrics&nbsp;etc. You share these terms across an organization to ensure that all the stakeholders understand the terminology.&nbsp;</p>', 'glossary', true, '', '', 'admin', now());
