insert into helpcenter_topic(topicid, description, content, context, active, type, external_link, createdby, createdate) values (16, 'What is a Crawling?', '<p><strong>Crawling</strong> is a process of collecting information about the data from various data sources like on-premise and cloud databases, Hadoop, visualization software&nbsp;and file systems.&nbsp;When an OvalEdge crawler connects to a data source, it collects and catalogs all the metadata of data elements like schema name, table name, description, column name, column type, column comments, views and procedures and existing relationships in the remote server. It then stores it in the OvalEdge data repository.</p>', 'crawler', true, '', '', 'admin', now());
