insert into helpcenter_topic(topicid, description, content, context, active, type, external_link, createdby, createdate) values (19, 'What are Custom Fields?', '<p style="margin:0in">Custom fields give the user an opportunity to add more metadata content for each of the data objects. OvalEdge data catalog provides a list of standard fields like Minimum/Maximum count, Density, Null density, distinct, etc. If a user wants to add more information about data like Median, Sum, etc, they can create additional fields from the custom field module.</p><p style="margin:0in"><br></p><p style="margin:0in"><b id="docs-internal-guid-4a765a55-7fff-8ea9-6dd2-3212ca57ead4"><img data-cke-saved-src="oecss/images/helpcenter/pointer_hand.png" src="oecss/images/helpcenter/pointer_hand.png" style="width: 20px; height: 20px;"></b><span style="font-size:11.0pt"><span style="font-family:Calibri"><strong>Only OvalEdge Admins can create custom fields.&nbsp;</strong></span></span></p>', 'customfields', true, '', '', 'admin', now());
