insert into helpcenter_topic(topicid, description, content, context, active, type, external_link, createdby, createdate) values (20, 'How to view all the tasks that are scheduled in OvalEdge?', '<p>This module allows tracking the jobs that are scheduled and submitted to execute on a particular date and time in OvalEdge. The list of Schedules include:</p><ul><li><strong>Schedule Description -</strong> Gives the schedule description of what tasks are scheduled to perform.</li><li><strong>Cron Entry - </strong>Displays the date and time the task is scheduled to run.</li><li><strong>User&nbsp;</strong>- Username who scheduled this task to run.</li><li><strong>Date Modified&nbsp;</strong>- The&nbsp;last date when any modifications are done on this scheduled task in the selected Time Zone.</li><li><strong>Active&nbsp;</strong>- Whether this task is active.</li><li><strong>Class Name&nbsp;</strong>- Location where this job is generated.</li></ul>', 'schedule', true, '', '', 'admin', now());
