insert into helpcenter_topic(topicid, description, content, context, active, type, external_link, createdby, createdate) values (21, 'What is Configuration Page?', '<p>There are several different configurations that we need to create&nbsp;before you start using OvalEdge. The configuration page allows <strong>ADMIN</strong> to set up the following and many more..</p><ul><li>Admin credentials</li><li>Various drivers jar path locations to ingest data into OvalEdge</li><li>SMTP server preferences to send messages between servers</li><li>Define Admin role, Tag role, Crawler role, and Public&nbsp;role for predefined access</li><li>Server and Port details</li><li>OvalEdge application language</li></ul>', 'configuration', true, '', '', 'admin', now());
