insert into helpcenter_topic(topicid, description, content, context, active, type, external_link, createdby, createdate) values (24, 'How does OvalEdge Home Page assist users?', '<p><span style="font-size:12pt;"><span style=''font-family:"Times New Roman",serif;''>OvalEdge HomePage is a dashboard view that displays a list of main features allowing users to navigate quickly to the page that interests them.</span></span></p><p><span style="font-size:12pt;"><span style=''font-family:"Times New Roman",serif;''>OvalEdge Home Page&nbsp;is divided into five sections.</span></span></p><ul><li><span style="font-size:12pt;"><span style=''font-family:"Times New Roman",serif;''><strong>Left Menu Bar&nbsp;</strong>- Displays a list of all modules in the application.</span></span></li><li><span style="font-size:12pt;"><span style=''font-family:"Times New Roman",serif;''><strong>Visual Snapshot&nbsp;</strong>- A numeric and a visual snapshot of the total count of Tables, Reports, and Files &amp; Folders helps users to know how many database objects are associated with their account.&nbsp;</span></span></li><li><span style="font-size:12pt;"><span style=''font-family:"Times New Roman",serif;''><strong>Helpful Resources&nbsp;</strong>- Provides access to the service desk and has an app help center.</span></span></li><li><span style="font-size:12pt;"><span style=''font-family:"Times New Roman",serif;''><strong>Top Menu bar&nbsp;</strong>- Can see the notifications, do a quick search, &nbsp;and bookmark favorite pages.</span></span></li><li><span style="font-size:12pt;"><span style=''font-family:"Times New Roman",serif;''><strong>Tags&nbsp;</strong>- Tagging allows users to categorize the data and organize them functionally which makes it easier to find.</span></span></li><li><span style="font-size:12pt;"><span style=''font-family:"Times New Roman",serif;''><strong>Catalog Highlights&nbsp;</strong>- Users can select the Catalog Highlights to see the Most Recent or top data objects like Tables, Queries, Reports, Files and Folders that are used.</span></span><!--{cke_protected}%3Cmeta%20charset%3D%22utf-8%22%20%2F%3E--></li></ul><p dir="ltr"><!--{cke_protected}%3Cmeta%20charset%3D%22utf-8%22%20%2F%3E--><br></p><p dir="ltr"><!--{cke_protected}%3Cmeta%20charset%3D%22utf-8%22%20%2F%3E--><b id="docs-internal-guid-155332a2-7fff-fd39-1b98-1b1018b05709"><img data-cke-saved-src="oecss/images/helpcenter/pointer_hand.png" src="oecss/images/helpcenter/pointer_hand.png" style="width: 20px; height: 20px;" class="fr-fil fr-dib"></b><strong>&nbsp;Do you know, you can quickly see a combination of recent and most searched database objects on your homepage? Toggle between Tags and Catalog Highlights to unwind the recent and most visited data objects in the application.</strong></p><p dir="ltr"><br></p>', 'home', true, '', '', 'admin', now());
