insert into helpcenter_topic(topicid, description, content, context, active, type, external_link, createdby, createdate) values (25, 'What is an Inbox?', '<p>Inbox provides a platform to collaborate with the users and user groups within an organization. The Inbox feature in OvalEdge allows a user to have a consolidated view of all the messages  received/sent across by other users within the organization. The messages include <br></p><ul><li>Colloboration messages on objects<br></li><li>System Alerts</li><li>Watchlist Alerts</li><li>Data Quality Rule Alerts</li><li>Service Tickets</li></ul>', 'messages', true, '', '', 'admin', now());
