insert into helpcenter_topic(topicid, description, content, context, active, type, external_link, createdby, createdate) values (26, 'What is a File Manager?', '<p><span style="font-size:12.0pt"><span style="line-height:107%"><span style="font-family:&quot;Times New Roman&quot;,serif">The file manager window enables users to display and manage their files and folders. OvalEdge File Manager helps to efficiently organize all your files that&nbsp;are stored in your local Network File System(NFS) server, Hadoop cluster, Cloud storage like Amazon S3, Azure data lake, Google cloud storage. Each file can be cataloged within the file manager. </span></span></span>&#8203;&#8203;&#8203;&#8203;&#8203;&#8203;&#8203;</p>', 'filemanager', true, '', '', 'admin', now());
