insert into helpcenter_topic(topicid, description, content, context, active, type, external_link, createdby, createdate) values (27, 'What is a Query Sheet?', '<p><!--{cke_protected}%3Cmeta%20charset%3D%22utf-8%22%20%2F%3E-->OvalEdge&nbsp;Query sheet is an interactive interface that allows users to build, summarize, save&nbsp;and run queries as per your business requirements. This is a lightweight query tool that provides users without familiar SQL knowledge&nbsp;to perform query operations like aggregation, joins, unions, grouping, sorting&nbsp;and filtering with a single click buttons.</p><p><img data-fr-image-pasted="true" src="https://helpcenter.ovaledge.net/oecss/images/helpcenter/light_bulb1.png" data-cke-saved-src="oecss/images/helpcenter/light_bulb1.png" style="box-sizing: border-box; border-width: 0px; border-style: solid; border-color: currentcolor; max-width: 100%; height: 20px; cursor: pointer; padding: 0px 1px; text-align: left; width: 20px;" class="fr-fil fr-dib"> See this article <a href="https://support.ovaledge.com/query-sheet-cheat-sheet" rel="noopener noreferrer" target="_blank">QuerySheet cheat sheet</a> to know more about the data operations used in Query Sheet.</p>', 'querysheet', true, '', '', 'admin', now());
