insert into helpcenter_topic(topicid, description, content, context, active, type, external_link, createdby, createdate) values (28, 'What is Database Exchange?', '<p>Database exchange is to exchange specific or all&nbsp;the data from a source table into a target table in the same instance or in a different instance.</p><p><!--{cke_protected}%3Cmeta%20charset%3D%22utf-8%22%20%2F%3E--><b id="docs-internal-guid-6f72afdb-7fff-7cb2-eb84-4c95505ce9ca"><img data-cke-saved-src="oecss/images/helpcenter/pointer_hand.png" src="oecss/images/helpcenter/pointer_hand.png" style="width: 25px; height: 25px;">U</b><strong>sers mostly use this application to transfer data from a source table to the staging table in the OvalEdge database.</strong></p>', 'dbex', true, '', '', 'admin', now());
