insert into helpcenter_topic(topicid, description, content, context, active, type, external_link, createdby, createdate) values (34, 'What is a Project?', '<p>OvalEdge Project is a feature available for all the end-users within an organization to manage and organize data objects without the need to have an admin role or a tag&nbsp;role. Projects are used by a small group of users working on a similar task to collaborate on their data objects. You can also save some project notes to understand the project.</p><p><!--{cke_protected}%3Cmeta%20charset%3D%22utf-8%22%20%2F%3E--><b id="docs-internal-guid-4a765a55-7fff-8ea9-6dd2-3212ca57ead4"><img data-cke-saved-src="oecss/images/helpcenter/pointer_hand.png" src="oecss/images/helpcenter/pointer_hand.png" style="width: 20px; height: 20px;"></b><strong> For adding any content to your project you need to select a default project.</strong></p>', 'project', true, '', '', 'admin', now());
