insert into helpcenter_topic(topicid, description, content, context, active, type, external_link, createdby, createdate) values (35, 'What is a Job Workflow?', '<p>A&nbsp;workflow defines a repeatable pattern of a series of specific tasks or jobs (as we call in OvalEdge) that gets completed regularly.&nbsp;</p><p dir="ltr">With workflows, users can:</p><ul dir="ltr"><li>Run and troubleshoot jobs independently with status feedback.</li><li><strong></strong>Schedule jobs to run periodically.</li><li><strong></strong>Run multiple jobs in parallel for efficient version testing.</li></ul>', 'jobworkflow', true, '', '', 'admin', now());
