insert into helpcenter_topic(topicid, description, content, context, active, type, external_link, createdby, createdate) values (38, 'What are Advanced Tools?', '<p>The advanced tools include custom features&nbsp;that help OvalEdge users to explore and manage the data. These features are available when the advanced tools tab is selected.</p><p><br>The advanced tools include:</p><ul><li><strong>Impact Analysis -</strong> To find the impact of any metadata or data changes that happens on files/ queries/ chart.</li><li><strong>Alerts -&nbsp;</strong>Notifies users on any specific data activity.</li><li><strong>Lineage Maintenance -&nbsp;</strong>To maintain the lineage of a file, chart, and table.</li><li><strong><strong>Build Auto Lineage -&nbsp;</strong></strong>Use this feature to automatically build lineage for different connections.<strong><br></strong></li><li><strong>Compare Schemas -</strong> A schema comparison captures and displays information like any column index, data type, or column length change.</li><li><strong><strong>Compare Profile Results -</strong>&nbsp;</strong>To compare the profile statistics between two tables to get the metadata changes over a period.<strong><br></strong></li><li><strong>Load Metadata from files&nbsp;</strong>- Use this feature to load the business description, glossary terms, data dictionary for different data elements like schema, tables, table-columns, etc.&nbsp;from .csv files.</li><li><strong>Data &amp; Metadata Changes -&nbsp;</strong>To view the changes done to both Data &amp; Metadata of the objects.</li><li><strong>Upload File Or Folder -&nbsp;</strong>Use this feature to upload your files or folders from your local.</li></ul>', 'advancedtools', true, '', '', 'admin', now());
