insert into helpcenter_topic(topicid, description, content, context, active, type, external_link, createdby, createdate) values (39, 'What is Impact Analysis?', '<p class="articlenote" style="margin:0in; margin-bottom:.0001pt; margin-right:0in; margin-left:0in"><span style="font-size:12pt"><span style="font-family:&quot;Times New Roman&quot;,serif">Impact Analysis helps to analyze the impact of changes in the application. It gives information about the affected objects due to the data movements within the&nbsp;application.<br>On complex projects with multiple data elements, it is time-consuming&nbsp;to manually determine where the data movement&nbsp;has happened. OvalEdge includes built-in functionality for end-to-end traceability and to perform impact analysis, that helps determine the scope of impact.&nbsp;Using this application, you can recognize where the data flows from source to destination.</span></span></p>', 'impactanalysis', true, '', '', 'admin', now());
