insert into helpcenter_topic(topicid, description, content, context, active, type, external_link, createdby, createdate) values (42, 'What is Query Catalog?', '<p><style type="text/css"><!--{cke_protected}{C}%3C!%2D%2Dtd%20%7Bborder%3A%201px%20solid%20%23ccc%3B%7Dbr%20%7Bmso-data-placement%3Asame-cell%3B%7D%2D%2D%3E--></style><style type="text/css"><!--{cke_protected}{C}%3C!%2D%2Dtd%20%7Bborder%3A%201px%20solid%20%23ccc%3B%7Dbr%20%7Bmso-data-placement%3Asame-cell%3B%7D%2D%2D%3E--></style>Query catalog means a collection of queries. OvalEdge provides a feature to search each catalog independently either by providing a query name or database name&#8203;&#8203;&#8203;&#8203;&#8203;&#8203;&#8203;.</p>', 'queries', true, '', '', 'admin', now());
