insert into helpcenter_topic(topicid, description, content, context, active, type, external_link, createdby, createdate) values (43, 'What is Compare Schema?', '<p style="margin:0in 0in 8pt; text-align:justify">The Compare Schema feature is used to compare the changes in the metadata between two database schemas. The source and target schema in the comparison&nbsp;can be&nbsp;the same or different schema. The results of the comparison display the new metadata changes(table names and column counts)&nbsp;that have happened between the compared dates.</p><p style="margin:0in 0in 8pt; text-align:justify">The differences between source and target appear in a grid for easy review. Click the change count to review each difference highlighted.</p>', 'comparedb', true, '', '', 'admin', now());
