insert into helpcenter_topic(topicid, description, content, context, active, type, external_link, createdby, createdate) values (44, 'What is Lineage Maintenance?', '<p>Data Lineage is a visual representation that helps to see where the data comes from, where it moves&nbsp;and what transformations it undergoes over time. It provides the ability to track, manage&nbsp;and view the data transformation along its path from source to destination.&#8203;&#8203;&#8203;&#8203;&#8203;&#8203;&#8203;</p><p>&#8203;&#8203;&#8203;&#8203;&#8203;&#8203;&#8203;Users can manually edit and update the Lineage of a table, file&nbsp;and report using this advanced feature.</p>', 'lineage', true, '', '', 'admin', now());
