insert into helpcenter_topic(topicid, description, content, context, active, type, external_link, createdby, createdate) values (45, 'What is the User Information list page?', '<p>In OvalEdge, we have a user page&nbsp;that&nbsp;stores information about users such as First name, Last name, Title, Email, Phone&nbsp;and address.<br>As soon as&nbsp;a user is created in&nbsp;OvalEdge, the user information is available on the user dashboard page.</p>', 'userlist', true, '', '', 'admin', now());


insert into helpcenter_topic(topicid, description, content, context, active, type, external_link, createdby, createdate) values (46, 'What is Data Exchange?', '<p>Data exchange is the process of sharing source data from one system into a target system. Data exchange allows sharing of data between different computer programs.<br></p>', 'dataingestion', true, '', '', 'admin', now());
