insert into helpcenter_topic(topicid, description, content, context, active, type, external_link, createdby, createdate) values (47, 'What does My Permissions Page display?', '<p><span style="font-size:12pt"><span style="font-family:&quot;Times New Roman&quot;,serif"><span style="color:black"><strong>My permissions</strong> module displays the list of all the data objects for only the users having&nbsp;the access as an Owner/Steward/Author. In this dashboard view, users can see a bar graph on the top right corner of this page. Each bar in the graph displays the total number of data objects actively used by that user based on the access date.&nbsp;<br>You can hover&nbsp;over the data points in the bar graph to get information about the usage of the data objects. If you need more information, you can navigate to each data object by selecting the data objects listed on this page.</span></span></span><span style="font-size:12pt"><span style="font-family:&quot;Times New Roman&quot;,serif"><span style="color:black"></span></span></span><span style="font-size:12pt"><span style="font-family:&quot;Times New Roman&quot;,serif"><span style="color:black"></span></span></span><br></p>', 'usage', true, '', '', 'admin', now());
