insert into helpcenter_topic(topicid, description, content, context, active, type, external_link, createdby, createdate) values (49, 'What is Build Auto Lineage?', '<p><!--{cke_protected}%3Cmeta%20charset%3D%22utf-8%22%20%2F%3E-->In OvalEdge, when building the lineage from queries, few queries might fail to parse the tables and columns associated with it to build the lineage. Those failed queries are viewed, manually corrected&nbsp;and processed using the&nbsp;<strong>&ldquo;Build Auto Lineage&rdquo;</strong> application.</p>', 'splineage', true, '', '', 'admin', now());
