insert into helpcenter_topic(topicid, description, content, context, active, type, external_link, createdby, createdate) values (51, 'What is the purpose of a Servicedesk module?', '<p style="margin:0in"><span style="font-size:11.0pt"><span style="font-family:Calibri">A&nbsp;<strong>Servicedesk </strong>is&nbsp;a module where&nbsp;user can raise&nbsp;a service request. A <strong>Service Request</strong>&nbsp;is a<strong> User request</strong>&nbsp;raised on the following:</span></span></p><p style="margin:0in"><span style="font-size:11.0pt"><span style="font-family:Calibri"></span></span><br></p><ul><li style="margin: 0in;"><strong>Data access&nbsp;</strong>- To grant data access permissions (like Data Preview, Data Read, and Data Write) on tables, files&nbsp;and reports.</li><li style="margin: 0in;"><span style="font-size:11.0pt"><span style="font-family:Calibri"><strong>Content Change&nbsp;</strong>- Request data stewards to modify the business description, tags&nbsp;and terms for a table/ column/ file/ report/ business glossary.</span></span></li><li style="margin: 0in;"><span style="font-size:11.0pt"><span style="font-family:Calibri"><strong>Data Quality&nbsp;</strong>- Notify data stewards for change in data quality of a table/table column/file/report/business glossary.</span></span></li><li style="margin: 0in;"><span style="font-size:11.0pt"><span style="font-family:Calibri"><strong>New Asset Request&nbsp;</strong>- Notify data owners to create new table or column in the remote system.</span></span></li><li style="margin: 0in;"><span style="font-size:11.0pt"><span style="font-family:Calibri"><strong>Other&nbsp;</strong>- Notify stewards of any additional changes requires on the data objects like a change of User password.</span></span><span style="font-size:11.0pt"><span style="font-family:Calibri"></span></span></li></ul>', 'servicedesk', true, '', '', 'admin', now());
