insert into helpcenter_topic(topicid, description, content, context, active, type, external_link, createdby, createdate) values (53, 'What is Data Quality Rule?', '<p><span id="docs-internal-guid-bf528b91-7fff-7e9e-6249-32665ebc0b02">Governance on data quality is not only&nbsp;just correcting the poor quality data but also&nbsp;regularly monitoring, controlling, and verifying the quality of data when new data comes into the system.</span></p><p><span id="docs-internal-guid-bf528b91-7fff-7e9e-6249-32665ebc0b02"><strong>Data quality rule</strong> is a set of process written on a data object to regularly monitor, control, and verify the quality of data whenever data transformations happen.</span></p>', 'dataquality', true, '', '', 'admin', now());
