insert into helpcenter_topic(topicid, description, content, context, active, type, external_link, createdby, createdate) values (54, 'How to use the Data Quality Rule module?', '<p>Users can do the following within the Data Quality Rule module,</p><ul><li><strong>Create</strong> a new data quality rule.<br></li><li><strong>View</strong> the list of data objects on which the rule acts.</li><li><strong>Edit </strong>or set the threshold ranges or values for the selected rule type.</li><li>Change rule status to <strong>DRAFT</strong> for editing the rule settings</li><li>Change rule status to <strong>ACTIVE</strong> to finalize the rule settings</li><li><strong>Copy</strong> the rule to a watchlist for quick monitoring</li><li>Set an <strong>alert or ticket</strong> system when the rule <strong>PASSES or FAILS</strong></li><li><strong>Schedule</strong> the execution of data quality rule in a preferred time</li><li><strong>Delete</strong> Data Quality Rules</li></ul><p>&nbsp;Additionally, users can do the following,</p><ul><li><strong>Filter</strong> <strong>the data quality rules</strong> based on a Domain or rule type.</li><li><strong>Search for a Data Quality Rule</strong> based on Rule Name, purpose, a business term, or Steward.</li><li><strong>Sort the rules </strong>based on the rule Creation Date.</li></ul>', 'dqrule', true, '', '', 'admin', now());
