insert into helpcenter_topic(topicid, description, content, context, active, type, external_link, createdby, createdate) values (56, 'How to use the Governed Data Query compliance page?', '<p>Once a Governed Data Query (GDQ) is configured, users can do the following on this page:</p><ul><li><strong>Add entries</strong> based on the configured terms to search the customer information.</li><li><strong>Edit</strong> the GDQ entries.</li><li><strong>Run or Execute</strong> all or specific GDQ search entries.</li><li><strong>View</strong> associated data objects for the search result.</li></ul>', 'gdqcompliance', true, '', '', 'admin', now());
