insert into helpcenter_topic(topicid, description, content, context, active, type, external_link, createdby, createdate) values (57, 'What is Lineage Dashboard?', '<p>The Lineage Dashboard gives it''s business users an ability to visualize the end-to-end flow of the data from a source to destination for all the data connections in OvalEdge. With this interactive visualization(Sankey Chart), users can see the data movement from one point to another. The data objects that are connected are called nodes and the data flow between them is called links. The width of each link is proportional to the quantity represented.</p>', 'datamovementdashboard', true, '', '', 'admin', now());


insert into helpcenter_topic(topicid, description, content, context, active, type, external_link, createdby, createdate) values (58, 'What is a Watchlist?', '<p>This window is like a favorite page. Users can add their favorite Databases Schemas, Tables, Reports and Data Quality rules to this page to keep a regular track on any metadata or data changes.&nbsp;</p>', 'watchlist', true, '', '', 'admin', now());
