insert into helpcenter_topic(topicid, description, content, context, active, type, external_link, createdby, createdate) values (60, 'What is the User Profile detail?', '<p><strong>User Profile Detail</strong> shows details about a user logged in to the application. It includes contact information (Phone number and Email), profile photo, date and time when the profile information was last updated. Users can edit their profile and change the password in this page. Also, users can share their&nbsp;out of office status using the out of office option.<br><br></p>', 'profile', true, '', '', 'admin', now());
