insert into helpcenter_topic(topicid, description, content, context, active, type, external_link, createdby, createdate) values (61, 'How to import the metadata from a template file?', '<p>The Load metadata from the files module helps users to upload the metadata pertaining to schemas, tables, business glossaries&nbsp;etc. in bulk using .CSV files. This module is used to either update existing metadata or creates new metadata for the data objects.</p>', 'loadfilesdata', true, '', '', 'admin', now());


insert into helpcenter_topic(topicid, description, content, context, active, type, external_link, createdby, createdate) values (62, 'What is a Certification Policy framework?', '<p><span data-sheets-userformat=''{"2":6845,"3":{"1":0},"5":{"1":[{"1":2,"2":0,"5":{"1":2,"2":0}},{"1":0,"2":0,"3":3},{"1":1,"2":0,"4":1}]},"6":{"1":[{"1":2,"2":0,"5":{"1":2,"2":0}},{"1":0,"2":0,"3":3},{"1":1,"2":0,"4":1}]},"7":{"1":[{"1":2,"2":0,"5":{"1":2,"2":0}},{"1":0,"2":0,"3":3},{"1":1,"2":0,"4":1}]},"8":{"1":[{"1":2,"2":0,"5":{"1":2,"2":0}},{"1":0,"2":0,"3":3},{"1":1,"2":0,"4":1}]},"10":2,"12":0,"14":{"1":2,"2":16711680},"15":"Calibri"}'' data-sheets-value=''{"1":2,"2":"Certification Policy is a framework within OvalEdge that could be leveraged to set up and automate certification of data objects through a standard set of policies. The policies are generally a set of business rules applied to the metadata. This framework is intended to help the data governance team to add more layers of trust and transparency to the data objects."}'' style="font-size: 14px; font-family: Calibri, Arial; font-style: normal; color: rgb(0, 0, 0); position: static;">Certification Policy is a framework within OvalEdge that could be leveraged to set up and automate certification of data objects through a standard set of policies. The policies are generally a set of business rules applied to the metadata. This framework is intended to help the data governance team to add more layers of trust and transparency to the data objects.</span> </p>', 'governpolicy', true, '', '', 'admin', now());
