insert into helpcenter_topic(topicid, description, content, context, active, type, external_link, createdby, createdate) values (67, 'What is Navigator Audit Events?', '<p>In OvalEdge, we can make a live call to Navigator API to show the audit events.</p><p>Currently, we are making the calls to get the following audit events:</p><ul><li>Hive</li><li>Impala</li><li>HBase</li><li>HDFS</li></ul><p><b id="docs-internal-guid-7a86189f-7fff-4ecd-36b7-3e5f16a12e8b"><img style="width: 20px; height: 20px;" src="oecss/images/helpcenter/warning icon.jpg" data-cke-saved-src="oecss/images/helpcenter/warning icon.jpg">&nbsp;</b><b>OvalEdge will only crawl the historical audit events.</b></p><p><b id="docs-internal-guid-4a765a55-7fff-8ea9-6dd2-3212ca57ead4"><img data-cke-saved-src="oecss/images/helpcenter/pointer_hand.png" src="oecss/images/helpcenter/pointer_hand.png" style="width: 20px; height: 20px;">&nbsp;Default: Last 2 hours of audits will be shown on the initial page load.</b><br></p><p dir="ltr"><b id="docs-internal-guid-f6147e01-7fff-5174-7c14-3a8c1386558e"><br></b></p><p dir="ltr"><br></p>', 'auditevents', true, '', '', 'admin', now());


insert into helpcenter_topic(topicid, description, content, context, active, type, external_link, createdby, createdate) values (68, 'What is Temp Lineage Correction?', '<p><span data-sheets-userformat=''{"2":7101,"3":{"1":0},"5":{"1":[{"1":2,"2":0,"5":{"1":2,"2":0}},{"1":0,"2":0,"3":3},{"1":1,"2":0,"4":1}]},"6":{"1":[{"1":2,"2":0,"5":{"1":2,"2":0}},{"1":0,"2":0,"3":3},{"1":1,"2":0,"4":1}]},"7":{"1":[{"1":2,"2":0,"5":{"1":2,"2":0}},{"1":0,"2":0,"3":3},{"1":1,"2":0,"4":1}]},"8":{"1":[{"1":2,"2":0,"5":{"1":2,"2":0}},{"1":0,"2":0,"3":3},{"1":1,"2":0,"4":1}]},"10":2,"11":4,"12":0,"14":{"1":2,"2":0},"15":"Arial"}'' data-sheets-value=''{"1":2,"2":"We have lots of connectors which allow users to create Auto build lineage and also build lineage from the scripts. All of this system will have internally some tables and columns associated. When we crawl connections and build lineage, we might not find the respective tables/columns into our application to get them associated. In this case, the system will create temporary schemas, tables and columns and build Lineage using them. When we crawl Original schemas and tables, the Lineage mapped with temp schemas and tables should be mapped with these objects. \r\n\r\n\r\nWe can use this application to achieve the above scenario, it lists the temp tables(with Lineage) with Recommended Tables. Upon Apply, Recommended Table and its columns will be replaced by temporary table / columns in its Lineage graph."}'' style="font-size: 10pt; font-family: Arial; font-weight: normal; font-style: normal; color: rgb(0, 0, 0); position: static;">We have lots of connectors which allow users to create Auto build lineage and also build lineage from the scripts. All of this system will have internally some tables and columns associated. When we crawl connections and build lineage, we might not find the respective tables/columns into our application to get them associated. In this case, the system will create temporary schemas, tables and columns and build Lineage using them. When we crawl Original schemas and tables, the Lineage mapped with temp schemas and tables should be mapped with these objects. <br style="position: static;"> <br style="position: static;">&nbsp;We can use this application to achieve the above scenario, it lists the temp tables(with Lineage) with Recommended Tables. Upon Apply, Recommended Table and its columns will be replaced by temporary table / columns in its Lineage graph.</span> </p>', 'templineagemerge', true, '', '', 'admin', now());


insert into helpcenter_topic(topicid, description, content, context, active, type, external_link, createdby, createdate) values (69, 'What are GDQ results?', '<p><span data-sheets-userformat=''{"2":7101,"3":{"1":0},"5":{"1":[{"1":2,"2":0,"5":{"1":2,"2":0}},{"1":0,"2":0,"3":3},{"1":1,"2":0,"4":1}]},"6":{"1":[{"1":2,"2":0,"5":{"1":2,"2":0}},{"1":0,"2":0,"3":3},{"1":1,"2":0,"4":1}]},"7":{"1":[{"1":2,"2":0,"5":{"1":2,"2":0}},{"1":0,"2":0,"3":3},{"1":1,"2":0,"4":1}]},"8":{"1":[{"1":2,"2":0,"5":{"1":2,"2":0}},{"1":0,"2":0,"3":3},{"1":1,"2":0,"4":1}]},"10":2,"11":4,"12":0,"14":{"1":2,"2":16711680},"15":"Arial"}'' data-sheets-value=''{"1":2,"2":"Once the entries are executed, the matched results can be viewed using the View Results tab. It lists all the associated objects that have information entered in the GDQ.\nNote: Each search entry should be selected individually to view results. Each time the GDQ search entry is executed, a data point is created and represented in the line. Users can click on the data point to see the earlier results."}'' style="font-size: 10pt; font-family: Arial; font-weight: normal; font-style: normal; color: rgb(0, 0, 0); position: static;">Once the entries are executed, the matched results can be viewed using the View Results tab. It lists all the associated objects that have information entered in the GDQ. Each search entry should be selected individually to view results. Each time the GDQ search entry is executed, a data point is created and represented in the line.&nbsp;</span></p><p><img data-fr-image-pasted="true" src="https://helpcenter.ovaledge.net/oecss/images/helpcenter/pointer_hand.png" data-cke-saved-src="oecss/images/helpcenter/pointer_hand.png" style="box-sizing: border-box; border-width: 0px; border-style: solid; border-color: currentcolor; max-width: 100%; height: 20px; cursor: pointer; padding: 0px 1px; text-align: left; width: 20px;" class="fr-fil fr-dib">  <span style="color: rgb(0, 0, 0); font-family: Arial; font-size: 13.3333px; font-style: normal; font-variant-ligatures: normal; font-variant-caps: normal; font-weight: 400; letter-spacing: normal; orphans: 2; text-align: left; text-indent: 0px; text-transform: none; white-space: normal; widows: 2; word-spacing: 0px; -webkit-text-stroke-width: 0px; background-color: rgb(255, 255, 255); text-decoration-thickness: initial; text-decoration-style: initial; text-decoration-color: initial; float: none; display: inline !important; position: static;">Users can click on the data point to see the earlier results.</span> </p>', 'gdqviewresults', true, '', '', 'admin', now());


insert into helpcenter_topic(topicid, description, content, context, active, type, external_link, createdby, createdate) values (70, 'What is Data Literacy Dashboard?', '<p dir="ltr" style="line-height: 1.37445; margin-top: 11.6608pt; margin-bottom: 0pt; position: static;"><span style=''font-size: 14px; font-family: "Helvetica Neue", sans-serif; color: rgb(0, 0, 0); background-color: transparent; font-weight: 400; font-style: normal; font-variant: normal; text-decoration: none; vertical-align: baseline; white-space: pre-wrap; position: static;''>OvalEdge provides a simple yet a comprehensive Data Literacy dashboard which provides the information graphically on the following:</span></p><p dir="ltr" style="line-height: 1.37445; margin-top: 11.6608pt; margin-bottom: 0pt; position: static;"><span style="font-size: 14px;"><span style="color: rgb(0, 0, 0);"><span style=''font-family: "Helvetica Neue", sans-serif; background-color: transparent; font-weight: 400; font-style: normal; font-variant: normal; text-decoration: none; vertical-align: baseline; white-space: pre-wrap; position: static;''>i.&nbsp;</span><span style=''font-family: "Helvetica Neue", sans-serif; background-color: transparent; font-weight: 700; font-style: normal; font-variant: normal; text-decoration: none; vertical-align: baseline; white-space: pre-wrap; position: static;''>Tribal Knowledge Progress</span><span style=''font-family: "Helvetica Neue", sans-serif; background-color: transparent; font-weight: 400; font-style: normal; font-variant: normal; text-decoration: none; vertical-align: baseline; white-space: pre-wrap; position: static;''>&nbsp;- Tables/ Table Columns/ Report/ Report Columns/ File/ File Columns: This provides the count (cumulative) on above mentioned data objects as per the last updated time using stacked bar graphs.</span></span></span></p><p dir="ltr" style="line-height: 1.37445; margin-top: 11.6608pt; margin-bottom: 0pt; position: static;"><span style="font-size: 14px;"><span style="color: rgb(0, 0, 0);"><span style=''font-family: "Helvetica Neue", sans-serif; background-color: transparent; font-weight: 400; font-style: normal; font-variant: normal; text-decoration: none; vertical-align: baseline; white-space: pre-wrap; position: static;''>ii.&nbsp;</span><span style=''font-family: "Helvetica Neue", sans-serif; background-color: transparent; font-weight: 700; font-style: normal; font-variant: normal; text-decoration: none; vertical-align: baseline; white-space: pre-wrap; position: static;''>Number of Terms by Month:</span><span style=''font-family: "Helvetica Neue", sans-serif; background-color: transparent; font-weight: 400; font-style: normal; font-variant: normal; text-decoration: none; vertical-align: baseline; white-space: pre-wrap; position: static;''>&nbsp;This is a stacked bar chart that gives the total number of terms created in a month and total number of terms published (cumulative).</span></span></span></p><p dir="ltr" style="line-height: 1.37445; margin-top: 11.6608pt; margin-bottom: 0pt; position: static;"><span style="font-size: 14px;"><span style="color: rgb(0, 0, 0);"><span style=''font-family: "Helvetica Neue", sans-serif; background-color: transparent; font-weight: 400; font-style: normal; font-variant: normal; text-decoration: none; vertical-align: baseline; white-space: pre-wrap; position: static;''>iii.&nbsp;</span><span style=''font-family: "Helvetica Neue", sans-serif; background-color: transparent; font-weight: 700; font-style: normal; font-variant: normal; text-decoration: none; vertical-align: baseline; white-space: pre-wrap; position: static;''>Number of objects for which Terms are applied:</span><span style=''font-family: "Helvetica Neue", sans-serif; background-color: transparent; font-weight: 400; font-style: normal; font-variant: normal; text-decoration: none; vertical-align: baseline; white-space: pre-wrap; position: static;''>&nbsp;This is a bar chart for every month with the Total number of Terms that are applied to all objects (cumulative). &nbsp;</span></span></span></p><p dir="ltr" style="line-height: 1.37445; margin-top: 11.6608pt; margin-bottom: 0pt; position: static;"><span style="font-size: 14px;"><span style="color: rgb(0, 0, 0);"><span style=''font-family: "Helvetica Neue", sans-serif; background-color: transparent; font-weight: 400; font-style: normal; font-variant: normal; text-decoration: none; vertical-align: baseline; white-space: pre-wrap; position: static;''>iv.&nbsp;</span><span style=''font-family: "Helvetica Neue", sans-serif; background-color: transparent; font-weight: 700; font-style: normal; font-variant: normal; text-decoration: none; vertical-align: baseline; white-space: pre-wrap; position: static;''>Domain Vs Number of Terms:</span></span></span><span style=''font-size: 14px; font-family: "Helvetica Neue", sans-serif; color: rgb(0, 0, 0); background-color: transparent; font-weight: 400; font-style: normal; font-variant: normal; text-decoration: none; vertical-align: baseline; white-space: pre-wrap; position: static;''>&nbsp;This is a pie-chart with Domain and its terms counts.</span></p>', 'dataliteracydashboard', true, '', '', 'admin', now());


insert into helpcenter_topic(topicid, description, content, context, active, type, external_link, createdby, createdate) values (71, 'What is Data Quality Dashboard?', '<p dir="ltr" style="line-height: 1.38; margin-top: 0pt; margin-bottom: 0pt; position: static;"><span style="font-size: 14px; font-family: Roboto, sans-serif; color: rgb(0, 0, 0); background-color: transparent; font-weight: 400; font-style: normal; font-variant: normal; text-decoration: none; vertical-align: baseline; white-space: pre-wrap; position: static;">OvalEdge provides the Data Quality dashboard which provides the following information graphically:</span></p><p><span style="color: rgb(0, 0, 0);"><span style="font-size: 14px;"><span style="font-family: Roboto,sans-serif;"><span style="background-color: transparent; font-weight: 400; font-style: normal; font-variant: normal; text-decoration: none; vertical-align: baseline; white-space: pre-wrap; position: static;">i.&nbsp;</span><span style="background-color: transparent; font-weight: 700; font-style: normal; font-variant: normal; text-decoration: none; vertical-align: baseline; white-space: pre-wrap; position: static;">Servicedesk Report:</span><span style="background-color: transparent; font-weight: 400; font-style: normal; font-variant: normal; text-decoration: none; vertical-align: baseline; white-space: pre-wrap; position: static;">&nbsp;This report shows only Data Quality tickets raised. It is a stacked bar chart which contains the last 12 months report on Created vs Resolved by month. &nbsp;</span></span></span></span></p><p><span style="color: rgb(0, 0, 0);"><span style="font-size: 14px;"><span style="font-family: Roboto,sans-serif;"><span style="background-color: transparent; font-weight: 400; font-style: normal; font-variant: normal; text-decoration: none; vertical-align: baseline; white-space: pre-wrap; position: static;">ii.&nbsp;</span><span style="background-color: transparent; font-weight: 700; font-style: normal; font-variant: normal; text-decoration: none; vertical-align: baseline; white-space: pre-wrap; position: static;">Data Quality Rule - Validity:</span><span style="background-color: transparent; font-weight: 400; font-style: normal; font-variant: normal; text-decoration: none; vertical-align: baseline; white-space: pre-wrap; position: static;">&nbsp;It is Pie chart which shows the Data Quality Rule (DQR) validity based on success or failed conditions.&nbsp;</span></span></span></span></p><p><span style="color: rgb(0, 0, 0);"><span style="font-size: 14px;"><span style="font-family: Roboto,sans-serif;"></span></span></span><span style="color: rgb(0, 0, 0);"><span style="font-size: 14px;"><span style="font-family: Roboto,sans-serif;"><span style="background-color: transparent; font-weight: 400; font-style: normal; font-variant: normal; text-decoration: none; vertical-align: baseline; white-space: pre-wrap; position: static;">iii.&nbsp;</span><span style="background-color: transparent; font-weight: 700; font-style: normal; font-variant: normal; text-decoration: none; vertical-align: baseline; white-space: pre-wrap; position: static;">Data Quality Rule - Uniqueness:</span></span></span></span><span style="font-size: 14px; font-family: Roboto, sans-serif; color: rgb(0, 0, 0); background-color: transparent; font-weight: 400; font-style: normal; font-variant: normal; text-decoration: none; vertical-align: baseline; white-space: pre-wrap; position: static;">&nbsp;It is Pie chart which shows the Data Quality Rule (DQR) uniqueness based on success or failed conditions.&nbsp;</span></p>', 'dataqualitydashboard', true, '', '', 'admin', now());


insert into helpcenter_topic(topicid, description, content, context, active, type, external_link, createdby, createdate) values (72, 'How can I request Content Change on a Table/File/Reports? ', '<p><span style="font-size: 14px; color: rgb(0, 0, 0); font-family: Roboto, sans-serif;">Users can raise a content change request on particular tables/columns/files/reports/business glossary to change the associated business description/term/tag in that data object.&nbsp;</span><span style="font-family: Roboto,sans-serif;"><span style="color: rgb(0, 0, 0);"><span style="font-size: 14px;">Users can raise a content change request through the following,</span></span></span></p><ul><li style="margin-left: 20px;"><span style="font-family: Roboto,sans-serif;"><span style="color: rgb(0, 0, 0);"><span style="font-size: 14px;">the Data Catalog</span></span></span></li><li style="margin-left: 20px;"><span style="font-size: 14px; color: rgb(0, 0, 0); font-family: Roboto, sans-serif;">the Governance Catalog &gt; Service Desk module</span></li></ul>', 'servicedeskvtwo', true, '', '', 'admin', now());
