insert into helpcenter_topic(topicid, description, content, context, active, type, external_link, createdby, createdate) values (73, 'How is the Data Quality Index calculated?', '<p><b style="position: static;"><span style="font-family: Roboto, sans-serif; font-size: 14px;">Data Quality Index</span></b></p><p style="position: static;"><span style="font-size: 14px;"><span style="font-family: Roboto,sans-serif;">The quality index is calculated in the following way:</span></span></p><p style="position: static;"><span style="font-size: 14px;"><span style="font-family: Roboto,sans-serif;"><b style="position: static;">Number of issues associated with data objects:</b></span></span></p><ul><li><span style="font-size: 14px;"><span style="font-family: Roboto,sans-serif;">Sum of Last status of Associated rules : (success rules / total rules) * 50% + Resolved / Total Issues * 50%</span></span></li><li><span style="font-size: 14px;"><span style="font-family: Roboto,sans-serif;">Rules = Sum of Last status of Associated rules * (success rules / total rules) * 50%</span></span></li><li><span style="font-size: 14px;"><span style="font-family: Roboto,sans-serif;">Issues= Resolved / Total Issues * 50%</span></span></li></ul><p style="position: static;"><span style="font-size: 14px;"><span style="font-family: Roboto,sans-serif;"><b style="position: static;">DQ Index Score = Rules + Issues&nbsp;</b></span></span></p><p style="position: static;"><span style="font-size: 14px;"><span style="font-family: Roboto,sans-serif;">This is the final quality index score displayed in the Data Catalog page.</span></span></p><p style="margin-left: 0.5in; position: static;"><span style="font-size: 14px;"><span style="font-family: Roboto,sans-serif;">if 0 rules &rarr; 100% on issues</span></span></p><p style="margin-left: 0.5in; position: static;"><span style="font-size: 14px;"><span style="font-family: Roboto,sans-serif;">If 0 issues &rarr; 100% on rules</span></span></p><p style="margin-left: 0.5in; position: static;"><span style="font-size: 14px;"><span style="font-family: Roboto,sans-serif;">if both 0, then N/A</span></span></p><p style="position: static;"><span style="font-size: 14px;"><span style="font-family: Roboto,sans-serif;"><b style="position: static;">Data Quality Grades</b></span></span></p><ul><li><span style="font-size: 14px;"><span style="font-family: Roboto,sans-serif;">100% :Excellent</span></span></li><li><span style="font-size: 14px;"><span style="font-family: Roboto,sans-serif;">90-100% :Good</span></span></li><li><span style="font-family: Roboto, sans-serif; font-size: 14px;">80% :Fair</span></li></ul>', 'dqindex', true, '', '', 'admin', now());


insert into helpcenter_topic(topicid, description, content, context, active, type, external_link, createdby, createdate) values (74, 'How does the OvalEdge Servicedesk integrate with the external tools?', '<p><span style="color: rgb(0, 0, 0); font-size: 14px; font-family: Roboto, sans-serif;"> Service Desk Integration&#39; button is used to push or map the tickets from the OvalEdge application to the JIRA or Service now tool.</span> </p>', 'serviceintegration', true, '', '', 'admin', now());
