insert into helpcenter_topic(topicid, description, content, context, active, type, external_link, createdby, createdate) values (21019, 'What is Popularityscore?', '<p><style type="text/css"><!--{cke_protected}{C}%3C!%2D%2Dtd%20%7Bborder%3A%201px%20solid%20%23ccc%3B%7Dbr%20%7Bmso-data-placement%3Asame-cell%3B%7D%2D%2D%3E--></style><strong>Popularity score</strong> is a score that provides a count on&nbsp;number of times&nbsp;user has interacted with this dataobject (table, tablecolumn, file, filecolumn, report, reportcolumn).</p>', 'popularity', true, '', '', 'admin', now());
