insert into helpcenter_topic(topicid, description, content, context, active, type, external_link, createdby, createdate) values (21024, 'What is a Column type?', '<p>The column type represents the data type of the values, the column holds. Some common data types OvalEdge supports are Integer, Date, String&nbsp;and Float.</p>', 'type', true, '', '', 'admin', now());


insert into helpcenter_topic(topicid, description, content, context, active, type, external_link, createdby, createdate) values (21025, 'What are additional fields?', '<p style="margin-bottom:.0001pt; margin:0in 0in 8pt; text-align:justify;"><span style="font-size: 14px;"><span style="line-height: normal; color: rgb(0, 0, 0); font-family: Roboto, sans-serif;">Additional fields are known as the <strong>Custom Fields</strong>. Custom fields give OvalEdge users an opportunity to add more metadata content for each of the data objects in your Organization. OvalEdge provides a list of statistics or attributes by default such as table count, row count, column count, total row count, Minimum value, Maximum value, Null count, distinct count, density, and null density. If a user wants to add more information about the data objects, they can create additional fields called <strong>Custom fields</strong>.<br>Users can view the custom fields on each data object in the Data Catalog module.</span></span></p>', 'additionalfields', true, '', '', 'admin', now());


insert into helpcenter_topic(topicid, description, content, context, active, type, external_link, createdby, createdate) values (21026, 'What is a term and how to use it for navigating data?', '<p>Terms are the common and standard&nbsp;definitions&nbsp;used within an organization. They are generally known as "Business glossaries". They are relevant to business domains used across the enterprise to&nbsp;reduce miscommunication and to create&nbsp;trust in your data.&nbsp;Users can also organize terms by adding a tag to the term for easy navigation.</p>', 'term', true, '', '', 'admin', now());


insert into helpcenter_topic(topicid, description, content, context, active, type, external_link, createdby, createdate) values (21027, 'What does the status define?', '<p>The&nbsp;status attribute in a table or a file displays the analyzed state of the data object during profiling. The following are the different values:</p><ul><li><strong>Profiled -</strong> When full profiling is complete on the data object.</li><li><strong>NULL/Not Profiled -</strong> When the data object is not profiled.</li><li><strong>Sample Profiled -</strong> When sample profiling is conducted and completed on the data object.</li><li><strong>Partially Profiled -</strong> When the data object&nbsp;is Partially profiled.</li><li><strong>Profile Failed - </strong>When the profiling fails.</li></ul>', 'tb.status', true, '', '', 'admin', now());


insert into helpcenter_topic(topicid, description, content, context, active, type, external_link, createdby, createdate) values (21028, 'What is a column count?', '<p>The total number of columns in this table.</p>', 'tb.columncount', true, '', '', 'admin', now());


insert into helpcenter_topic(topicid, description, content, context, active, type, external_link, createdby, createdate) values (21029, 'What is a historic row count?', '<p>The historic row count is a&nbsp;bar graph that displays&nbsp;the total number of rows in a table changing over a period of time. This helps user to identify any data changes that have occurred each time when the data was profiled. When you hover over each bar, you get the count of the number of rows in that&nbsp;table for each profiled date.</p>', 'tb.historicrowcount', true, '', '', 'admin', now());


insert into helpcenter_topic(topicid, description, content, context, active, type, external_link, createdby, createdate) values (21030, 'What is right match count?', '<p>The right match count is the total number of rows of data in the secondary column that matches to the compared table.</p>', 'er.rightmatchcount', true, '', '', 'admin', now());


insert into helpcenter_topic(topicid, description, content, context, active, type, external_link, createdby, createdate) values (21031, 'What is unique match count?', '<p>The unique match count displays how many unique rows in the column of Table B have a matching record in the column of Table A.</p>', 'er.uniquematchcount', true, '', '', 'admin', now());


insert into helpcenter_topic(topicid, description, content, context, active, type, external_link, createdby, createdate) values (21032, 'What is a match count?', '<p>In OvalEdge, matching count displays the number of row&nbsp;counts in the secondary column that have a matching value in the primary column</p>', 'table.relationshipmatchingcount', true, '', '', 'admin', now());


insert into helpcenter_topic(topicid, description, content, context, active, type, external_link, createdby, createdate) values (21033, 'What is a base match count?', '<p>In OvalEdge, the base match count displays the number of row counts in the primary column that has a matching value in the secondary column.<br></p>', 'table.relationmainmatchcount', true, '', '', 'admin', now());


insert into helpcenter_topic(topicid, description, content, context, active, type, external_link, createdby, createdate) values (21034, 'What is a unique match count?', '<p>The unique match count displays how many unique rows in the column of Table B have a matching record in the column of Table A.</p>', 'table.relationuniquematchcount', true, '', '', 'admin', now());


insert into helpcenter_topic(topicid, description, content, context, active, type, external_link, createdby, createdate) values (21035, 'What is the status of the Column relationships?', '', 'cdr.status', true, '', '', 'admin', now());


insert into helpcenter_topic(topicid, description, content, context, active, type, external_link, createdby, createdate) values (21036, 'What is Add to Relationship?', '<p><style type="text/css"><!--{cke_protected}{C}%3C!%2D%2Dtd%20%7Bborder%3A%201px%20solid%20%23ccc%3B%7Dbr%20%7Bmso-data-placement%3Asame-cell%3B%7D%2D%2D%3E--></style>Once you identified the data objects with a high pattern match score, you can add it to the column relationship to find how strong or similar the objects are related.</p>', 'as.addtorelationship', true, '', '', 'admin', now());


insert into helpcenter_topic(topicid, description, content, context, active, type, external_link, createdby, createdate) values (21037, 'What is the length of a field?', '<p>The length of a table column defined in the remote database.</p>', 'as.length', true, '', '', 'admin', now());


insert into helpcenter_topic(topicid, description, content, context, active, type, external_link, createdby, createdate) values (21038, 'How to propagate the Caution to downstream objects?', '<p>When users mark a caution on a particular table/ column/ report, all the downstream data objects in the lineage are assigned a <span style="color: rgb(65, 65, 65); font-family: sans-serif; font-size: 14px; font-style: normal; font-variant-ligatures: normal; font-variant-caps: normal; font-weight: 400; letter-spacing: normal; orphans: 2; text-align: left; text-indent: 0px; text-transform: none; white-space: normal; widows: 2; word-spacing: 0px; -webkit-text-stroke-width: 0px; background-color: rgb(255, 255, 255); text-decoration-thickness: initial; text-decoration-style: initial; text-decoration-color: initial; float: none; display: inline !important; position: static;">caution</span> to warn the users of any data quality issue.</p><p>To <span style="color: rgb(65, 65, 65); font-family: sans-serif; font-size: 14px; font-style: normal; font-variant-ligatures: normal; font-variant-caps: normal; font-weight: 400; letter-spacing: normal; orphans: 2; text-align: left; text-indent: 0px; text-transform: none; white-space: normal; widows: 2; word-spacing: 0px; -webkit-text-stroke-width: 0px; background-color: rgb(255, 255, 255); text-decoration-thickness: initial; text-decoration-style: initial; text-decoration-color: initial; float: none; display: inline !important; position: static;">caution&nbsp;</span>all the downstream objects,</p><ol><li>Navigate to the Data catalog tab.</li><li>Select a data object (Table/File/Report).</li><li>Select the<img src="oecss/images/helpcenter/ninedots1.png" id="photo-id" style="width: 25px; height: 25px;" data-cke-saved-src="oecss/images/helpcenter/ninedots1.png" class="fr-fil fr-dib">nine dots icon and select Process upstream/downstream objects.</li><li>Choose C<span style="color: rgb(65, 65, 65); font-family: sans-serif; font-size: 14px; font-style: normal; font-variant-ligatures: normal; font-variant-caps: normal; font-weight: 400; letter-spacing: normal; orphans: 2; text-align: left; text-indent: 0px; text-transform: none; white-space: normal; widows: 2; word-spacing: 0px; -webkit-text-stroke-width: 0px; background-color: rgb(255, 255, 255); text-decoration-thickness: initial; text-decoration-style: initial; text-decoration-color: initial; float: none; display: inline !important; position: static;">aution&nbsp;</span>for all the downstream objects.</li></ol>', 'table.redflagupstream', true, '', '', 'admin', now());


insert into helpcenter_topic(topicid, description, content, context, active, type, external_link, createdby, createdate) values (21039, 'How to propagate the Metadata to upstream/downstream objects?', '<p>The purpose of this task is to propagate Tags, Business Description &amp; Technical Description of a selected object on both its upstream and downstream levels based on Lineage.</p><ol><li>Navigate to the Data catalog tab.</li><li>Select a data object (Table/ File/ Report).</li><li>Select the&nbsp;<img src="oecss/images/helpcenter/ninedots1.png" id="photo-id" style="width: 25px; height: 25px;" data-cke-saved-src="oecss/images/helpcenter/ninedots1.png" class="fr-fil fr-dib">&nbsp;nine dots icon and choose process Upstream/ Downstream objects to propagate Tags, Business Description &amp; Technical Description upward/downward up to n-levels.</li><li>Select Copy metadata using Lineage.</li><li>Choose a lineage level up to which the metadata should be applied. By default Propagate to all levels will be checked. If you want to apply up to certain levels, uncheck the &ldquo;Propagate to all levels&rdquo; and enter the number of lineage levels in the text box.</li><li>Select Downstream or Upstream or both to apply the metadata chosen.&nbsp;</li></ol><p><br></p>', 'table.propagatemetadata', true, '', '', 'admin', now());


insert into helpcenter_topic(topicid, description, content, context, active, type, external_link, createdby, createdate) values (21040, 'What are Top Values?', '<p><style type="text/css"><!--{cke_protected}{C}%3C!%2D%2Dtd%20%7Bborder%3A%201px%20solid%20%23ccc%3B%7Dbr%20%7Bmso-data-placement%3Asame-cell%3B%7D%2D%2D%3E--></style>This section displays the top 50 values of a column based on how many times the value has occurred. The result can be sorted by top values and count of top values in both the ascending and descending order.<br></p>', 'topvalues', true, '', '', 'admin', now());


insert into helpcenter_topic(topicid, description, content, context, active, type, external_link, createdby, createdate) values (21041, 'What are Values?', '<p><style type="text/css"><!--{cke_protected}{C}%3C!%2D%2Dtd%20%7Bborder%3A%201px%20solid%20%23ccc%3B%7Dbr%20%7Bmso-data-placement%3Asame-cell%3B%7D%2D%2D%3E--></style>OvalEdge provides a list of standard fields like minimum / maximum count, density, null density, distinct count etc. In this section, minimum and maximum values of each column are displayed.<br></p>', 'minmaxvalue', true, '', '', 'admin', now());


insert into helpcenter_topic(topicid, description, content, context, active, type, external_link, createdby, createdate) values (21042, 'What does the quality index of a table mean?', '<p><span style="color: rgb(0, 0, 0); font-family: Arial; font-size: 13px; font-style: normal; font-variant-ligatures: normal; font-variant-caps: normal; font-weight: 400; letter-spacing: normal; orphans: 2; text-align: left; text-indent: 0px; text-transform: none; white-space: pre-wrap; widows: 2; word-spacing: 0px; -webkit-text-stroke-width: 0px; background-color: rgb(255, 255, 255); text-decoration-thickness: initial; text-decoration-style: initial; text-decoration-color: initial; float: none; position: static; display: inline !important;">Quality Index is the Data Quality score of a table based on the status and age factor of the rule last run time and ticket resolved date.</span> </p>', 'tb.qualityindex', true, '', '', 'admin', now());


insert into helpcenter_topic(topicid, description, content, context, active, type, external_link, createdby, createdate) values (21043, 'What does the quality index of a table column mean?', '<p><span data-sheets-userformat=''{"2":15165,"3":{"1":0},"5":{"1":[{"1":2,"2":0,"5":{"1":2,"2":0}},{"1":0,"2":0,"3":3},{"1":1,"2":0,"4":1}]},"6":{"1":[{"1":2,"2":0,"5":{"1":2,"2":0}},{"1":0,"2":0,"3":3},{"1":1,"2":0,"4":1}]},"7":{"1":[{"1":2,"2":0,"5":{"1":2,"2":0}},{"1":0,"2":0,"3":3},{"1":1,"2":0,"4":1}]},"8":{"1":[{"1":2,"2":0,"5":{"1":2,"2":0}},{"1":0,"2":0,"3":3},{"1":1,"2":0,"4":1}]},"11":4,"12":0,"14":[null,2,16711680],"15":"Arial","16":10}'' data-sheets-value=''{"1":2,"2":"Quality Index is the Data Quality score of a table column based on the status and age factor of the rule last run time and ticket resolved date."}'' style="font-size: 10pt; font-family: Arial; font-weight: normal; font-style: normal; color: rgb(0, 0, 0); position: static;">Quality Index is the Data Quality score of a table column based on the status and age factor of the rule last run time and ticket resolved date.</span> </p>', 'tc.qualityindex', true, '', '', 'admin', now());


insert into helpcenter_topic(topicid, description, content, context, active, type, external_link, createdby, createdate) values (21044, 'What is a total count of a service requests of an object?', '<p><span data-sheets-userformat=''{"2":15165,"3":{"1":0},"5":{"1":[{"1":2,"2":0,"5":[null,2,0]},{"1":0,"2":0,"3":3},{"1":1,"2":0,"4":1}]},"6":{"1":[{"1":2,"2":0,"5":[null,2,0]},{"1":0,"2":0,"3":3},{"1":1,"2":0,"4":1}]},"7":{"1":[{"1":2,"2":0,"5":[null,2,0]},{"1":0,"2":0,"3":3},{"1":1,"2":0,"4":1}]},"8":{"1":[{"1":2,"2":0,"5":[null,2,0]},{"1":0,"2":0,"3":3},{"1":1,"2":0,"4":1}]},"11":4,"12":0,"14":[null,2,16711680],"15":"Arial","16":12}'' data-sheets-value=''{"1":2,"2":"It gives count of the total number of service request raised against an active object. If data fails to satisfy the data quality rule, the system can automatically raise a service desk ticket and send a notification message to the assigned Steward at the object level."}'' style="font-size: 12pt; font-family: Arial; font-weight: normal; font-style: normal; color: rgb(0, 0, 0); position: static;">It gives count of the total number of service request raised against an active object. If data fails to satisfy the data quality rule, the system can automatically raise a service desk ticket and send a notification message to the assigned Steward at the object level.</span> </p>', 'tc.servicerequestcount', true, '', '', 'admin', now());


insert into helpcenter_topic(topicid, description, content, context, active, type, external_link, createdby, createdate) values (21045, 'What do we mean by table key?', '<p><span style="font-size: 14px; font-family: Roboto, sans-serif; color: rgb(0, 0, 0); background-color: transparent; font-weight: 400; font-style: normal; font-variant: normal; text-decoration: none; vertical-align: baseline; white-space: pre-wrap; position: static;">The table key in the column details displays whether the column is Primary Key/Foreign Key and if the column is neither Primary Key nor Foreign Key it just displays as the blank.</span></p>', 'table.key', true, '', '', 'admin', now());


insert into helpcenter_topic(topicid, description, content, context, active, type, external_link, createdby, createdate) values (22000, 'What is a business description?', '<p><!--{cke_protected}%3Cmeta%20charset%3D%22utf-8%22%20%2F%3E--></p><p dir="ltr">Descriptions are&nbsp;helpful for users to understand the data objects.&nbsp;You can manually enter the business description or you can bulk upload the business descriptions for all the data objects from a&nbsp;file format (.csv). You can download the template from <strong>Advance Tools &gt; Load metadata from the files</strong> module to enter and load&nbsp;the business descriptions from a file.</p><p><!--{cke_protected}%3Cmeta%20charset%3D%22utf-8%22%20%2F%3E--><b id="docs-internal-guid-574867a9-7fff-8dc5-8dbd-c0936ef001d9"><img data-cke-saved-src="oecss/images/helpcenter/pointer_hand.png" src="oecss/images/helpcenter/pointer_hand.png" style="width: 30px; height: 30px;"></b><b>&nbsp;Data stewards and&nbsp;users with Meta Write permission on a data object has the right to edit the business description of that object.&nbsp;</b></p>', 'file.busidesc', true, '', '', 'admin', now());
insert into helpcenter_relation(topicid, childid) values(22000, 50005);


insert into helpcenter_topic(topicid, description, content, context, active, type, external_link, createdby, createdate) values (22001, 'What are tags and methods of tagging?', '<p><style type="text/css"><!--{cke_protected}{C}%3C!%2D%2Dtd%20%7Bborder%3A%201px%20solid%20%23ccc%3B%7Dbr%20%7Bmso-data-placement%3Asame-cell%3B%7D%2D%2D%3E--></style>Tags are the keywords that allow users to easily organize the data and categorize them to improve data search and quick discovery. OvalEdge tags help users to explore their data elements with a single click.<br>OvalEdge provides both a controlled tagging method (where user selects from existing tags that other users have created) and a free tagging method (where a user can create a new tag). Tags are added to data objects like databases, tables, table columns, files, file columns, reports, report columns, business glossary, data stories, and queries. Each data object can have a single tag or multiple tags. Also, each tag can have multiple data objects.<br><b id="docs-internal-guid-3516c9ac-7fff-2b4c-e5ac-f53474601891"><img data-cke-saved-src="oecss/images/helpcenter/pointer_hand.png" src="oecss/images/helpcenter/pointer_hand.png" style="width: 25px; height: 25px;"></b><strong>&nbsp;Tags are typically used in searches and with other features to give&nbsp;more control and power to search within OvalEdge. </strong></p><p><b id="docs-internal-guid-3516c9ac-7fff-2b4c-e5ac-f53474601891"><img data-cke-saved-src="oecss/images/helpcenter/pointer_hand.png" src="oecss/images/helpcenter/pointer_hand.png" style="width: 25px; height: 25px;">&nbsp;User&nbsp;role&nbsp;needs to be configured to create DAG Tags and Custom Tags.</b></p><p><br></p>', 'file.tags', true, '', '', 'admin', now());


insert into helpcenter_topic(topicid, description, content, context, active, type, external_link, createdby, createdate) values (22002, 'What is Popularityscore?', '<p><style type="text/css"><!--{cke_protected}{C}%3C!%2D%2Dtd%20%7Bborder%3A%201px%20solid%20%23ccc%3B%7Dbr%20%7Bmso-data-placement%3Asame-cell%3B%7D%2D%2D%3E--></style><strong>Popularity score</strong> is a score that provides a count on&nbsp;number of times&nbsp;user has interacted with this dataobject (table, tablecolumn, file, filecolumn, report, reportcolumn).</p>', 'file.popularity', true, '', '', 'admin', now());


insert into helpcenter_topic(topicid, description, content, context, active, type, external_link, createdby, createdate) values (22003, 'What is Importance Score?', '<p><style type="text/css"><!--{cke_protected}{C}%3C!%2D%2Dtd%20%7Bborder%3A%201px%20solid%20%23ccc%3B%7Dbr%20%7Bmso-data-placement%3Asame-cell%3B%7D%2D%2D%3E--></style>OvalEdge provides an importance score for each table, tablecolumn, file and report. It helps users decide how vital this data object is across the organization.</p>', 'file.importance', true, '', '', 'admin', now());


insert into helpcenter_topic(topicid, description, content, context, active, type, external_link, createdby, createdate) values (22004, 'What is Profiled?', '<p><style type="text/css"><!--{cke_protected}{C}%3C!%2D%2Dtd%20%7Bborder%3A%201px%20solid%20%23ccc%3B%7Dbr%20%7Bmso-data-placement%3Asame-cell%3B%7D%2D%2D%3E--></style>It displays the latest date and time that a specific data object is profiled to compute statistics of new data. If this attribute is empty, you can identify that the object has not been profiled.</p><p></p><p><!--{cke_protected}%3Cmeta%20charset%3D%22utf-8%22%20%2F%3E--></p><p dir="ltr"><!--{cke_protected}%3Cmeta%20charset%3D%22utf-8%22%20%2F%3E--><b id="docs-internal-guid-4e3610f4-7fff-f68e-e385-56bbbc580c78"><img data-cke-saved-src="oecss/images/helpcenter/light_bulb1.png" src="oecss/images/helpcenter/light_bulb1.png" style="width: 30px; height: 30px;"></b><strong>&nbsp;Do you know,&nbsp;you can compare the changes between the latest and previous profile dates of a table? Navigate to Data and Metadata Changes tab in Advanced Tools to see any changes.</strong></p>', 'file.analyzed', true, '', '', 'admin', now());


insert into helpcenter_topic(topicid, description, content, context, active, type, external_link, createdby, createdate) values (22006, 'Who are top users?', '<p><style type="text/css"><!--{cke_protected}{C}%3C!%2D%2Dtd%20%7Bborder%3A%201px%20solid%20%23ccc%3B%7Dbr%20%7Bmso-data-placement%3Asame-cell%3B%7D%2D%2D%3E--></style>Top users are experts who&nbsp;interacted mostly with the selected&nbsp;data object and can answer your question.</p>', 'file.topusers', true, '', '', 'admin', now());


insert into helpcenter_topic(topicid, description, content, context, active, type, external_link, createdby, createdate) values (22007, 'What is a term and how to use it for navigating data?', '<p>Terms are the common and standard&nbsp;definitions&nbsp;used within an organization. They are generally known as "Business glossaries". They are relevant to business domains used across the enterprise to&nbsp;reduce miscommunication and to create&nbsp;trust in your data.&nbsp;Users can also organize terms by adding a tag to the term for easy navigation.</p>', 'file.term', true, '', '', 'admin', now());


insert into helpcenter_topic(topicid, description, content, context, active, type, external_link, createdby, createdate) values (22008, 'What is the size of a file in this file system?', '<p>The size of the file is the amount of space it takes on your hard disk.</p>', 'size.file', true, '', '', 'admin', now());


insert into helpcenter_topic(topicid, description, content, context, active, type, external_link, createdby, createdate) values (22009, 'What is a file type?', '<p>A type of the file system denotes whether it is a "Folder" or a "File".</p>', 'file.type', true, '', '', 'admin', now());


insert into helpcenter_topic(topicid, description, content, context, active, type, external_link, createdby, createdate) values (22015, 'What is a business description?', '<p><!--{cke_protected}%3Cmeta%20charset%3D%22utf-8%22%20%2F%3E--></p><p dir="ltr">Descriptions are&nbsp;helpful for users to understand the data objects.&nbsp;You can manually enter the business description or you can bulk upload the business descriptions for all the data objects from a&nbsp;file format (.csv). You can download the template from <strong>Advance Tools &gt; Load metadata from the files</strong> module to enter and load&nbsp;the business descriptions from a file.</p><p><!--{cke_protected}%3Cmeta%20charset%3D%22utf-8%22%20%2F%3E--><b id="docs-internal-guid-574867a9-7fff-8dc5-8dbd-c0936ef001d9"><img data-cke-saved-src="oecss/images/helpcenter/pointer_hand.png" src="oecss/images/helpcenter/pointer_hand.png" style="width: 30px; height: 30px;"></b><b>&nbsp;Data stewards and&nbsp;users with Meta Write permission on a data object has the right to edit the business description of that object.&nbsp;</b></p>', 'files.busidesc', true, '', '', 'admin', now());


insert into helpcenter_topic(topicid, description, content, context, active, type, external_link, createdby, createdate) values (22016, 'What is the technical description?', '<p><!--{cke_protected}%3Cmeta%20charset%3D%22utf-8%22%20%2F%3E--></p><p dir="ltr">Any comments in the remote system are captured as a technical description in the crawling stage. The technical description also&nbsp;describes the technical functionality of a field. Additionally, you can enter one of the following&nbsp;in this attribute.&nbsp;</p><p><b id="docs-internal-guid-716b1b36-7fff-3c0d-25c7-271057c48a66">Example:&nbsp; </b>Details about the primary key, foreign key, field data type, calculations.</p>', 'files.techdesc', true, '', '', 'admin', now());


insert into helpcenter_topic(topicid, description, content, context, active, type, external_link, createdby, createdate) values (22017, 'What are remote tags?', '<p>The tags that are there in the remote repository is called remote tags.When OvalEdge connects to the file systems, it captures any existing metadata such as remote tags and stores in our database.<br></p>', 'f.remotetags', true, '', '', 'admin', now());


insert into helpcenter_topic(topicid, description, content, context, active, type, external_link, createdby, createdate) values (22019, 'What are the different file systems that OvalEdge supports?', '<p>OvalEdge connects to file systems and quickly organizes all the folders and files that are stored in the user''s local Network File System(NFS) server, Hadoop distributed file system(HDFS), cloud storage like Amazon S3 or Google cloud storage.<br></p>', 'f.filesystem', true, '', '', 'admin', now());


insert into helpcenter_topic(topicid, description, content, context, active, type, external_link, createdby, createdate) values (22020, 'When is the File Cataloged?', '<p>The file cataloged date represents the date when the file informatiion gets saved in the OvalEdge application.</p>', 'f.catalogged', true, '', '', 'admin', now());


insert into helpcenter_topic(topicid, description, content, context, active, type, external_link, createdby, createdate) values (22021, 'What does file column count represent?', '<p>The column headers in a file are called as file columns. The file column count represents the count of total column headers in a file.</p>', 'f.filecolumncount', true, '', '', 'admin', now());


insert into helpcenter_topic(topicid, description, content, context, active, type, external_link, createdby, createdate) values (22022, 'What is cataloging all sub-folders?', '<p>When different levels of subfolders exist in your root folder structure, you can catalog all the subfolders in it with a single click.</p><p><strong>Note:</strong> You have to enter how many levels of subfolders should be cataloged.</p><p>To catalog all subfolders,</p><ol><li>Navigate to the <strong>Data catalog--&gt;Files tab</strong>.</li><li>Select a <strong>root folder</strong>.</li><li>Click the nine <img src="oecss/images/helpcenter/ninedots1.png" id="photo-id" style="width: 25px; height: 25px;" data-cke-saved-src="oecss/images/helpcenter/ninedots1.png">dots and select Catalog all the subfolders. A pop-up page displays to enter the folder&nbsp;level up to which the subfolders should be cataloged.</li><li>Select a level. Example: level 2.&nbsp;</li></ol><p>&nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp;<img src="oecss/images/helpcenter/catalogsubfolder.png" id="photo-id" style="width: 200px; height: 100px;" data-cke-saved-src="oecss/images/helpcenter/catalogsubfolder.png"></p><p>&nbsp; &nbsp; &nbsp; &nbsp; &nbsp;The root folder is at level 0.</p><p>&nbsp; &nbsp; &nbsp; 5. Click Ok to start the process to catalog all the subfolders up to the level entered.</p><p>&nbsp; &nbsp; &nbsp; 6. Navigate to the Files tab to see the cataloged subfolders up to the level selected</p><p><img src="oecss/images/helpcenter/catalogsubfolder1.png" id="photo-id" style="width: 615px; height: 115px;" data-cke-saved-src="oecss/images/helpcenter/catalogsubfolder1.png"></p><p><b>&nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp;</b></p><p><br></p>', 'file.catalogsubfolders', true, '', '', 'admin', now());
insert into helpcenter_relation(topicid, childid) values(22022, 22028);


insert into helpcenter_topic(topicid, description, content, context, active, type, external_link, createdby, createdate) values (22023, 'What is cataloging all files?', '<p>When&nbsp;different levels of files exist in your root folder, you can catalog all the files in it with a single click. To catalog all the files in the subfolders you need to first catalog the subfolders where the file exists.</p><p>To catalog all files:</p><ol><li>Navigate to the Data catalog--&gt;Files tab.</li><li>Select a root folder.</li><li>Click the <img src="oecss/images/helpcenter/ninedots1.png" id="photo-id" style="width: 25px; height: 25px;" data-cke-saved-src="oecss/images/helpcenter/ninedots1.png">nine dots and select Catalog for all the files. A pop-up page displays to enter the folder&nbsp;level up to which the files should be cataloged.<img data-cke-saved-src="ovaledgeimages/story/srinidhi_Untitled picture" src="ovaledgeimages/story/srinidhi_Untitled picture" id="photo-id" style="width: 150px;height: 150px;"><!--{cke_protected}%3Cmeta%20charset%3D%22utf-8%22%20%2F%3E--><b id="docs-internal-guid-b1b7a8e9-7fff-843d-22fb-a347886c02bc"></b><b></b></li><li>Select a level. Example: level 2. The root folder is at level 0.<img src="oecss/images/helpcenter/catlogallfile1.png" id="photo-id" style="width: 250px; height: 100px;" data-cke-saved-src="oecss/images/helpcenter/catlogallfile1.png"></li><li>Click Ok to start the process to catalog all the files up to the level entered.</li><li>Navigate to the files tab to see the cataloged files at each level.</li></ol>', 'file.catalogfiles', true, '', '', 'admin', now());


insert into helpcenter_topic(topicid, description, content, context, active, type, external_link, createdby, createdate) values (22024, 'What is profiling a folder assuming same content means?', '<p>When users execute a Profile folder assuming the same content task, the profiler scans the first file under that selected folder and parses all the file columns in it. The collected file column names will be stored under that folder metadata.&nbsp;<br>By doing this task, the system does not have to profile all the 1000 files to retrieve the file columns.<br></p>', 'file.profilefolder', true, '', '', 'admin', now());


insert into helpcenter_topic(topicid, description, content, context, active, type, external_link, createdby, createdate) values (22025, 'What is profiling all files do?', '<p>Profiling allows you to collect the column statistics. To profile a file, it should be first cataloged. By default, the parameters to profile a file are added in the configuration module by OvalEdge administrators. When you have different levels of cataloged files in your root folder and subfolders, you can profile all the files in a single click using the profiling all files button.</p><p>Once the files are profiled, the files and column statistics are listed in the summary page. The data can then be used to run the Artificial Intelligence Algorithm for File Column tagging, and Lineage discovery of files and folders in OvalEdge.</p><p>To profile files in a cataloged folder and subfolder:</p><ol><li>Navigate to the Data catalog--&gt;Files tab.</li><li>Select a root folder.</li><li>Click the &nbsp;<img src="oecss/images/helpcenter/ninedots1.png" id="photo-id" style="width: 25px; height: 25px;" data-cke-saved-src="oecss/images/helpcenter/ninedots1.png">nine dots and select profile all the files. A pop-up page displays to enter the profile&nbsp;level upto which the files should be profiled.</li><li>Select a level. Example: level 2. The root folder is at level 0.</li><li>Click Ok to start the process to profile all the files upto the level entered.</li><li>Navigate to the profiled files and click the data tab to see the file contents.</li></ol><p>Note: To profile all the files in the subfolders you need to first catalog the subfolders and files.</p>', 'file.profilefile', true, '', '', 'admin', now());


insert into helpcenter_topic(topicid, description, content, context, active, type, external_link, createdby, createdate) values (22026, 'What does the status define?', '<p>The&nbsp;status attribute in a table or a file displays the analyzed state of the data object during profiling. The following are the different values:</p><ul><li><strong>Profiled -</strong> When full profiling is complete on the data object.</li><li><strong>NULL/Not Profiled -</strong> When the data object is not profiled.</li><li><strong>Sample Profiled -</strong> When sample profiling is conducted and completed on the data object.</li><li><strong>Partially Profiled -</strong> When the data object&nbsp;is Partially profiled.</li><li><strong>Profile Failed - </strong>When the profiling fails.</li></ul>', 'filetb.status', true, '', '', 'admin', now());


insert into helpcenter_topic(topicid, description, content, context, active, type, external_link, createdby, createdate) values (22027, 'What does file column count represent?', '<p>The column headers in a file are called as file columns. The file column count represents the count of total column headers in a file.</p>', 'f.filescolumncount', true, '', '', 'admin', now());


insert into helpcenter_topic(topicid, description, content, context, active, type, external_link, createdby, createdate) values (22028, 'What is the length of a field in file column?', '<p><style type="text/css"><!--{cke_protected}{C}%3C!%2D%2Dtd%20%7Bborder%3A%201px%20solid%20%23ccc%3B%7Dbr%20%7Bmso-data-placement%3Asame-cell%3B%7D%2D%2D%3E--></style>The length of a file column defined in the remote database.<br></p>', 'f.length', true, '', '', 'admin', now());


