insert into helpcenter_topic(topicid, description, content, context, active, type, external_link, createdby, createdate) values (23000, 'What is a report group?', '<p>The Report group is simply a group of Reports. Multiple Charts, dashboards, and workbooks can be grouped under a single report group to easily manage its security permissions.</p>', 'in.infographicgp', true, '', '', 'admin', now());
