insert into helpcenter_topic(topicid, description, content, context, active, type, external_link, createdby, createdate) values (23001, 'What is a business description?', '<p><!--{cke_protected}%3Cmeta%20charset%3D%22utf-8%22%20%2F%3E--></p><p dir="ltr">Descriptions are&nbsp;helpful for users to understand the data objects.&nbsp;You can manually enter the business description or you can bulk upload the business descriptions for all the data objects from a&nbsp;file format (.csv). You can download the template from <strong>Advance Tools &gt; Load metadata from the files</strong> module to enter and load&nbsp;the business descriptions from a file.</p><p><!--{cke_protected}%3Cmeta%20charset%3D%22utf-8%22%20%2F%3E--><b id="docs-internal-guid-574867a9-7fff-8dc5-8dbd-c0936ef001d9"><img data-cke-saved-src="oecss/images/helpcenter/pointer_hand.png" src="oecss/images/helpcenter/pointer_hand.png" style="width: 30px; height: 30px;"></b><b>&nbsp;Data stewards and&nbsp;users with Meta Write permission on a data object has the right to edit the business description of that object.&nbsp;</b></p>', 'reports.busidesc', true, '', '', 'admin', now());
insert into helpcenter_relation(topicid, childid) values(23001, 50005);


insert into helpcenter_topic(topicid, description, content, context, active, type, external_link, createdby, createdate) values (23003, 'What is a term and how to use it for navigating data?', '<p>Terms are the common and standard&nbsp;definitions&nbsp;used within an organization. They are generally known as "Business glossaries". They are relevant to business domains used across the enterprise to&nbsp;reduce miscommunication and to create&nbsp;trust in your data.&nbsp;Users can also organize terms by adding a tag to the term for easy navigation.</p>', 'reports.term', true, '', '', 'admin', now());


insert into helpcenter_topic(topicid, description, content, context, active, type, external_link, createdby, createdate) values (23007, 'What does the report column count represent?', '<p style="margin:0in 0in 8pt; text-align:justify">The column in a report is called report columns. The column count represents the count of total column headers used in a report.<br><!--{cke_protected}%3Cmeta%20charset%3D%22utf-8%22%20%2F%3E--></p><ul dir="ltr"></ul>', 'reportfield', true, '', '', 'admin', now());


insert into helpcenter_topic(topicid, description, content, context, active, type, external_link, createdby, createdate) values (23008, 'What are additional fields?', '<p style="margin-bottom:.0001pt; margin:0in 0in 8pt; text-align:justify;"><span style="font-size: 14px;"><span style="line-height: normal; color: rgb(0, 0, 0); font-family: Roboto, sans-serif;">Additional fields are known as the <strong>Custom Fields</strong>. Custom fields give OvalEdge users an opportunity to add more metadata content for each of the data objects in your Organization. OvalEdge provides a list of statistics or attributes by default such as table count, row count, column count, total row count, Minimum value, Maximum value, Null count, distinct count, density, and null density. If a user wants to add more information about the data objects, they can create additional fields called <strong>Custom fields</strong>.<br>Users can view the custom fields on each data object in the Data Catalog module.</span></span></p>', 'r.additionalfields', true, '', '', 'admin', now());


insert into helpcenter_topic(topicid, description, content, context, active, type, external_link, createdby, createdate) values (23009, 'What is subscribe/unsubscribe button used for?', '<p>The subscribe option allows the users to add the selected Reports to their personal dashboard space. The unsubscribe option removes the selected dashboard from the personal space.<br></p>', 'subsunsubsc', true, '', '', 'admin', now());


insert into helpcenter_topic(topicid, description, content, context, active, type, external_link, createdby, createdate) values (23011, 'What does the report type represent?', '<p>This represents the type of chart that is displayed. The report type is based on a remote tool from which the reports are captured.</p><table border="1" cellspacing=".75" cellpadding=".75" style="width: 500px;"><tbody><tr><td><strong>Remote Report Type</strong></td><td><strong>Report Type</strong></td></tr><tr><td>Tableau</td><td>Workbook/Dashboard/View</td></tr><tr><td>PowerBI</td><td>Dashboard/Tile</td></tr><tr><td>OvalEdge Reports</td><td>Bar Chart</td></tr></tbody></table>', 'report.type', true, '', '', 'admin', now());


insert into helpcenter_topic(topicid, description, content, context, active, type, external_link, createdby, createdate) values (23012, 'How to edit column/field names manually to build lineage?', '<p><style type="text/css"><!--{cke_protected}{C}%3C!%2D%2Dtd%20%7Bborder%3A%201px%20solid%20%23ccc%3B%7Dbr%20%7Bmso-data-placement%3Asame-cell%3B%7D%2D%2D%3E--></style>To Edit a column,</p><ul><li>Select a <strong>table</strong> from&nbsp;the source/destination lineage.</li><li>Click<strong>&nbsp;Edit column info</strong>.&nbsp;<em>A pop-up window opens</em>.</li><li>Select a Source and Target column name from the drop-down list.&nbsp;<em>These are the column names in the source and destination tables.</em></li><li>To delete a column from both the source and destination lineage table, click the delete button.</li><li>To add a column to both the source and destination lineage table, click the add&nbsp;button.</li></ul>', 'reportmanuallineageedit', true, '', '', 'admin', now());


