insert into helpcenter_topic(topicid, description, content, context, active, type, external_link, createdby, createdate) values (23015, 'How to add a new report group?', '<p dir="ltr" style="line-height: 1.38; background-color: rgb(255, 255, 255); margin-top: 0pt; margin-bottom: 0pt; position: static;"><strong>To add a new report group</strong></p><ul><li dir="ltr" style="line-height: 1.38; background-color: rgb(255, 255, 255); margin-top: 0pt; margin-bottom: 0pt; position: static;"><span style="font-size: 14px; font-family: Roboto, sans-serif; color: rgb(0, 0, 0); background-color: transparent; font-weight: 400; font-style: normal; font-variant: normal; text-decoration: none; vertical-align: baseline; white-space: pre-wrap; position: static;">Navigate to the Administration &gt; Security &gt; Report Groups.</span></li><li dir="ltr" style="line-height: 1.38; background-color: rgb(255, 255, 255); margin-top: 0pt; margin-bottom: 0pt; position: static;"><span style="color: rgb(0, 0, 0);"><span style="font-size: 14px;"><span style="font-family: Roboto,sans-serif;"><span style="background-color: transparent; font-weight: 400; font-style: normal; font-variant: normal; text-decoration: none; vertical-align: baseline; white-space: pre-wrap; position: static;">Click the&nbsp;</span><span style="background-color: transparent; font-weight: 700; font-style: normal; font-variant: normal; text-decoration: none; vertical-align: baseline; white-space: pre-wrap; position: static;">+</span><span style="background-color: transparent; font-weight: 400; font-style: normal; font-variant: normal; text-decoration: none; vertical-align: baseline; white-space: pre-wrap; position: static;">&nbsp;icon to Add a new Reports Group, pop-up window is displayed.&nbsp;</span></span></span></span></li><li dir="ltr" style="line-height: 1.38; background-color: rgb(255, 255, 255); margin-top: 0pt; margin-bottom: 0pt; position: static;"><span style="color: rgb(0, 0, 0);"><span style="font-size: 14px;"><spa