insert into helpcenter_topic(topicid, description, content, context, active, type, external_link, createdby, createdate) values (39000, 'How to compare the profiled results between two dates?', '<p>When a user Selects a particular table to know the changes the system automatically compares the data between the latest crawled dates. The graphical representation displays all the crawled dates of that table as a data point.User can select a particular data point to select the start and end date for comparison.</p><p><strong></strong><b id="docs-internal-guid-90892192-7fff-82de-1632-e0984f991865"><img data-cke-saved-src="oecss/images/helpcenter/pointer_hand.png" src="oecss/images/helpcenter/pointer_hand.png" style="width: 30px; height: 30px;"></b><strong>To select a date a user has to cancel one of the dates by clicking the cancel ??? &nbsp;button.</strong></p>', 'as.changesearch', true, '', '', 'admin', now());


insert into helpcenter_topic(topicid, description, content, context, active, type, external_link, createdby, createdate) values (39001, 'How to know when there is a data change?', '<p>A user can easily see the data changes highlighted in yellow.&nbsp;<br></p>', 'as.datacolorchange', true, '', '', 'admin', now());


insert into helpcenter_topic(topicid, description, content, context, active, type, external_link, createdby, createdate) values (39002, 'What is undefined mean when there is a data change?', '<p>A column name "UNDEFINED" across another column name means that there was no exisitng column in the corresponding table and that a new column is created at a later point in time.<br></p>', 'as.undefined', true, '', '', 'admin', now());


insert into helpcenter_topic(topicid, description, content, context, active, type, external_link, createdby, createdate) values (25002, 'What is a Parent tag? How to create them?', '<p>Master tags are parent or root tag.&#8203;&#8203;&#8203;&#8203;&#8203;&#8203;&#8203; <span style="font-size:12pt"><span style="line-height:107%"><span style="font-family:&quot;Times New Roman&quot;,serif">To create a parent tag:</span></span></span></p><p class="MsoNoSpacing" style="margin:0in 0in 0.0001pt"><span style="font-size:12pt"><span style="font-family:&quot;Times New Roman&quot;,serif">1. Select the&nbsp;<b>Home </b>tab from the object browser.</span></span></p><p class="MsoNoSpacing" style="margin:0in 0in 0.0001pt"><span style="font-size:12pt"><span style="font-family:&quot;Times New Roman&quot;,serif">2. Click the <img src="oecss/images/helpcenter/plus1.png" id="photo-id" style="width: 25px; height: 25px;" data-cke-saved-src="oecss/images/helpcenter/plus1.png">&nbsp;Add Tag icon from the left corner of the page.</span></span></p><p class="MsoNoSpacing" style="margin:0in 0in 0.0001pt"><span style="font-size:12pt"><span style="font-family:&quot;Times New Roman&quot;,serif">3. Enter a <b>Parent</b> <b>tag name</b> and click <b>Save</b>.</span></span></p><p class="MsoNoSpacing" style="margin:0in 0in 0.0001pt"><span style="font-size:12pt"><span style="font-family:&quot;Times New Roman&quot;,serif">&nbsp;&nbsp; <i>&nbsp;A new Parent&nbsp;Tag is created, and the user can group data elements under this tag.</i></span></span></p><p class="MsoNoSpacing" style="margin:0in 0in 0.0001pt"><br></p><p class="MsoNoSpacing" style="margin:0in 0in 0.0001pt"><span style="font-size:12pt"><span style="font-family:&quot;Times New Roman&quot;,serif">&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;<img src="oecss/images/helpcenter/addparenttag.png" id="photo-id" style="width: 300px; height: 150px;" data-cke-saved-src="oecss/images/helpcenter/addparenttag.png"></span></span></p><p><br><!--{cke_protected}%3Cmeta%20charset%3D%22utf-8%22%20%2F%3E--><b id="docs-internal-guid-4bb589cd-7fff-48ad-48de-ae911bfc757c"><img data-cke-saved-src="oecss/images/helpcenter/pointer_hand.png" src="oecss/images/helpcenter/pointer_hand.png" style="width: 50px; height: 50px;"></b><strong>Parent&nbsp;tag names are unique. The system does not allow to create multiple parent&nbsp;tags with the same name. However, tag names are Case Sensitive.</strong></p>', 'alltag.mastertag', true, '', '', 'admin', now());


insert into helpcenter_topic(topicid, description, content, context, active, type, external_link, createdby, createdate) values (25003, 'What is a Child Tag?', '<p>Tags that are a subset of Master/Parent tags are known as Child tags. Child tags are more in detail form and&nbsp;categorized under each Master/Parent tag.</p>', 'alltag.childtag', true, '', '', 'admin', now());


insert into helpcenter_topic(topicid, description, content, context, active, type, external_link, createdby, createdate) values (25004, 'How to add a tag as a child to a parent tag?', '<p style="margin:0in 0in 8pt; text-align:justify;"><span style="font-size:12pt;"><span style="line-height:107%;"><span style=''font-family:"Times New Roman",serif;''><strong>&ldquo;Child tags&rdquo;</strong> are subtags created within a Parent Tag. Users can create a new child tag or alternatively add existing tags as a child tag.</span></span></span></p><p style="margin:0in 0in 8pt; text-align:justify;"><span style="font-size:12pt;"><span style="line-height:107%;"><span style=''font-family:"Times New Roman",serif;''><span style="color:#4472c4;">To create a new child tag:</span></span></span></span></p><ol><li style="list-style-type:none;">1.&nbsp;<span style="font-size:12pt;"><span style=''font-family:"Times New Roman",serif;''>Select the <strong>Home&nbsp;</strong>tab from the object browser.</span></span></li><li style="list-style-type:none;"><span style="font-size:12pt;"><span style=''font-family:"Times New Roman",serif;''>2.&nbsp;</span></span><span style="font-size:12pt;"><span style=''font-family:"Times New Roman",serif;''>Select a Master tag from the page.</span></span></li><li style="list-style-type:none;"><span style="font-size:12pt;"><span style=''font-family:"Times New Roman",serif;''>3. Click the&nbsp;<img src="oecss/images/helpcenter/plus1.png" id="photo-id" style="width: 25px; height: 25px;" data-cke-saved-src="oecss/images/helpcenter/plus1.png" class="fr-fil fr-dib">&nbsp; Add Tag icon from the list.</span></span></li><li style="list-style-type:none;"><span style="font-size:12pt;"><span style=''font-family:"Times New Roman",serif;''>4. Enter a&nbsp; <strong>tag name</strong> and click S<strong>ave</strong>.</span></span></li></ol><p class="MsoNoSpacing" style="margin:0in 0in 0.0001pt;"><span style="font-size:12pt;"><span style=''font-family:"Times New Roman",serif;''><em>&nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; A new Child Tag is created, and the user can group data elements under this tag.</em></span></span></p><p class="MsoNoSpacing" style="margin-left:1.25in; margin:0in 0in 0.0001pt;"><br></p><p class="MsoNoSpacing" style="margin:0in 0in 0.0001pt;"><span style="font-size:12pt;"><span style=''font-family:"Times New Roman",serif;''><span style="color:#4472c4;">To add a tag as a child tag:</span></span></span></p><p class="MsoNoSpacing" style="margin:0in 0in 0.0001pt;"><br></p><p class="MsoNoSpacing" style="margin:0in 0in 0.0001pt;"><span style="font-size:12pt;"><span style=''font-family:"Times New Roman",serif;''>1. Select the&nbsp;<strong>Home&nbsp;</strong>tab from the object browser.</span></span></p><p class="MsoNoSpacing" style="margin:0in 0in 0.0001pt;"><span style="font-size:12pt;"><span style=''font-family:"Times New Roman",serif;''>2. Select a <strong>Parent</strong> Tag.</span></span></p><p class="MsoNoSpacing" style="margin:0in 0in 0.0001pt;"><span style="font-size:12pt;"><span style=''font-family:"Times New Roman",serif;''>3. Click the&nbsp;<img src="oecss/images/helpcenter/information.png" id="photo-id" style="width: 25px; height: 25px;" data-cke-saved-src="oecss/images/helpcenter/information.png" class="fr-fil fr-dib"><em>&nbsp;</em> icon from the right corner.</span></span></p><p class="MsoNoSpacing" style="margin:0in 0in 0.0001pt;"><span style="font-size:12pt;"><span style=''font-family:"Times New Roman",serif;''>4.&nbsp;</span></span><span style="font-size:12pt;"><span style=''font-family:"Times New Roman",serif;''>The Parent edit tag detail window opens.</span></span></p><p class="MsoNoSpacing" style="margin:0in 0in 0.0001pt;"><span style="font-size:12pt;"><span style=''font-family:"Times New Roman",serif;''>5. Click the &nbsp;<img src="oecss/images/helpcenter/plus1.png" id="photo-id" style="width: 25px; height: 25px;" data-cke-saved-src="oecss/images/helpcenter/plus1.png" class="fr-fil fr-dib">&nbsp; icon in the child tag option.</span></span></p><p class="MsoNoSpacing" style="margin:0in 0in 0.0001pt;"><span style="font-size:12pt;"><span style=''font-family:"Times New Roman",serif;''><em>&nbsp;&nbsp;&nbsp; A drop-down menu displays a list of existing tags.</em></span></span></p><p class="MsoNoSpacing" style="margin:0in 0in 0.0001pt;"><span style="font-size:12pt;"><span style=''font-family:"Times New Roman",serif;''>6.&nbsp;</span></span><span style="font-size:12pt;"><span style=''font-family:"Times New Roman",serif;''><em>Alternatively, search&nbsp;the child tag and choose the matching result&nbsp;from the drop-down list.</em></span></span></p><p class="MsoNoSpacing" style="margin:0in 0in 0.0001pt;"><span style="font-size:12pt;"><span style=''font-family:"Times New Roman",serif;''>7. Click to save the tag.&nbsp;</span></span><span style="font-size:12pt;"><span style=''font-family:"Times New Roman",serif;''><em>The child tag gets added to the master tag.</em></span></span><span style="font-size:12pt;"><span style=''font-family:"Times New Roman",serif;''><em>&nbsp;</em><em>&nbsp; &nbsp;&nbsp;&nbsp;&nbsp; &nbsp; &nbsp;&nbsp;&nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp;</em></span></span></p><p class="MsoNoSpacing" style="margin:0in 0in 0.0001pt;"><span style="font-size:12pt;"><span style=''font-family:"Times New Roman",serif;''><em>&nbsp;&nbsp;&nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp;&nbsp; &nbsp; &nbsp; &nbsp; &nbsp;&nbsp;</em></span></span></p><p class="MsoNoSpacing" style="text-align:justify; margin:0in 0in 0.0001pt;"><b id="docs-internal-guid-a3e15776-7fff-e0d5-ba25-5436bdfec5de"><img style="width: 20px; height: 20px;" src="oecss/images/helpcenter/pointer_hand.png" data-cke-saved-src="oecss/images/helpcenter/pointer_hand.png" class="fr-fil fr-dib"></b><span style="font-size:12pt;"><span style="background:#c5e0b3;"><span style=''font-family:"Times New Roman",serif;''><span style="background:#c5e0b3;"><span style="color:black;">Users can add a <strong>Parent tag</strong> in the same way as a child tag. Select a <strong>Tag(Master/Child)</strong> and</span></span> <span style="background:#c5e0b3;"><span style="color:black;">click the + icon near the parent tag. The parent tag added will become the Root tag of the tag</span></span> <span style="background:#c5e0b3;"><span style="color:black;">selected.</span></span></span></span></span></p>', 'alltag.addchildtag', true, '', '', 'admin', now());


insert into helpcenter_topic(topicid, description, content, context, active, type, external_link, createdby, createdate) values (25005, 'How to delete a Parent and Child tag?', '<p style="margin:0in 0in 8pt; text-align:justify"><span style="font-size:12pt"><span style="line-height:107%"><span style="font-family:&quot;Times New Roman&quot;,serif"><span style="color:#4472c4">To delete a Parent&nbsp;Tag:</span></span></span></span></p><p class="MsoNoSpacing" style="margin:0in 0in 0.0001pt"><span style="font-size:12pt"><span style="font-family:&quot;Times New Roman&quot;,serif">&nbsp;1.&nbsp;</span></span><span style="font-size:12pt"><span style="font-family:&quot;Times New Roman&quot;,serif">Select and click a <b>Parent</b> Tag.</span></span></p><p class="MsoNoSpacing" style="margin:0in 0in 0.0001pt"><span style="font-size:12pt"><span style="font-family:&quot;Times New Roman&quot;,serif">2. Click the <img src="oecss/images/helpcenter/information.png" id="photo-id" style="width: 25px; height: 25px;" data-cke-saved-src="oecss/images/helpcenter/information.png">icon.</span></span></p><p class="MsoNoSpacing" style="margin:0in 0in 0.0001pt"><span style="font-size:12pt"><span style="font-family:&quot;Times New Roman&quot;,serif">3. Scroll to the bottom of the edit tag and select <img src="oecss/images/helpcenter/deletetag.png" id="photo-id" style="width: 100px; height: 50px;" data-cke-saved-src="oecss/images/helpcenter/deletetag.png">.</span></span></p><p class="MsoNoSpacing" style="margin:0in 0in 0.0001pt"><span style="font-size:12pt"><span style="font-family:&quot;Times New Roman&quot;,serif">&nbsp;&nbsp;&nbsp; <i>A pop-up window is displayed asking for user confirmation to delete the selection. The child &nbsp;&nbsp;&nbsp;&nbsp;</i></span></span></p><p class="MsoNoSpacing" style="margin:0in 0in 0.0001pt"><span style="font-size:12pt"><span style="font-family:&quot;Times New Roman&quot;,serif"><i>&nbsp;&nbsp; tags associated with the selected master tag gets added to the main Tag window.</i></span></span></p><p class="MsoNoSpacing" style="margin:0in 0in 0.0001pt"><img src="oecss/images/helpcenter/deletetagicon.png" id="photo-id" style="width: 150px; height: 50px;" data-cke-saved-src="oecss/images/helpcenter/deletetagicon.png"></p><p class="MsoNoSpacing" style="margin:0in 0in 0.0001pt"><span style="font-size:12pt"><span style="font-family:&quot;Times New Roman&quot;,serif">&nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp;&nbsp;</span></span><span style="font-size:12pt"><span style="font-family:&quot;Times New Roman&quot;,serif">&nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp;&nbsp;&nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp;&nbsp;</span></span></p><p class="MsoNoSpacing" style="margin:0in 0in 0.0001pt">&#8203;&#8203;&#8203;&#8203;&#8203;&#8203;&#8203;<span style="font-size:12pt"><span style="line-height:107%"><span style="font-family:&quot;Times New Roman&quot;,serif"><span style="color:#4472c4">To delete a child tag,</span></span></span></span>&nbsp;follow the same process by choosing the child tag.<br><!--{cke_protected}%3Cmeta%20charset%3D%22utf-8%22%20%2F%3E--><b id="docs-internal-guid-52aa7213-7fff-b1c5-d331-a6c654f4c3eb"><img data-cke-saved-src="oecss/images/helpcenter/pointer_hand.png" src="oecss/images/helpcenter/pointer_hand.png" style="width: 25px; height: 25px;"></b><strong>When a user deletes a Parent tag, all the child tags associated with it gets converted to root tags.</strong></p>', 'alltag.delete', true, '', '', 'admin', now());


insert into helpcenter_topic(topicid, description, content, context, active, type, external_link, createdby, createdate) values (25006, 'How to disable a tag associated to a data object?', '<p><strong>To disable a Master tag from an associated data object,</strong></p><ul><li>Click on the Master tag.</li><li>All the&nbsp;data objects associated with the selected tag are classified and listed.</li><li>Click on a&nbsp;specific data object.</li><li>In the tag section, select an existing tag and then click the delete&nbsp;icon to remove the tag.</li></ul><p><strong>To disable a Child tag from an associated data object,</strong></p><ul><li>Click on a child tag and follow the same steps as in the Master tag.</li></ul>', 'tag.disabletag', true, '', '', 'admin', now());


insert into helpcenter_topic(topicid, description, content, context, active, type, external_link, createdby, createdate) values (25007, 'What permissions should a user have to create tag?', '<p>To create tags, a user_role must be configured as a <strong>Tag_Role</strong>. Adding data objects to the tags are based on Metadata permissions on the data attributes as follows:</p><ul><li><em><strong>Meta Read-only</strong></em>&#8211;Has rights to only view the existing tag names and names of the data objects associated with it. Access to data object content is based on data permissions.</li><li><em><strong>Meta Read-Write</strong></em>&#8211;Has the right to view and add new data objects to the existing tags. Access to data object content is based on data permissions.</li></ul><p><b id="docs-internal-guid-52aa7213-7fff-b1c5-d331-a6c654f4c3eb"><img data-cke-saved-src="oecss/images/helpcenter/pointer_hand.png" src="oecss/images/helpcenter/pointer_hand.png" style="width: 25px; height: 25px;"></b><strong>Access to data object content is based on data permissions.</strong></p>', 'alltag.tagpermission', true, '', '', 'admin', now());


insert into helpcenter_topic(topicid, description, content, context, active, type, external_link, createdby, createdate) values (25008, 'How to add an image to a tag?', '<p>To help distinguish tags you manage on OvalEdge (and help your users find them), you can add an image that appears in the thumbnail.&nbsp;<br>To set a tag image:</p><ul><li>Login to OvalEdge.</li><li>Choose and click a&nbsp;master or child tag.</li><li>Click the i button.</li><li>Click the change picture tab.</li><li>Choose an image file from the directory and open it.</li><li>The image gets added to your tag.</li></ul><p><!--{cke_protected}%3Cmeta%20charset%3D%22utf-8%22%20%2F%3E--><b id="docs-internal-guid-d1e6ca36-7fff-aa50-de62-4422e17e64da"><img data-cke-saved-src="oecss/images/helpcenter/pointer_hand.png" src="oecss/images/helpcenter/pointer_hand.png" style="width: 25px; height: 25px;"></b><strong>The image must be a standard internet format: .jpg, png, or gif.</strong></p>', 'tag.addthumbnail', true, '', '', 'admin', now());


insert into helpcenter_topic(topicid, description, content, context, active, type, external_link, createdby, createdate) values (25010, 'What is additional information?', '<p>This field provides an opportunity to add more metadata content/additional information for each of the data objects. &nbsp;If you want to add more information about data, user can create custom fields from the Custom field module.<br></p>', 't.additionalfields', true, '', '', 'admin', now());


insert into helpcenter_topic(topicid, description, content, context, active, type, external_link, createdby, createdate) values (25011, 'What is a Parent tag?', '<p>Master tags are Parent or Root tags. Each tag includes a name and description for identification purposes. You can&nbsp;add child tags to a parent&nbsp;tag through edit option.</p>', 'edittags.parenttag', true, '', '', 'admin', now());


insert into helpcenter_topic(topicid, description, content, context, active, type, external_link, createdby, createdate) values (25012, 'What are Child tags?', '<p><style type="text/css"><!--{cke_protected}{C}%3C!%2D%2Dtd%20%7Bborder%3A%201px%20solid%20%23ccc%3B%7Dbr%20%7Bmso-data-placement%3Asame-cell%3B%7D%2D%2D%3E--></style>Tags that are a subset of parent tags are known as Child tags. Child tags are more in detail form and categorized under each Master/Parent tag.<br></p>', 'edittags.childtag', true, '', '', 'admin', now());


insert into helpcenter_topic(topicid, description, content, context, active, type, external_link, createdby, createdate) values (26001, 'How to send a message to a particular User?', '<p><style type="text/css"><!--{cke_protected}{C}%3C!%2D%2Dtd%20%7Bborder%3A%201px%20solid%20%23ccc%3B%7Dbr%20%7Bmso-data-placement%3Asame-cell%3B%7D%2D%2D%3E--></style>Users can send messages to a particular member with the help of an ''@'' sign. Type the <strong>@ identifier</strong> in the chat window, and select the USER from the user list.Type the message and post it to the selected user.<br></p>', 'mes.particularuser', true, '', '', 'admin', now());


insert into helpcenter_topic(topicid, description, content, context, active, type, external_link, createdby, createdate) values (26003, 'What is the use of @ sign annotation?', '<p><style type="text/css"><!--{cke_protected}{C}%3C!%2D%2Dtd%20%7Bborder%3A%201px%20solid%20%23ccc%3B%7Dbr%20%7Bmso-data-placement%3Asame-cell%3B%7D%2D%2D%3E--></style>@ sign is an identifier. A user may start typing a message and can use &#8216;@&#8217; to pull up a list (as below) for the user to select any to include it as part of the messaging.<br>User(@US), Business Glossary (@BG), Projects (@PR), Tables (@TB), Queries (@QY), Conversations (@CV), Table Columns (@TC), Files (@FL) 8. File Columns(@FC), Reports (@RP), Report Columns(@RC) and Data Stories (@ST).</p>', 'mes.annotation', true, '', '', 'admin', now());


insert into helpcenter_topic(topicid, description, content, context, active, type, external_link, createdby, createdate) values (26004, 'Where can I receive my messages or notifications?', '<p><style type="text/css"><!--{cke_protected}{C}%3C!%2D%2Dtd%20%7Bborder%3A%201px%20solid%20%23ccc%3B%7Dbr%20%7Bmso-data-placement%3Asame-cell%3B%7D%2D%2D%3E--></style>Users can receive their&nbsp;notification in the message icon on the top of the application screen.&nbsp;They can also access their messages from the My Resources&gt;&gt;Inbox Module. The messages are categorized into the following types:</p><ul><li>Collaborations</li><li>System Alerts</li><li>Watchlist</li><li>Service desk</li><li>Data Quality</li><li>All Messages</li></ul><p><!--{cke_protected}%3Cmeta%20charset%3D%22utf-8%22%20%2F%3E--><!--{cke_protected}%3Cmeta%20charset%3D%22utf-8%22%20%2F%3E--><b id="docs-internal-guid-90892192-7fff-82de-1632-e0984f991865"><img data-cke-saved-src="oecss/images/helpcenter/pointer_hand.png" src="oecss/images/helpcenter/pointer_hand.png" style="width: 30px; height: 30px;">T</b>o work with OvalEdge, users must have a user account and email-id created for them. Users should log in to the application to see their messages.</p>', 'mes.messagealerts', true, '', '', 'admin', now());


insert into helpcenter_topic(topicid, description, content, context, active, type, external_link, createdby, createdate) values (26005, 'How to mark all unanswered messages as "responded"?', '<p>User can view all their messages in the "All Messages" category in the Inbox module. A user may select the &#8217;mark as done&#8217; checkbox against each of the messages to move them here under the &#8216;All Messages&#8217; tab. This helps the user to maintain their messaging/inbox workspace and avoid the overpopulation of many messages.</p>', 'mes.answeredmessages', true, '', '', 'admin', now());


insert into helpcenter_topic(topicid, description, content, context, active, type, external_link, createdby, createdate) values (26006, 'How to delete a conversation?', '<p><style type="text/css"><!--{cke_protected}{C}%3C!%2D%2Dtd%20%7Bborder%3A%201px%20solid%20%23ccc%3B%7Dbr%20%7Bmso-data-placement%3Asame-cell%3B%7D%2D%2D%3E--></style>A user can select a message and delete the entire conversation by clicking the ''Delete conversation'' button on the top right corner of the screen.&#8203;&#8203;&#8203;&#8203;&#8203;&#8203;&#8203;<br><!--{cke_protected}%3Cmeta%20charset%3D%22utf-8%22%20%2F%3E--><!--{cke_protected}%3Cmeta%20charset%3D%22utf-8%22%20%2F%3E--><b id="docs-internal-guid-9a307195-7fff-690f-9311-3f9b20e8ddb1"><img data-cke-saved-src="oecss/images/helpcenter/pointer_hand.png" src="oecss/images/helpcenter/pointer_hand.png" style="width: 30px; height: 30px;"></b><b>&nbsp;</b>Click <img src="oecss/images/helpcenter/ninedots1.png" id="photo-id" style="width: 25px; height: 25px;" data-cke-saved-src="oecss/images/helpcenter/ninedots1.png"><strong>nine dots&nbsp;</strong>sign to unfold the function.</p>', 'mes.deletemessages', true, '', '', 'admin', now());


insert into helpcenter_topic(topicid, description, content, context, active, type, external_link, createdby, createdate) values (26007, 'What are the elements available in the @ sign annotation?', '<p><style type="text/css"><!--{cke_protected}{C}%3C!%2D%2Dtd%20%7Bborder%3A%201px%20solid%20%23ccc%3B%7Dbr%20%7Bmso-data-placement%3Asame-cell%3B%7D%2D%2D%3E--></style><style type="text/css"><!--{cke_protected}{C}%3C!%2D%2Dtd%20%7Bborder%3A%201px%20solid%20%23ccc%3B%7Dbr%20%7Bmso-data-placement%3Asame-cell%3B%7D%2D%2D%3E--></style><style type="text/css"><!--{cke_protected}{C}%3C!%2D%2Dtd%20%7Bborder%3A%201px%20solid%20%23ccc%3B%7Dbr%20%7Bmso-data-placement%3Asame-cell%3B%7D%2D%2D%3E--></style></p><p><!--{cke_protected}%3Cmeta%20charset%3D%22utf-8%22%20%2F%3E--></p><p dir="ltr">The following items can be selected to use with the @ annotation in messages.</p><ul><li>Users</li><li>Business Glossary</li><li>Projects</li><li>Tables</li><li>Queries</li><li>Table Columns</li><li>Files</li><li>File Columns</li><li>Reports</li><li>Report Columns</li><li>Data Stories</li></ul>', 'mes.elementssign', true, '', '', 'admin', now());


insert into helpcenter_topic(topicid, description, content, context, active, type, external_link, createdby, createdate) values (26009, 'How to reply to a message received on a data object?', '<p>All of the messages in the Inbox module are categorized into 5 types. A user may select the "Collaboration" or&nbsp;"All Messages" to view their messages. When you open a message received on a data object, the screen will direct you to the corresponding data object. You can click on the inbox icon on the right of the screen to view your message. You can then select the reply button to directly respond to a user. Alternatively, go to a particular object and click the <img src="oecss/images/helpcenter/inbox_msg.png" id="photo-id" style="width: 25px; height: 25px;" data-cke-saved-src="oecss/images/helpcenter/inbox_msg.png">inbox button to directly open the conversations on that object and respond to a specific user.</p>', 'mes.conversation', true, '', '', 'admin', now());
insert into helpcenter_relation(topicid, childid) values(26009, 26005);


insert into helpcenter_topic(topicid, description, content, context, active, type, external_link, createdby, createdate) values (26010, 'What messages are created as service desk alerts?', '<p>A Service Request is a user request raised on the below types:<br><strong>Access</strong>- to request data access permissions (like Data Preview, Data Read, and Data Write) on table and report.<br><strong>Content Change</strong>- Request stewards to modify the business description, tags, and terms for a table/column/file/report/business glossary.<br><strong>Data Quality</strong>- Request raised to report a data quality issue on a table/column/file/report/business glossary.<br><strong>New Asset Request</strong>- A request to create a new table and table column at the data source.<br><strong>Other</strong>- Request to notify stewards of any additional changes required on the data objects such as a change of User password.<br></p>', 'mes.servicedeskalert', true, '', '', 'admin', now());


insert into helpcenter_topic(topicid, description, content, context, active, type, external_link, createdby, createdate) values (26011, 'What messages are displayed as data quality alerts?', '<p>Alerts related to Data Quality rules are viewed under this tab.<br></p>', 'mes.dqralertnotfi', true, '', '', 'admin', now());


insert into helpcenter_topic(topicid, description, content, context, active, type, external_link, createdby, createdate) values (27000, 'What is a Story Zone?', '<p><style type="text/css"><!--{cke_protected}{C}%3C!%2D%2Dtd%20%7Bborder%3A%201px%20solid%20%23ccc%3B%7Dbr%20%7Bmso-data-placement%3Asame-cell%3B%7D%2D%2D%3E--></style><strong>Story Zones</strong> are used to create Data Stories or documents about data. This helps in knowledge sharing and can quickly access related articles or data stories. Each data story can be grouped under a Story Zone. Each Story zone can have one or more "Parent" data stories. Each parent story has single or multiple "Child" stories.</p>', 'ds.storydomain', true, '', '', 'admin', now());


insert into helpcenter_topic(topicid, description, content, context, active, type, external_link, createdby, createdate) values (27001, 'How to create a Story Zone?', '<p><style type="text/css"><!--{cke_protected}{C}%3C!%2D%2Dtd%20%7Bborder%3A%201px%20solid%20%23ccc%3B%7Dbr%20%7Bmso-data-placement%3Asame-cell%3B%7D%2D%2D%3E--></style></p><p><!--{cke_protected}%3Cmeta%20charset%3D%22utf-8%22%20%2F%3E--></p><p dir="ltr">To create a Story Zone:</p><ol dir="ltr"><li>Expand the <strong>Administration</strong> tab in the object browser and select &nbsp;Security.</li><li>Select the Story Zone tab from the security menu bar.&nbsp;</li><li>Click <img src="oecss/images/helpcenter/plus1.png" id="photo-id" style="width: 25px; height: 25px;" data-cke-saved-src="oecss/images/helpcenter/plus1.png">&nbsp;to add a Story Zone. A dialogue box opens for user input.</li><li>Enter the Story Zone name and description.</li><li>Click <strong>Save</strong> to save the name and return to the security page.</li></ol><p dir="ltr"><!--{cke_protected}%3Cmeta%20charset%3D%22utf-8%22%20%2F%3E--><b id="docs-internal-guid-e58253ec-7fff-f2f4-2aa6-00b4961466ea"><img data-cke-saved-src="oecss/images/helpcenter/pointer_hand.png" src="oecss/images/helpcenter/pointer_hand.png" style="width: 30px; height: 30px;"></b><b> Only application administrators can create a Story Zone.</b></p>', 'ds.createdsdomain', true, '', '', 'admin', now());


insert into helpcenter_topic(topicid, description, content, context, active, type, external_link, createdby, createdate) values (27002, 'How secured is a Story Zone?', '<p><style type="text/css"><!--{cke_protected}{C}%3C!%2D%2Dtd%20%7Bborder%3A%201px%20solid%20%23ccc%3B%7Dbr%20%7Bmso-data-placement%3Asame-cell%3B%7D%2D%2D%3E--></style></p><p><!--{cke_protected}%3Cmeta%20charset%3D%22utf-8%22%20%2F%3E--></p><p dir="ltr">Only application administrators can create a <strong>Story Zone</strong>. An Administrator can grant other user_roles Read and Write permissions to access the contents in the data story module.</p>', 'ds.secureddomain', true, '', '', 'admin', now());


insert into helpcenter_topic(topicid, description, content, context, active, type, external_link, createdby, createdate) values (27003, 'How to find a Story Zone?', '<p><style type="text/css"><!--{cke_protected}{C}%3C!%2D%2Dtd%20%7Bborder%3A%201px%20solid%20%23ccc%3B%7Dbr%20%7Bmso-data-placement%3Asame-cell%3B%7D%2D%2D%3E--></style><!--{cke_protected}%3Cmeta%20charset%3D%22utf-8%22%20%2F%3E--></p><p dir="ltr"><b>Users</b> can search the Story Zone through&nbsp;the data lake search or in&nbsp;the Data stories&nbsp;module. Each Story zones are displayed on the main level.</p>', 'ds.locatedsdomain', true, '', '', 'admin', now());


insert into helpcenter_topic(topicid, description, content, context, active, type, external_link, createdby, createdate) values (27004, 'How to create a Data Story?', '<p><!--{cke_protected}%3Cmeta%20charset%3D%22utf-8%22%20%2F%3E--></p><p dir="ltr"><b id="docs-internal-guid-6da9485b-7fff-7626-0b46-09524d1686ea">T</b>o write&nbsp;a data story, a user should first create a Story Zone. Once you create a story zone, navigate the cursor to the data story module, and select the story zone. Click the + icon on the right of the page to add the first data story. To add successive data stories, select a parent data story and then click the nine dots to Add new child stories.</p><p><!--{cke_protected}%3Cmeta%20charset%3D%22utf-8%22%20%2F%3E--><b id="docs-internal-guid-20a42f09-7fff-9780-39ba-efb93efce568"><img data-cke-saved-src="oecss/images/helpcenter/pointer_hand.png" src="oecss/images/helpcenter/pointer_hand.png" style="width: 30px; height: 30px;"></b><b> To learn more about actions performed on the data story, click <img src="oecss/images/helpcenter/ninedots1.png" id="photo-id" style="width: 25px; height: 25px;" data-cke-saved-src="oecss/images/helpcenter/ninedots1.png">&nbsp;&nbsp;icon.</b></p>', 'ds.createstory', true, '', '', 'admin', now());


insert into helpcenter_topic(topicid, description, content, context, active, type, external_link, createdby, createdate) values (27005, 'What are the user permissions to edit the data Story?', '<p>The edit option depends on the data story domain permissions. Users with a data story domain Read-only permission can only view the data story. Whereas, users with Read-Write permission has editing capabilities. Editing a data story allows users to:</p><p><!--{cke_protected}%3Cmeta%20charset%3D%22utf-8%22%20%2F%3E--></p><ul dir="ltr"><li><b id="docs-internal-guid-59004023-7fff-a27e-36ae-717d6661ef80">F</b>ormat</li><li><b id="docs-internal-guid-59004023-7fff-a27e-36ae-717d6661ef80">S</b>tyle</li><li><b id="docs-internal-guid-59004023-7fff-a27e-36ae-717d6661ef80">I</b>nsert images and content</li><li><b id="docs-internal-guid-59004023-7fff-a27e-36ae-717d6661ef80">S</b>pell check&nbsp;</li><li><b id="docs-internal-guid-59004023-7fff-a27e-36ae-717d6661ef80">C</b>hange story zone/parent story/author/order</li><li><b id="docs-internal-guid-59004023-7fff-a27e-36ae-717d6661ef80">D</b>elete a story</li></ul><p dir="ltr"><!--{cke_protected}%3Cmeta%20charset%3D%22utf-8%22%20%2F%3E--><b id="docs-internal-guid-9098a11e-7fff-dfb7-09a7-7c31b4207437"><img data-cke-saved-src="oecss/images/helpcenter/pointer_hand.png" src="oecss/images/helpcenter/pointer_hand.png" style="width: 30px; height: 30px;"></b><b> Only OvalEdge Admins has the right to grant Story Zone Read-only/Read-write permissions to other users.</b></p>', 'ds.editstory', true, '', '', 'admin', now());


insert into helpcenter_topic(topicid, description, content, context, active, type, external_link, createdby, createdate) values (28000, 'What is the purpose of cataloging a file?', '<p><style type="text/css"><!--{cke_protected}{C}%3C!%2D%2Dtd%20%7Bborder%3A%201px%20solid%20%23ccc%3B%7Dbr%20%7Bmso-data-placement%3Asame-cell%3B%7D%2D%2D%3E--></style>A file that is cataloged is available for search in the data catalog.<br></p>', 'fm.catalog', true, '', '', 'admin', now());


insert into helpcenter_topic(topicid, description, content, context, active, type, external_link, createdby, createdate) values (28001, 'What is the size of a file in this file system?', '<p>The size of the file is the amount of space it takes on your hard disk.</p>', 'size', true, '', '', 'admin', now());


insert into helpcenter_topic(topicid, description, content, context, active, type, external_link, createdby, createdate) values (29000, 'What are ways of creating a query, other than from a Query sheet?', '<p><style type="text/css"><!--{cke_protected}{C}%3C!%2D%2Dtd%20%7Bborder%3A%201px%20solid%20%23ccc%3B%7Dbr%20%7Bmso-data-placement%3Asame-cell%3B%7D%2D%2D%3E--></style>Users can create and save new queries on any database and schema from the Query search page.</p><ul><li>Click the&nbsp;<img src="oecss/images/helpcenter/ninedots1.png" id="photo-id" style="width: 25px; height: 25px;" data-cke-saved-src="oecss/images/helpcenter/ninedots1.png">&nbsp; and choose Add New Query on this page to create a new query.</li></ul>', 'qs.creatingquery', true, '', '', 'admin', now());


insert into helpcenter_topic(topicid, description, content, context, active, type, external_link, createdby, createdate) values (29002, 'What is a Cataloged Query?', '<p><style type="text/css"><!--{cke_protected}{C}%3C!%2D%2Dtd%20%7Bborder%3A%201px%20solid%20%23ccc%3B%7Dbr%20%7Bmso-data-placement%3Asame-cell%3B%7D%2D%2D%3E--></style>A cataloged query is a saved query with a name. These queries are available for search in the data catalog module. Users can launch the cataloged queries to make any changes and to create and publish reports from the query editor page.<br></p>', 'qs.savedquery', true, '', '', 'admin', now());


insert into helpcenter_topic(topicid, description, content, context, active, type, external_link, createdby, createdate) values (29003, 'How do I display fewer columns to preview my query results?', '<p><style type="text/css"><!--{cke_protected}{C}%3C!%2D%2Dtd%20%7Bborder%3A%201px%20solid%20%23ccc%3B%7Dbr%20%7Bmso-data-placement%3Asame-cell%3B%7D%2D%2D%3E--></style>In Query sheet, when a user selects a source database|schema and a table, it displays all the fields of the table by default. Click the <img src="oecss/images/helpcenter/queryfilter.png" id="photo-id" style="width: 25px; height: 25px;" data-cke-saved-src="oecss/images/helpcenter/queryfilter.png">&nbsp;sign on the right corner of this page. Choose the required fields to display.<br><!--{cke_protected}%3Cmeta%20charset%3D%22utf-8%22%20%2F%3E--><!--{cke_protected}%3Cmeta%20charset%3D%22utf-8%22%20%2F%3E--><b id="docs-internal-guid-83421091-7fff-8c44-5293-c16b82d318d1"><img data-cke-saved-src="oecss/images/helpcenter/pointer_hand.png" src="oecss/images/helpcenter/pointer_hand.png" style="width: 30px; height: 30px;"></b><b>&nbsp;</b>You can select or unselect the fields needed in the output.</p>', 'qs.excolumn', true, '', '', 'admin', now());


insert into helpcenter_topic(topicid, description, content, context, active, type, external_link, createdby, createdate) values (29004, 'How can I add column alias to query results without writing scripts?', '<p><style type="text/css"><!--{cke_protected}{C}%3C!%2D%2Dtd%20%7Bborder%3A%201px%20solid%20%23ccc%3B%7Dbr%20%7Bmso-data-placement%3Asame-cell%3B%7D%2D%2D%3E--></style>By default, the column of a query result set is the name given in the SELECT statement. To rename a column name in the query result, select the <strong>"Output Column name</strong>" function from the menu bar or use the shortcut icon <img src="oecss/images/helpcenter/output-icon1.jpg" id="photo-id" style="width: 25px; height: 25px;" data-cke-saved-src="oecss/images/helpcenter/output-icon1.jpg">. The query result will have the renamed column names.<br><br></p>', 'qs.outputcolumnname', true, '', '', 'admin', now());


insert into helpcenter_topic(topicid, description, content, context, active, type, external_link, createdby, createdate) values (29005, 'How does "Define Output" function?', '<p>This allows the user to recreate/append the query result on the table defined.<br></p>', 'qs.defineop', true, '', '', 'admin', now());


insert into helpcenter_topic(topicid, description, content, context, active, type, external_link, createdby, createdate) values (29006, 'What is a Historic Query?', '<p>All the queries executed in the query editor are recorded as historic queries. A user can simply view the latest query executed and save the query by selecting the catalog button. Additionally, a user can view the results of the historic queries by clicking the &#39;view result&#39; icon on this page. </p>', 'qs.histquery', true, '', '', 'admin', now());


insert into helpcenter_topic(topicid, description, content, context, active, type, external_link, createdby, createdate) values (29007, 'What is the Filter function?', '<p><span data-sheets-userformat=''{"2":15165,"3":{"1":0},"5":{"1":[{"1":2,"2":0,"5":[null,2,0]},{"1":0,"2":0,"3":3},{"1":1,"2":0,"4":1}]},"6":{"1":[{"1":2,"2":0,"5":[null,2,0]},{"1":0,"2":0,"3":3},{"1":1,"2":0,"4":1}]},"7":{"1":[{"1":2,"2":0,"5":[null,2,0]},{"1":0,"2":0,"3":3},{"1":1,"2":0,"4":1}]},"8":{"1":[{"1":2,"2":0,"5":[null,2,0]},{"1":0,"2":0,"3":3},{"1":1,"2":0,"4":1}]},"11":4,"12":0,"14":[null,2,16711680],"15":"Arial","16":12}'' data-sheets-value=''{"1":2,"2":"With data filters, users can select a subset of rows in the worksheet that meets the given criteria. "}'' style="font-size: 12pt; font-family: Arial; font-weight: normal; font-style: normal; color: rgb(0, 0, 0); position: static;">With data filters, users can select a subset of rows in the worksheet that meets the given criteria. </span> </p>', 'qs.filter', true, '', '', 'admin', now());


insert into helpcenter_topic(topicid, description, content, context, active, type, external_link, createdby, createdate) values (30000, 'What is a Data Lake?', '<p>A data lake is a platform which stores vast amounts of raw data in its native format. The data can be:</p><ul><li>Structured data from relational databases-SQL Server, MYSQL, Oracle</li><li>Structured data from NoSQL databases-mongo, DB, Cassandra</li><li>Semi-structured data-CSV, logs, XML, JSON</li><li>Unstructured data-emails, documents, PDFs</li><li>Binary data-images, audio, video</li></ul>', 'de.datalakedef', true, '', '', 'admin', now());


insert into helpcenter_topic(topicid, description, content, context, active, type, external_link, createdby, createdate) values (30001, 'What are the different Data Lake technology stacks used in OvalEdge?', '<p><style type="text/css"><!--{cke_protected}{C}%3C!%2D%2Dtd%20%7Bborder%3A%201px%20solid%20%23ccc%3B%7Dbr%20%7Bmso-data-placement%3Asame-cell%3B%7D%2D%2D%3E--></style><style type="text/css"><!--{cke_protected}{C}%3C!%2D%2Dtd%20%7Bborder%3A%201px%20solid%20%23ccc%3B%7Dbr%20%7Bmso-data-placement%3Asame-cell%3B%7D%2D%2D%3E--></style>The data lake technology stack OvalEdge supports are Network File Systems(NFS), Amazon S3, Hadoop Distributed File System(HDFS), Google Cloud Storage, Azure Data Lake(ADL).</p>', 'de.datalaketype', true, '', '', 'admin', now());


insert into helpcenter_topic(topicid, description, content, context, active, type, external_link, createdby, createdate) values (30003, 'What is Directory?', '<p><style type="text/css"><!--{cke_protected}{C}%3C!%2D%2Dtd%20%7Bborder%3A%201px%20solid%20%23ccc%3B%7Dbr%20%7Bmso-data-placement%3Asame-cell%3B%7D%2D%2D%3E--></style>A Directory is nothing but a container, used to organize folders and files into a hierarchical structure.</p>', 'de.directory', true, '', '', 'admin', now());


insert into helpcenter_topic(topicid, description, content, context, active, type, external_link, createdby, createdate) values (30004, 'How to share files and folders on OvalEdge?', '<p><style type="text/css"><!--{cke_protected}{C}%3C!%2D%2Dtd%20%7Bborder%3A%201px%20solid%20%23ccc%3B%7Dbr%20%7Bmso-data-placement%3Asame-cell%3B%7D%2D%2D%3E--></style><style type="text/css"><!--{cke_protected}{C}%3C!%2D%2Dtd%20%7Bborder%3A%201px%20solid%20%23ccc%3B%7Dbr%20%7Bmso-data-placement%3Asame-cell%3B%7D%2D%2D%3E--></style>To upload the files or folders on OvalEdge:</p><ul><li>Click the Data Exchange tab in the object browser and select Upload files or folders.</li><li>Select a data lake from the existing list, where you wish to store your data. Click <strong>Next.</strong>&nbsp;<span style="font-size:12pt"><span style="line-height:107%"><span style="font-family:&quot;Times New Roman&quot;,serif">&#8203;&#8203;&#8203;&#8203;&#8203;&#8203;&#8203; </span></span></span></li><li>Select Your Directory, where you want to import your file from the list of existing directories.&nbsp;<em>Alternatively, you can create a new directory by clicking the Action tab on the left-hand section below. Give a unique name for the new directory.</em>&nbsp;</li><li>Click <strong>Next</strong>.&nbsp;</li><li>Set the toggle key to<em><strong> File/ Folder</strong></em> to upload data content.&nbsp;<span style="font-size:12pt"><span style="line-height:107%"><span style="font-family:&quot;Times New Roman&quot;,serif">&nbsp; &nbsp; &nbsp;&nbsp;</span></span></span></li><li>Select the data content from the source computer.</li><li>Click<em><strong> Finish</strong></em>.</li></ul>', 'de.upfilesfolders', true, '', '', 'admin', now());


insert into helpcenter_topic(topicid, description, content, context, active, type, external_link, createdby, createdate) values (30005, 'How do I create a new directory?', '<p><!--{cke_protected}%3Cmeta%20name%3D%22generator%22%20content%3D%22Sheets%22%20%2F%3E--><style type="text/css"><!--{cke_protected}{C}%3C!%2D%2Dtd%20%7Bborder%3A%201px%20solid%20%23ccc%3B%7Dbr%20%7Bmso-data-placement%3Asame-cell%3B%7D%2D%2D%3E--></style></p><p>To create a new directory using the upload files or folders application:</p><ul><li>Select a <strong>data lake</strong> and click next.</li><li>Click the <strong>Action</strong> button and select <strong>Create Directory</strong>.</li><li>Enter the new Directory Name.&nbsp;</li><li>Click <strong>Ok</strong>.</li></ul>', 'de.crestedir', true, '', '', 'admin', now());


insert into helpcenter_topic(topicid, description, content, context, active, type, external_link, createdby, createdate) values (30006, 'What is staging table?What is the purpose of staging table?', '<p>A staging table is a temporary database table containing the data. It is used to store the source data temporarily, and prepare the data before loading it into the target table.<br><!--{cke_protected}%3Cmeta%20charset%3D%22utf-8%22%20%2F%3E--><b id="docs-internal-guid-f25ad137-7fff-8d26-4095-7b07382baaf0"><img data-cke-saved-src="oecss/images/helpcenter/pointer_hand.png" src="oecss/images/helpcenter/pointer_hand.png" style="width: 25px; height: 25px;"></b><strong>The staging table stg_tbl_catalog exists in Ovaledgedb.</strong></p>', 'de.stgtble', true, '', '', 'admin', now());


insert into helpcenter_topic(topicid, description, content, context, active, type, external_link, createdby, createdate) values (30007, 'Where is the staging table in OvalEdge?', '<p>OvalEdge database <strong>ovaledgedb</strong> has a staging table called <strong>"stg_tbl_catalog"</strong>.</p>', 'de.locatestgtbl', true, '', '', 'admin', now());


insert into helpcenter_topic(topicid, description, content, context, active, type, external_link, createdby, createdate) values (30008, 'What is Block Size?', '<p><style type="text/css"><!--{cke_protected}{C}%3C!%2D%2Dtd%20%7Bborder%3A%201px%20solid%20%23ccc%3B%7Dbr%20%7Bmso-data-placement%3Asame-cell%3B%7D%2D%2D%3E--></style>The block size is the smallest unit of data that a file system can store. If you store a file that''s 1k or 60Mb, it''ll take up one block. Once you cross the 64Mb boundry, you need a second block.<br></p>', 'de.blocksz', true, '', '', 'admin', now());


insert into helpcenter_topic(topicid, description, content, context, active, type, external_link, createdby, createdate) values (30009, 'What is Replication?', '<p><style type="text/css"><!--{cke_protected}{C}%3C!%2D%2Dtd%20%7Bborder%3A%201px%20solid%20%23ccc%3B%7Dbr%20%7Bmso-data-placement%3Asame-cell%3B%7D%2D%2D%3E--></style>Data Replication is the process of storing data in more than one site or node. It is useful in improving the availability of data. It is simply copying data from a database from one server to another server so that all the users can share the same data without any inconsistency.<br></p>', 'de.replication', true, '', '', 'admin', now());


insert into helpcenter_topic(topicid, description, content, context, active, type, external_link, createdby, createdate) values (30010, 'What datatype conversions are supported in OvalEdge Database Exchange?', '<p>Data type conversions refer to changing a value from one data type format to another. &nbsp;Users can choose to convert the data types explicitly. Conversion to timestamp/date automatically is possible in OvalEdge.<br></p>', 'de.datatypeconv', true, '', '', 'admin', now());


insert into helpcenter_topic(topicid, description, content, context, active, type, external_link, createdby, createdate) values (30011, 'How to import a file into a table?', '<p><style type="text/css"><!--{cke_protected}{C}%3C!%2D%2Dtd%20%7Bborder%3A%201px%20solid%20%23ccc%3B%7Dbr%20%7Bmso-data-placement%3Asame-cell%3B%7D%2D%2D%3E--></style>There are two modes to import data from a file to a table:&nbsp;</p><ol><li>Automatic&nbsp;</li><li>Manual</li></ol><p><strong>Automatic file table transfer:</strong></p><ul><li>Enter a Filename.<em>A drop-down menu displays all the files which have a matching filename in the entire data lake.</em>&nbsp;</li><li>Choose the appropriate name for further action.&nbsp;</li><li>Select a database and a schema location where the target table exists.</li><li>Set the toggle key to Automatic to import the file into the table automatically.</li><li>Click Import file into a table.<br></li></ul><p><strong>Manual file table transfer:</strong></p><ul><li>Enter a Filename.&nbsp;<em>A drop-down menu displays all the files which have a matching filename in the entire data lake</em>.&nbsp;</li><li>Choose the appropriate name for further action.</li><li>Select a database and a schema location where the target table exists.&nbsp;</li><li>Set the toggle key to Manual and choose the Table name.</li><li>Click Get the columns to get all the columns in the file.&nbsp;<em>All the columns in the file selected are displayed as a result in a grid view.</em></li><li>Change the datatype of selected fields if necessary or choose to keep the original data type.</li><li>Set the toggle key to Append, if the user needs to add more rows of data to the table.&nbsp; Alternatively, set the toggle key to Recreate, if the user needs to transfer data to the table.</li><li>Enter a Job name and click Import file into a table.&nbsp;<em>A pop-up message displays a job successfully created for the file into table data transfer.</em>&nbsp;</li><li>Validate the data transfer to the new table created in the target database, by searching the smart catalog.</li></ul>', 'de.importfiletbl', true, '', '', 'admin', now());


insert into helpcenter_topic(topicid, description, content, context, active, type, external_link, createdby, createdate) values (30012, 'What are the different ways to import data?', '<p>Data import can happen between files, folders, databases, and tables. OvalEdge has different applications for each type of data transfer.</p><ul><li><strong>Create Table-</strong> This application is used for creating a new table from file, query or a table.</li><li><strong>Upload Files or Folders-</strong>Upload files or folders from your computer.</li><li><strong>File Table Echange-</strong>To exchange data between file and table.</li><li><strong>Database Exchange-</strong>To exchange data between various kinds of databases.</li><li><strong>FTP Import-</strong>To transfer data from FTP/SCP server.</li></ul>', 'de.waystoimport', true, '', '', 'admin', now());


insert into helpcenter_topic(topicid, description, content, context, active, type, external_link, createdby, createdate) values (30013, 'Can I create a new table during the database exchange?', '<p>Yes. OvalEdge provides an option to create a new table and append the data during data transfer. Also, users can Overwrite the data to an existing table.<br></p>', 'de.createnewtbl', true, '', '', 'admin', now());


insert into helpcenter_topic(topicid, description, content, context, active, type, external_link, createdby, createdate) values (30014, 'What are the possible ways of database exchange?', '<p>The database exchange allows to perform data transfer from:<br>&#9679; &nbsp;Cataloged Query to Table transfer<br>&#9679; &nbsp;Table to table transfer<br></p>', 'de.waysofexch', true, '', '', 'admin', now());


insert into helpcenter_topic(topicid, description, content, context, active, type, external_link, createdby, createdate) values (30015, 'What are the two modes in which a query is transferred to a table?', '<p>There are two modes how data from a query is moved to a table<br>&#9679; &nbsp;Query to existing table<br>&#9679; &nbsp;Query to New table<br></p>', 'de.modeofexch', true, '', '', 'admin', now());


insert into helpcenter_topic(topicid, description, content, context, active, type, external_link, createdby, createdate) values (30016, 'How do I move the data from a query to an existing table?', '<p style="margin:0in 0in 0.0001pt 0.5in; text-align:justify"><span style="font-size:12pt"><span style="line-height:107%"><span style="font-family:Calibri,sans-serif"><span style="font-family:&quot;Times New Roman&quot;,serif">1. Click the <b>Data Exchange</b> tab in the object browser and select <b>Database Exchange</b>.</span></span></span></span></p><p style="margin:0in 0in 0.0001pt 0.5in; text-align:justify"><span style="font-size:12pt"><span style="line-height:107%"><span style="font-family:Calibri,sans-serif"><span style="font-family:&quot;Times New Roman&quot;,serif">2. Select New data transfer.</span></span></span></span></p><p style="margin:0in 0in 0.0001pt 0.5in; text-align:justify"><span style="font-size:12pt"><span style="line-height:107%"><span style="font-family:Calibri,sans-serif"><span style="font-family:&quot;Times New Roman&quot;,serif">3. Select <b>from the Query</b> radio button, to transfer data from the cataloged/saved query.</span></span></span></span></p><p style="margin:0in 0in 8pt 0.5in; text-align:justify"><span style="font-size:12pt"><span style="line-height:107%"><span style="font-family:Calibri,sans-serif">4. Enter a cataloged query name.</span></span></span><span style="font-size:12pt"><span style="font-family:&quot;Times New Roman&quot;,serif"><i><span style="background:white">&nbsp;A drop-down menu displays the query which has matching query name in the entire data</span></i></span></span><span style="font-size:12pt"><span style="font-family:&quot;Times New Roman&quot;,serif"><i><span style="background:white">&nbsp;lake. Choose the Query for further action.</span></i></span></span></p><p style="margin:0in 0in 8pt; text-align:justify"><span style="font-size:12pt"><span style="line-height:107%"><span style="font-family:&quot;Times New Roman&quot;,serif">&nbsp; &nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; <span style="color:black"><img id="image76.jpg" style="width: 600px; height: 200px;" data-cke-saved-src="oecss/images/helpcenter/qtoexisting.png" src="oecss/images/helpcenter/qtoexisting.png" alt="qtoexisting"> </span></span></span></span></p><p style="margin:0in 0in 0.0001pt 0.5in; text-align:justify"><span style="font-size:12pt"><span style="line-height:107%"><span style="font-family:Calibri,sans-serif"><span style="background:white"><span style="font-family:&quot;Times New Roman&quot;,serif">5. Select a <b>database and a schema</b> location where the target table exists.</span></span></span></span></span></p><p style="margin:0in 0in 8pt 0.5in; text-align:justify"><span style="font-size:12pt"><span style="line-height:107%"><span style="font-family:Calibri,sans-serif"><span style="font-family:&quot;Times New Roman&quot;,serif">6. Set the toggle key to <b>Existing</b> and select an existing <b>table name</b>.</span></span></span></span></p><p style="margin:0in 0in 8pt 0.5in; text-align:justify"><span style="font-size:12pt"><span style="font-family:&quot;Times New Roman&quot;,serif"><span style="background:white">7. Click <b>Get the columns</b> to get all the columns from the query.&nbsp;</span></span></span><span style="font-size:12pt"><span style="font-family:&quot;Times New Roman&quot;,serif"><i><span style="background:white">&nbsp;All the columns in the query selected are displayed as a result in&nbsp;a grid view.</span></i></span></span></p><p style="margin:0in 0in 8pt 0.5in; text-align:justify"><span style="font-size:12pt"><span style="line-height:107%"><span style="font-family:Calibri,sans-serif"><span style="background:white"><span style="font-family:&quot;Times New Roman&quot;,serif">8. Choose and map each column from <b>Query</b>(Source columns) to the columns in the Destination table. </span></span></span></span></span><span style="font-size:12pt"><span style="line-height:107%"><span style="font-family:&quot;Times New Roman&quot;,serif"><i><span style="background:white">Change the <b>data type</b> of selected fields if necessary or choose to keep the original data type.</span></i></span></span></span></p><p style="margin:0in 0in 8pt; text-align:justify">&nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp;<span style="font-size:12pt"><span style="line-height:107%"><span style="font-family:&quot;Times New Roman&quot;,serif"><img id="image73.jpg" style="width: 700px; height: 200px;" data-cke-saved-src="oecss/images/helpcenter/qtoexisting2.png" src="oecss/images/helpcenter/qtoexisting2.png" alt="qtoexisting2"> </span></span></span></p><p style="margin:0in 0in 0.0001pt 0.5in; text-align:justify"><span style="font-size:12pt"><span style="line-height:107%"><span style="font-family:Calibri,sans-serif"><span style="background:white"><span style="font-family:&quot;Times New Roman&quot;,serif">9. Set the toggle key to <b>Append</b>, if the user needs to add more rows of data to the table selected from <b>step 6</b>. </span></span></span></span></span></p><p style="margin:0in 0in 8pt 0.5in; text-align:justify"><span style="font-size:12pt"><span style="line-height:107%"><span style="font-family:Calibri,sans-serif"><span style="font-family:&quot;Times New Roman&quot;,serif">10. Alternatively, set the toggle key to <b>overwrite</b>, if the user needs to overwrite the data to the table selected from <b>step 6</b>.</span></span></span></span></p><p style="margin:0in 0in 0.0001pt" class="MsoNoSpacing"><span style="font-size:12pt"><span style="font-family:&quot;Times New Roman&quot;,serif">&nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; 11. Enter a Job name and Click Transfer data.&nbsp;</span></span><span style="font-size:12pt"><span style="font-family:&quot;Times New Roman&quot;,serif"><i>A pop-up message displays a job successfully created for the file into table data transfer.</i></span></span></p><p style="margin:0in 0in 8pt 0.5in; text-align:justify"><span style="font-size:12pt"><span style="line-height:107%"><span style="font-family:Calibri,sans-serif"><span style="background:white"><span style="font-family:&quot;Times New Roman&quot;,serif">12. Validate the data transfer to the table created in the target database, by searching the smart catalog.</span></span></span></span></span></p>', 'de.quetbl', true, '', '', 'admin', now());


insert into helpcenter_topic(topicid, description, content, context, active, type, external_link, createdby, createdate) values (30017, 'How do I move the data from a query to a new table?', '<p style="margin:0in 0in 0.0001pt 0.5in; text-align:justify"><span style="font-size:12pt"><span style="line-height:107%"><span style="font-family:Calibri,sans-serif"><span style="font-family:&quot;Times New Roman&quot;,serif">1. Click the <b>Data Exchange</b> tab in the object browser and select <b>Database Exchange</b>.</span></span></span></span></p><p style="margin:0in 0in 0.0001pt 0.5in; text-align:justify"><span style="font-size:12pt"><span style="line-height:107%"><span style="font-family:Calibri,sans-serif"><span style="font-family:&quot;Times New Roman&quot;,serif">2. Select New data transfer.</span></span></span></span></p><p style="margin:0in 0in 8pt 0.5in; text-align:justify"><span style="font-size:12pt"><span style="line-height:107%"><span style="font-family:Calibri,sans-serif"><span style="font-family:&quot;Times New Roman&quot;,serif">3. Select <b>from the Query</b> radio button, to transfer data from cataloged/saved query.</span></span></span></span></p><p style="margin:0in 0in 0.0001pt" class="MsoNoSpacing"><span style="font-size:12pt"><span style="font-family:&quot;Times New Roman&quot;,serif">&nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; 4. Enter a cataloged query name.&nbsp;</span></span><span style="font-size:12pt"><span style="font-family:&quot;Times New Roman&quot;,serif"><i><span style="background:white">A drop-down menu displays the query which has matching query name in the entire data </span></i></span></span><span style="font-size:12pt"><span style="font-family:&quot;Times New Roman&quot;,serif"><i><span style="background:white">lake. Choose the Query for further action.</span></i></span></span></p><p style="margin:0in 0in 8pt; text-align:justify"><span style="font-size:12pt"><span style="line-height:107%"><span style="font-family:&quot;Times New Roman&quot;,serif">&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; <span style="color:black"><img id="image76.jpg" style="width: 700px; height: 200px;" data-cke-saved-src="oecss/images/helpcenter/qtoexisting.png" src="oecss/images/helpcenter/qtoexisting.png" alt="qtoexisting"> </span></span></span></span></p><p style="margin:0in 0in 0.0001pt 0.5in; text-align:justify"><span style="font-size:12pt"><span style="line-height:107%"><span style="font-family:Calibri,sans-serif"><span style="background:white"><span style="font-family:&quot;Times New Roman&quot;,serif">5. Select a <b>database and a schema</b> location where the target table exists.</span></span></span></span></span></p><p style="margin:0in 0in 8pt 0.5in; text-align:justify"><span style="font-size:12pt"><span style="line-height:107%"><span style="font-family:Calibri,sans-serif"><span style="font-family:&quot;Times New Roman&quot;,serif">6. Set the toggle key to <b>create</b> and select a <b>table name</b>.</span></span></span></span></p><p style="margin:0in 0in 0.0001pt" class="MsoNoSpacing"><span style="font-size:12pt"><span style="font-family:&quot;Times New Roman&quot;,serif">&nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; 7. Set the toggle key to <b>overwrite</b> the data to the table selected from <b>step 6</b>.&nbsp;</span></span><span style="font-size:12pt"><span style="font-family:&quot;Times New Roman&quot;,serif"><i>The <b>append</b> key will be <b>disabled</b>.</i></span></span></p><p style="margin:0in 0in 0.0001pt" class="MsoNoSpacing"><span style="font-size:12pt"><span style="font-family:&quot;Times New Roman&quot;,serif">&nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; 8. Enter a Job name and Click Transfer data.&nbsp;</span></span><span style="font-size:12pt"><span style="font-family:&quot;Times New Roman&quot;,serif"><i>A pop-up message displays a job successfully created for the file into table data transfer.</i></span></span></p><p style="margin:0in 0in 8pt 0.5in; text-align:justify"><span style="font-size:12pt"><span style="line-height:107%"><span style="font-family:Calibri,sans-serif"><span style="background:white"><span style="font-family:&quot;Times New Roman&quot;,serif">9. Validate the data transfer to the table created in the target database, by searching the smart catalog.</span></span></span></span></span></p><p style="margin:0in 0in 8pt; text-align:justify">&nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp;&nbsp;<span style="font-size:12pt"><span style="line-height:107%"><span style="font-family:&quot;Times New Roman&quot;,serif">&nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp;<img id="image72.jpg" style="width: 700px; height: 200px;" data-cke-saved-src="oecss/images/helpcenter/qtonew.png" src="oecss/images/helpcenter/qtonew.png" alt="qtonew"> </span></span></span></p>', 'de.quenewtbl', true, '', '', 'admin', now());


insert into helpcenter_topic(topicid, description, content, context, active, type, external_link, createdby, createdate) values (30018, 'How to transfer data from table to table in OvalEdge?', '<p style="margin:0in 0in 0.0001pt 0.5in; text-align:justify"><span style="font-size:12pt"><span style="line-height:107%"><span style="font-family:Calibri,sans-serif"><span style="font-family:&quot;Times New Roman&quot;,serif">1. Click the <b>Data Exchange</b> tab in the object browser and select <b>Database Exchange</b>.</span></span></span></span></p><p style="margin:0in 0in 8pt 0.5in; text-align:justify"><span style="font-size:12pt"><span style="line-height:107%"><span style="font-family:Calibri,sans-serif"><span style="font-family:&quot;Times New Roman&quot;,serif">2. Select New data transfer.</span></span></span></span></p><p style="margin:0in 0in 8pt 0.5in; text-align:justify"><span style="font-size:12pt"><span style="font-family:&quot;Times New Roman&quot;,serif">3. Select <b>from the Table</b> radio button, to transfer data from the Source table.</span></span></p><p style="margin:0in 0in 8pt 0.5in; text-align:justify"><span style="font-size:12pt"><span style="font-family:&quot;Times New Roman&quot;,serif">4. Enter the source table name.&nbsp;</span></span><span style="font-size:12pt"><span style="font-family:&quot;Times New Roman&quot;,serif"><i><span style="background:white">A drop-down menu displays all the matching table names in the entire data lake. Choose</span></i></span></span><span style="font-size:12pt"><span style="font-family:&quot;Times New Roman&quot;,serif"><i><span style="background:white">&nbsp;the table for further action.</span></i></span></span></p><p style="margin:0in 0in 8pt; text-align:justify"><span style="font-size:12pt"><span style="line-height:107%"><span style="font-family:&quot;Times New Roman&quot;,serif">&nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp;&nbsp;</span></span></span><span style="font-size:12pt"><span style="line-height:107%"><span style="font-family:&quot;Times New Roman&quot;,serif">&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; <span style="color:black"><img id="image63.jpg" style="width: 700px; height: 200px;" data-cke-saved-src="oecss/images/helpcenter/tabtotab1.png" src="oecss/images/helpcenter/tabtotab1.png" alt="tabtotab1"> </span></span></span></span></p><p style="margin:0in 0in 8pt; text-align:justify"><span style="font-size:12pt"><span style="line-height:107%"><span style="font-family:Calibri,sans-serif"><span style="background:white"><span style="font-family:&quot;Times New Roman&quot;,serif">&nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp;5. Select a <b>database and a schema</b> location where the target table exists.</span></span></span></span></span></p><p style="margin:0in 0in 8pt; text-align:justify"><span style="font-size:12pt"><span style="line-height:107%"><span style="font-family:Calibri,sans-serif"><span style="background:white"><span style="font-family:&quot;Times New Roman&quot;,serif">&nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp;6.&nbsp;</span></span></span></span></span><span style="font-size:12pt"><span style="line-height:107%"><span style="font-family:Calibri,sans-serif"><span style="font-family:&quot;Times New Roman&quot;,serif">Set the toggle key to <b>Existing</b> and select an existing <b>table name</b>.</span></span></span></span></p><p style="margin:0in 0in 8pt 0.5in; text-align:justify"><span style="font-size:12pt"><span style="font-family:&quot;Times New Roman&quot;,serif"><span style="background:white">7. Click <b>Get the columns</b> to get all the columns in the file.&nbsp;</span></span></span><span style="font-size:12pt"><span style="font-family:&quot;Times New Roman&quot;,serif"><i><span style="background:white">All the columns in the file selected are displayed as a result in a grid view.</span></i></span></span></p><p style="margin:0in 0in 8pt 0.5in; text-align:justify"><span style="font-size:12pt"><span style="font-family:&quot;Times New Roman&quot;,serif"><span style="background:white">8. Choose and map each column from the source table to the columns in the destination table.&nbsp;</span></span></span><span style="font-size:12pt"><span style="font-family:&quot;Times New Roman&quot;,serif"><i><span style="background:white">Change the <b>data type</b> of selected fields if necessary or choose to keep the original data </span></i></span></span><span style="font-size:12pt"><span style="font-family:&quot;Times New Roman&quot;,serif"><i><span style="background:white">type.</span></i></span></span></p><p style="margin:0in 0in 8pt; text-align:justify"><span style="font-size:12pt"><span style="line-height:107%"><span style="font-family:&quot;Times New Roman&quot;,serif">&nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; <img id="image77.jpg" style="width: 700px; height: 200px;" data-cke-saved-src="oecss/images/helpcenter/tabtotab2.png" src="oecss/images/helpcenter/tabtotab2.png" alt="tabtotab2"> </span></span></span></p><p style="margin:0in 0in 0.0001pt 0.5in; text-align:justify"><span style="font-size:12pt"><span style="line-height:107%"><span style="font-family:Calibri,sans-serif"><span style="background:white"><span style="font-family:&quot;Times New Roman&quot;,serif">9. Set the toggle key to <b>Append</b>, if the user needs to add more rows of data to the table selected from <b>step 6</b>. </span></span></span></span></span></p><p style="margin:0in 0in 8pt 0.5in; text-align:justify"><span style="font-size:12pt"><span style="line-height:107%"><span style="font-family:Calibri,sans-serif"><span style="font-family:&quot;Times New Roman&quot;,serif">10. Alternatively, set the toggle key to <b>overwrite</b>, if the user needs to overwrite the data to the table selected from <b>step 6</b>.</span></span></span></span></p><p style="margin:0in 0in 8pt; text-align:justify">&nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp;&nbsp;<span style="font-size:12pt"><span style="line-height:107%"><span style="font-family:&quot;Times New Roman&quot;,serif">&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; <img id="image71.jpg" style="width: 350px; height: 75px;" data-cke-saved-src="oecss/images/helpcenter/tabtotab3.png" src="oecss/images/helpcenter/tabtotab3.png" alt="tabtotab3"> &nbsp;&nbsp;&nbsp;&nbsp;<img id="image61.jpg" style="width: 350px; height: 75px;" data-cke-saved-src="oecss/images/helpcenter/tabtotab4.png" src="oecss/images/helpcenter/tabtotab4.png" alt="tabtotab4"> </span></span></span></p><p style="margin:0in 0in 0.0001pt" class="MsoNoSpacing"><span style="font-size:12pt"><span style="font-family:&quot;Times New Roman&quot;,serif">&nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; 11. Enter a Job name and Click Transfer data.&nbsp;</span></span><span style="font-size:12pt"><span style="font-family:&quot;Times New Roman&quot;,serif"><i>A pop-up message displays a job successfully created for the file into table data transfer.</i></span></span></p><p style="margin:0in 0in 8pt 0.5in; text-align:justify"><span style="font-size:12pt"><span style="line-height:107%"><span style="font-family:Calibri,sans-serif"><span style="background:white"><span style="font-family:&quot;Times New Roman&quot;,serif">12. Validate the data transfer to the table created in the target database, by searching the smart catalog.</span></span></span></span></span></p>', 'de.tbltbl', true, '', '', 'admin', now());


insert into helpcenter_topic(topicid, description, content, context, active, type, external_link, createdby, createdate) values (30019, 'How to Catalog a file?', '<ol><li class="MsoNoSpacing" style="margin:0in 0in 0.0001pt"><span style="font-size:12pt"><span style="font-family:&quot;Times New Roman&quot;,serif">Select a file/folder and Click the + sign on the catalog section.</span></span></li></ol><p class="MsoNoSpacing" style="margin:0in 0in 0.0001pt"><span style="font-size:12pt"><span style="font-family:&quot;Times New Roman&quot;,serif">&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; <i>A pop-up window to perform the catalog action is displayed. Click <b>OK</b>.</i></span></span></p><p style="margin:0in 0in 8pt; text-align:justify"><span style="font-size:12pt"><span style="line-height:107%"><span style="font-family:&quot;Times New Roman&quot;,serif"><img id="image126.jpg" style="width: 320px; height: 180px;" data-cke-saved-src="oecss/images/helpcenter/Catalogfile_1.png" src="oecss/images/helpcenter/Catalogfile_1.png" alt="catalogfile1"> </span></span></span></p><p style="margin:0in 0in 8pt; text-align:justify"><span style="font-size:12pt"><span style="line-height:107%"><span style="font-family:&quot;Times New Roman&quot;,serif">&nbsp; 2.&nbsp;</span></span></span><span style="font-size:12pt"><span style="line-height:107%"><span style="font-family:Calibri,sans-serif"><span style="font-family:&quot;Times New Roman&quot;,serif">The <img src="oecss/images/helpcenter/plus1.png" id="photo-id" style="width: 25px; height: 25px;" data-cke-saved-src="oecss/images/helpcenter/plus1.png">&nbsp; sign changes to <img src="oecss/images/helpcenter/tickmark.png" id="photo-id" style="width: 25px; height: 25px;" data-cke-saved-src="oecss/images/helpcenter/tickmark.png">sign to inform that the file is cataloged successfully.</span></span></span></span></p><p style="margin:0in 0in 8pt; text-align:justify"><br></p><p style="margin:0in 0in 8pt; text-align:justify"><span style="font-size:12pt"><span style="line-height:107%"><span style="font-family:&quot;Times New Roman&quot;,serif"><img id="image123.jpg" style="width: 700px; height: 200px;" data-cke-saved-src="oecss/images/helpcenter/catalogfile2.png" src="oecss/images/helpcenter/catalogfile2.png" alt="catalogfile2"> </span></span></span></p>', 'de.catfileup', true, '', '', 'admin', now());


insert into helpcenter_topic(topicid, description, content, context, active, type, external_link, createdby, createdate) values (31001, 'How can I create a new Project?', '<p>To create a new project, use the Add Project button in the project module. Enter a Project name and a good description that explains what the project is and click <strong>Save</strong>.</p>', 'pj.addproj', true, '', '', 'admin', now());


insert into helpcenter_topic(topicid, description, content, context, active, type, external_link, createdby, createdate) values (31002, 'Where can I view all my projects?', '<p>Users can view all their projects in the <strong>Projects</strong> module.</p><p><!--{cke_protected}%3Cmeta%20charset%3D%22utf-8%22%20%2F%3E--></p><p dir="ltr"><!--{cke_protected}%3Cmeta%20charset%3D%22utf-8%22%20%2F%3E--><!--{cke_protected}%3Cmeta%20charset%3D%22utf-8%22%20%2F%3E--><b id="docs-internal-guid-f5aac5c1-7fff-45f5-f6f3-a24093a42fe4"><img data-cke-saved-src="oecss/images/helpcenter/pointer_hand.png" src="oecss/images/helpcenter/pointer_hand.png" style="width: 30px; height: 30px;"></b><b>Users should have&nbsp;access to a project to view its contents.</b></p>', 'pj.locateproj', true, '', '', 'admin', now());


insert into helpcenter_topic(topicid, description, content, context, active, type, external_link, createdby, createdate) values (31003, 'Can a user edit the Project Name?', '<p><b id="docs-internal-guid-94dcb3db-7fff-a37d-eccf-25138dbf31cb">No. U</b>sers who have access to view the project can only edit the name and title of the project.</p><p><!--{cke_protected}%3Cmeta%20charset%3D%22utf-8%22%20%2F%3E--></p><p dir="ltr"><!--{cke_protected}%3Cmeta%20charset%3D%22utf-8%22%20%2F%3E--><b id="docs-internal-guid-c652fe83-7fff-a339-03cd-42cb3d7ab586"><img data-cke-saved-src="oecss/images/helpcenter/pointer_hand.png" src="oecss/images/helpcenter/pointer_hand.png" style="width: 30px; height: 30px;">&#8203;&#8203;&#8203;&#8203;&#8203;&#8203;&#8203;</b><b>Users must first be invited to view the project.</b></p>', 'pj.editproj', true, '', '', 'admin', now());
insert into helpcenter_relation(topicid, childid) values(31003, 31005);


insert into helpcenter_topic(topicid, description, content, context, active, type, external_link, createdby, createdate) values (31004, 'Who can delete a project?', '<p><b id="docs-internal-guid-2a57a47c-7fff-63f7-7214-b3885c3704f2">Only</b> the user who has created a project can delete his own project. Invited users do not have the right to delete other user''s projects.</p><p><!--{cke_protected}%3Cmeta%20charset%3D%22utf-8%22%20%2F%3E--></p><p dir="ltr"><!--{cke_protected}%3Cmeta%20charset%3D%22utf-8%22%20%2F%3E--><b id="docs-internal-guid-544fd4e8-7fff-f0e0-c1e0-2de699560195"><img data-cke-saved-src="oecss/images/helpcenter/warning icon.jpg" src="oecss/images/helpcenter/warning icon.jpg" style="width: 25px; height: 25px;"></b><b> Once a project gets removed, no members of the user group to the project can further access that project.</b></p>', 'pj.delproj', true, '', '', 'admin', now());


insert into helpcenter_topic(topicid, description, content, context, active, type, external_link, createdby, createdate) values (31005, 'How to invite people to view project?', '<p><!--{cke_protected}%3Cmeta%20charset%3D%22utf-8%22%20%2F%3E--><b id="docs-internal-guid-0390b719-7fff-14d0-cf48-a0e4f5faecdd">A </b>user can invite other members of their organization to collaborate&nbsp;on their project. To invite other users:</p><ol><li>Click and select a project name from the project module.</li><li>Click the &#8203;&#8203;&#8203;&#8203;&#8203;&#8203;&#8203; <img src="oecss/images/helpcenter/ninedots1.png" id="photo-id" style="width: 25px; height: 25px;" data-cke-saved-src="oecss/images/helpcenter/ninedots1.png">to unfold the&nbsp;invite user icon&nbsp;<img src="oecss/images/helpcenter/invite user.png" id="photo-id" style="width: 25px; height: 25px;" data-cke-saved-src="oecss/images/helpcenter/invite user.png">&nbsp;in the top right corner of the page.</li><li>Select the user name and click<strong> Invite</strong> to share the project with other members.</li></ol><p><b id="docs-internal-guid-c652fe83-7fff-a339-03cd-42cb3d7ab586"><img data-cke-saved-src="oecss/images/helpcenter/pointer_hand.png" src="oecss/images/helpcenter/pointer_hand.png" style="width: 30px; height: 30px;"></b><strong>Click the <img src="oecss/images/helpcenter/ninedots1.png" id="photo-id" style="width: 30px; height: 30px;" data-cke-saved-src="oecss/images/helpcenter/ninedots1.png">nine dots to unfold the invite user icon.</strong></p>', 'pj.inviteproj', true, '', '', 'admin', now());


insert into helpcenter_topic(topicid, description, content, context, active, type, external_link, createdby, createdate) values (31006, 'What is a default project? How does it work?', '<p><b id="docs-internal-guid-b998524f-7fff-fdcc-476f-bfc2d5720947">U</b>sers can organize frequently used data objects to which they have access under&nbsp;any project name listed in the project module. Before adding your data object, users must select a default project name to add their data objects. Users can also&nbsp;select and choose a project when they add a data object from the data catalog page.&nbsp;</p><p><!--{cke_protected}%3Cmeta%20charset%3D%22utf-8%22%20%2F%3E--></p><p dir="ltr"><!--{cke_protected}%3Cmeta%20charset%3D%22utf-8%22%20%2F%3E--><b id="docs-internal-guid-4f6b226f-7fff-a33e-c134-6e07426f0362"><img data-cke-saved-src="oecss/images/helpcenter/pointer_hand.png" src="oecss/images/helpcenter/pointer_hand.png" style="width: 30px; height: 30px;"></b><b>Users should always assign one project as default. Failing to select a project name as default does not allow users to add any data.</b></p>', 'pj.defaultproj', true, '', '', 'admin', now());


insert into helpcenter_topic(topicid, description, content, context, active, type, external_link, createdby, createdate) values (31007, 'How to add a database object to a default project?', '<p><!--{cke_protected}%3Cmeta%20charset%3D%22utf-8%22%20%2F%3E--><br></p><p dir="ltr"><span style="color: rgb(0, 0, 0);"><span style="font-size: 18px;"><span style="font-family: Roboto,sans-serif;">To &nbsp;add a database object to a default project, navigate to the <strong>Data&nbsp;catalog</strong> module. Go to &#39;Project Status&#39; column, select your data object and click the + icon displayed next to each data object name. &nbsp;<span style="font-style: normal; font-variant-ligatures: normal; font-variant-caps: normal; font-weight: 400; letter-spacing: normal; orphans: 2; text-align: left; text-indent: 0px; text-transform: none; white-space: pre-wrap; widows: 2; word-spacing: 0px; -webkit-text-stroke-width: 0px; background-color: rgb(255, 255, 255); text-decoration-thickness: initial; text-decoration-style: initial; text-decoration-color: initial; float: none; position: static; display: inline !important;">clicking on the &lsquo;+&rsquo; icon will add the data object to the default project.</span></span></span></span></p><p dir="ltr"><span style="color: rgb(0, 0, 0);"><span style="font-size: 18px;"><span style="font-family: Roboto,sans-serif;"><br style="position: static;"><br style="position: static;"></span></span></span></p><p><!--{cke_protected}%3Cmeta%20charset%3D%22utf-8%22%20%2F%3E--><span style="color: rgb(0, 0, 0);"><span style="font-size: 18px;"><span style="font-family: Roboto,sans-serif;"><b id="docs-internal-guid-a89a39d9-7fff-2400-39e1-68405eded8a9"><img data-cke-saved-src="oecss/images/helpcenter/pointer_hand.png" src="oecss/images/helpcenter/pointer_hand.png" style="width: 30px; height: 30px;" class="fr-fil fr-dib"></b><strong>&nbsp;A message notifies that the data object added to the default project and a checkmark appears on the icon&nbsp;</strong><b id="docs-internal-guid-a89a39d9-7fff-2400-39e1-68405eded8a9"><img data-cke-saved-src="oecss/images/helpcenter/file with check mark.png" src="oecss/images/helpcenter/file%20with%20check%20mark.png" style="width: 26px; height: 26px;" class="fr-fil fr-dib"></b></span></span></span><strong><span style="font-family: Roboto, sans-serif; font-size: 18px; color: rgb(0, 0, 0);">.</span></strong></p>', 'pj.adddbproj', true, '', '', 'admin', now());


insert into helpcenter_topic(topicid, description, content, context, active, type, external_link, createdby, createdate) values (31008, 'How to attach a note with a data object added to the project?', '<p><!--{cke_protected}%3Cmeta%20charset%3D%22utf-8%22%20%2F%3E--></p><p dir="ltr">Users can write a note&nbsp;about each database object. To attach a note:</p><ol dir="ltr"><li>Go to the Data catalog module.</li><li>Select and add a data object to a default project.</li><li>Click the<strong>&nbsp;<img src="oecss/images/helpcenter/project1.png" id="photo-id" style="width: 25px; height: 25px;" data-cke-saved-src="oecss/images/helpcenter/project1.png"></strong>&nbsp;icon next to the data object title to add a small note.</li><li>Click <strong>Save</strong> to attach a project note to the selected data object.<em>&nbsp;<b id="docs-internal-guid-5287273b-7fff-0115-f774-9a0e6a1ef749">A pop-window facilitates to add a note.</b></em></li></ol><p><br></p>', 'pj.noteproj', true, '', '', 'admin', now());


insert into helpcenter_topic(topicid, description, content, context, active, type, external_link, createdby, createdate) values (31009, 'How do a user get notified of an assigned project?', '<p>When users are added as a member of the&nbsp;project they get a notification message to their inbox. Project notifications are categorized as System Alerts. Users can navigate to the My Resources&gt;&gt;Inbox and select System Alerts to see the alert.</p>', 'pj.projnotification', true, '', '', 'admin', now());


insert into helpcenter_topic(topicid, description, content, context, active, type, external_link, createdby, createdate) values (32000, 'What is Governance Catalog Domain?', '<p><style type="text/css"><!--{cke_protected}{C}%3C!%2D%2Dtd%20%7Bborder%3A%201px%20solid%20%23ccc%3B%7Dbr%20%7Bmso-data-placement%3Asame-cell%3B%7D%2D%2D%3E--></style>Domains are high-level business segments of an organization where a user can apply for group settings and permissions. Each organization has a custom-defined domain. It is used to categorize and manage similar data assets across an organization.<br><br></p>', 'gc.governancedomain', true, '', '', 'admin', now());


insert into helpcenter_topic(topicid, description, content, context, active, type, external_link, createdby, createdate) values (32001, 'What is Data Asset Group (DAG)?', '<p><style type="text/css"><!--{cke_protected}{C}%3C!%2D%2Dtd%20%7Bborder%3A%201px%20solid%20%23ccc%3B%7Dbr%20%7Bmso-data-placement%3Asame-cell%3B%7D%2D%2D%3E--></style><strong>Data Asset Group</strong> allows the user to provide Ownership and Stewardship for Tables, Files&nbsp;and Reports. Data Asset Group is at <strong>SCHEMA LEVEL</strong>. When a data source&nbsp;is initially crawled by OvalEdge&nbsp;ADMIN, he becomes the default Owner and Steward for that Schema. Only the ADMIN has the right to change the Owner, Steward&nbsp;and author of the data asset group at the schema level. Once an Owner is added, he shares the responsibility with ADMIN to edit the secondary Owner and Steward of that DAG and manage the request access of that DAG.&nbsp;</p><p>However, to manage the tags and organize data assets, ADMINS needs to create a DAG TAG.&nbsp; ADMIN can ONLY create DAG&nbsp;TAG through the security module. While creating a DAG TAG, a new set of Owners, Stewards and Approvers can be assigned to organize the data assets added to this DAG TAG.<br></p><p><br><!--{cke_protected}%3Cmeta%20charset%3D%22utf-8%22%20%2F%3E--><!--{cke_protected}%3Cmeta%20charset%3D%22utf-8%22%20%2F%3E--><b id="docs-internal-guid-c20739aa-7fff-897b-0ed7-ebe4e0d35a92"><img data-cke-saved-src="oecss/images/helpcenter/pointer_hand.png" src="oecss/images/helpcenter/pointer_hand.png" style="width: 30px; height: 30px;"></b><b>&nbsp;</b><strong>You cannot add more than one DAG Tag to a Table/ File/ Report.</strong></p>', 'gc.dag', true, '', '', 'admin', now());


insert into helpcenter_topic(topicid, description, content, context, active, type, external_link, createdby, createdate) values (32002, 'How to create Glossary terms in OvalEdge?', '<p>The user roles who can view and create business glossary terms associated with a specific Domain are:</p><ul><li><strong>OvalEdge Admins</strong>-who are the super users of OvalEdge</li><li><strong>Owners</strong>- Users who are assigned as an Owner to a particular Domain</li><li><strong>Stewards</strong>- Users who are associated with a particular Domain</li><li>Any user with <strong>READ-WRITE(RW)</strong> permissions on a&nbsp;DOMAIN</li></ul><p>To add/suggest a single business Glossary term:</p><ul><li>Login to the OvalEdge application with your user credentials.</li><li>Navigate to the <strong>Business Glossary</strong> module.</li><li>Select the <strong>Suggest a&nbsp;Term</strong> button.</li><li>Select the <strong>Domain</strong>. <strong><em>The Domain name that users have access permissions will only be visible in this drop-down list. Users will be able to add Glossary terms only to these domains.</em></strong></li><li>Enter a term and description. <strong><em>STEWARDS assigned to that DOMAIN become the&nbsp;default stewards of that&nbsp;Glossary term added.</em></strong></li><li>Click on the <strong>Submit</strong> <strong></strong>button to save the term as a DRAFT. <strong><em>The User assigned as a STEWARD to this DOMAIN will be notified by default to REVIEW this term.&nbsp;</em></strong><em>Alternatively, the request can be approved by the users associated with the customized Workflow Approval.</em></li><li>Once the term is published, it is available to other users to associate with the&nbsp;data objects.</li></ul><p>Alternatively, <strong>multiple Glossary&nbsp;terms</strong> can be imported from <strong>external</strong> <strong>files</strong> in OvalEdge. Use the&nbsp;<strong>Advanced Tools--&gt;Load metadata from files</strong> module to load terms in bulk.</p>', 'gc.creategossaryterms', true, '', '', 'admin', now());
