insert into helpcenter_topic(topicid, description, content, context, active, type, external_link, createdby, createdate) values (32003, 'What is PII?', '<p><style type="text/css"><!--{cke_protected}{C}%3C!%2D%2Dtd%20%7Bborder%3A%201px%20solid%20%23ccc%3B%7Dbr%20%7Bmso-data-placement%3Asame-cell%3B%7D%2D%2D%3E--></style>PII - Personally Identifiable Information is information that identifies a distinct individual. Examples of PII''s include Social Security Numbers (SSN), Driving License Numbers, Email ID''s, and Revenue details.<br></p>', 'gc.pii', true, '', '', 'admin', now());
