insert into helpcenter_topic(topicid, description, content, context, active, type, external_link, createdby, createdate) values (32041, 'How to create a new certified policy?', '<p><span data-sheets-userformat=''{"2":15165,"3":{"1":0},"5":{"1":[{"1":2,"2":0,"5":[null,2,0]},{"1":0,"2":0,"3":3},{"1":1,"2":0,"4":1}]},"6":{"1":[{"1":2,"2":0,"5":[null,2,0]},{"1":0,"2":0,"3":3},{"1":1,"2":0,"4":1}]},"7":{"1":[{"1":2,"2":0,"5":[null,2,0]},{"1":0,"2":0,"3":3},{"1":1,"2":0,"4":1}]},"8":{"1":[{"1":2,"2":0,"5":[null,2,0]},{"1":0,"2":0,"3":3},{"1":1,"2":0,"4":1}]},"11":4,"12":0,"14":[null,2,16711680],"15":"Arial","16":10}'' data-sheets-value=''{"1":2,"2":"A user can add policy by the select the domain, provide a name to the policy, and select the policy type as &#8216;Certify&#8217;."}'' style="font-size: 18px; font-family: Roboto, sans-serif; font-weight: normal; font-style: normal; color: rgb(0, 0, 0); position: static;">A user can add policy by the selecting the domain, provide a name to the policy, and select the policy type as &lsquo;Certify&rsquo;.</span> </p>', 'cert.addpolicy', true, '', '', 'admin', now());


insert into helpcenter_topic(topicid, description, content, context, active, type, external_link, createdby, createdate) values (32042, 'How to raise a new service request in service desk module?', '<p><span style="font-family: Roboto, sans-serif; font-size: 14px; color: rgb(0, 0, 0);">To raise a new service request<br style="position: static;">Navigate to the Governance catalog &gt; &nbsp;Servicedesk module.</span></p><ul><li><span style="color: rgb(0, 0, 0);"><span style="font-size: 14px;"><span style="font-family: Roboto,sans-serif;">Click on <strong>New Service Request</strong> button, a popup of new connection displayed.&nbsp;</span></span></span></li><li><span style="color: rgb(0, 0, 0);"><span style="font-size: 14px;"><span style="font-family: Roboto,sans-serif;">From the drop-down list search or select the required ticket template.<br style="position: static;">Click on the Continue button, the selected ticket template page is displayed.&nbsp;</span></span></span></li><li><span style="color: rgb(0, 0, 0);"><span style="font-size: 14px;"><span style="font-family: Roboto,sans-serif;">Enter all the details as requested in template according to the type of request raised.&nbsp;</span></span></span></li><li><span style="color: rgb(0, 0, 0);"><span style="font-size: 14px;"><span style="font-family: Roboto,sans-serif;">Click on the <strong>Submit</strong> button. It will generate a new new service desk ticket (Request ID)</span></span></span></li></ul><p><br></p><p><span style="font-family: Roboto, sans-serif; font-size: 14px; color: rgb(0, 0, 0);"><span style="background-color: rgb(255, 255, 255); font-weight: 400; font-style: normal; font-variant: normal; text-decoration: none; vertical-align: baseline; white-space: pre-wrap; position: static;">Note: In the summary page of the service desk request , there is an external application JIRA ticket number link that will get mapped and navigate to the JIRA application. Click on JIRA ticket Id then page will be redirected to external ticket number summary page. If ticket creation fails in an external system, &nbsp;then the &ldquo;repost&rdquo; option will retry to push the ticket to JIRA.&nbsp;</span></span></p><p><br></p>', 'new.servicerequest', true, '', '', 'admin', now());


insert into helpcenter_topic(topicid, description, content, context, active, type, external_link, createdby, createdate) values (32043, 'How to add a new connection with an external application?', '<div style="position: static;"><span style="font-family: Roboto, sans-serif; font-size: 14px; color: rgb(0, 0, 0);"><strong>Navigate to Administration &gt; Manage Service Desk &gt; Service Desk Integration. </strong></span></div><ul><li><div style="position: static;"><span style="color: rgb(0, 0, 0);"><span style="font-size: 14px;"><span style="font-family: Roboto,sans-serif;">Click on <strong>Service Desk Integration&nbsp;</strong>button, it shows the JIRA/ServiceNow tab.&nbsp;<br></span></span></span></div></li><li><div style="position: static;"><span style="color: rgb(0, 0, 0);"><span style="font-size: 14px;"><span style="font-family: Roboto,sans-serif;">Click on ServiceNow button.</span></span></span></div></li><li><div style="position: static;"><span style="color: rgb(0, 0, 0);"><span style="font-size: 14px;"><span style="font-family: Roboto,sans-serif;"><span style="font-style: normal; font-variant-ligatures: normal; font-variant-caps: normal; font-weight: 400; letter-spacing: normal; orphans: 2; text-align: left; text-indent: 0px; text-transform: none; white-space: normal; widows: 2; word-spacing: 0px; -webkit-text-stroke-width: 0px; background-color: rgb(255, 255, 255); text-decoration-thickness: initial; text-decoration-style: initial; text-decoration-color: initial; float: none; position: static; display: inline !important;">To establish connection enter the following parameters,</span></span></span></span></div></li><li style="margin-left: 20px;"><div style="position: static;"><span style="color: rgb(0, 0, 0);"><span style="font-size: 14px;"><span style="font-family: Roboto,sans-serif;">Enter the Connection Name: <em>Name of a ServiceNow connection(User-defined).</em></span></span></span></div></li><li style="margin-left: 20px;"><div style="position: static;"><span style="color: rgb(0, 0, 0);"><span style="font-size: 14px;"><span style="font-family: Roboto,sans-serif;">ServiceNow Server: <em>&nbsp;Instance URL.</em></span></span></span></div></li><li style="margin-left: 20px;"><div style="position: static;"><span style="color: rgb(0, 0, 0);"><span style="font-size: 14px;"><span style="font-family: Roboto,sans-serif;">Username: <em>Authorised email id associated with ServiceNow account password.</em></span></span></span></div></li><li style="margin-left: 20px;"><div style="position: static;"><span style="color: rgb(0, 0, 0);"><span style="font-size: 14px;"><span style="font-family: Roboto,sans-serif;">ServiceNow Token: <em>ServiceNow account password.</em></span></span></span></div></li><li><div style="position: static;"><span style="color: rgb(0, 0, 0);"><span style="font-size: 14px;"><span style="font-family: Roboto,sans-serif;">Click on Validate, and then new connection is established.</span></span></span></div></li></ul><div style="position: static;"><span style="font-family: Roboto, sans-serif; font-size: 14px; color: rgb(0, 0, 0);">Similarly, you can follow same steps to perform JIRA integration</span></div><p><br style="position: static;"><br></p>', 'add.newconnection', true, '', '', 'admin', now());


insert into helpcenter_topic(topicid, description, content, context, active, type, external_link, createdby, createdate) values (32044, 'What is Mapper column?', '<p><span data-sheets-userformat=''{"2":14909,"3":{"1":0},"5":{"1":[{"1":2,"2":0,"5":{"1":2,"2":0}},{"1":0,"2":0,"3":3},{"1":1,"2":0,"4":1}]},"6":{"1":[{"1":2,"2":0,"5":{"1":2,"2":0}},{"1":0,"2":0,"3":3},{"1":1,"2":0,"4":1}]},"7":{"1":[{"1":2,"2":0,"5":{"1":2,"2":0}},{"1":0,"2":0,"3":3},{"1":1,"2":0,"4":1}]},"8":{"1":[{"1":2,"2":0,"5":{"1":2,"2":0}},{"1":0,"2":0,"3":3},{"1":1,"2":0,"4":1}]},"12":0,"14":{"1":2,"2":16711680},"15":"Arial","16":11}'' data-sheets-value=''{"1":2,"2":"Mapper column displays the icon of external tools (JIRA or ServiceNow). With this, it maps the tickets from the OvalEdge application to the JIRA or Service now tool.  The tickets which are mapped to external tools are displayed in the Service Desk page."}'' style="font-size: 14px; font-family: Roboto, sans-serif; font-weight: normal; font-style: normal; color: rgb(0, 0, 0); position: static;">Mapper column displays the icon of external tools (JIRA or ServiceNow). With this, it maps the tickets from the OvalEdge application to the JIRA or Service now tool. &nbsp;The tickets which are mapped to external tools are displayed in the Service Desk page.</span></p>', 'map.column', true, '', '', 'admin', now());


insert into helpcenter_topic(topicid, description, content, context, active, type, external_link, createdby, createdate) values (33000, 'How to create a new job workflow?', '<p><!--{cke_protected}%3Cmeta%20charset%3D%22utf-8%22%20%2F%3E-->&#8203;&#8203;&#8203;&#8203;&#8203;&#8203;&#8203;To create a new&nbsp;job workflow, use the <img src="oecss/images/helpcenter/addnewjobw.png" id="photo-id" style="width: 100px; height: 25px;" data-cke-saved-src="oecss/images/helpcenter/addnewjobw.png">New button in the Job workflow module. The user selects the job type, design a workflow template, Save and Execute.</p>', 'jwd.createjobwf', true, '', '', 'admin', now());


insert into helpcenter_topic(topicid, description, content, context, active, type, external_link, createdby, createdate) values (33001, 'Where can I view the status of my job ?', '<p><!--{cke_protected}%3Cmeta%20charset%3D%22utf-8%22%20%2F%3E-->&#8203;&#8203;&#8203;&#8203;&#8203;&#8203;&#8203;Users can view the status of all jobs submitted,in the Jobs module.</p>', 'jwd..status', true, '', '', 'admin', now());


insert into helpcenter_topic(topicid, description, content, context, active, type, external_link, createdby, createdate) values (33002, 'What is a Job Type?', '<p>Job Type displays a list of tasks or actions that are eligible for designing the workflow. The following tasks/actions can be scheduled in the job workflow, Crawler Connection, SQL, Crawler, Profile, Profile Unprofiled, Job-Workflow, File Table Exchange, Database Table Exchange, Advance Jobs, and Alert.</p><p><!--{cke_protected}%3Cmeta%20charset%3D%22utf-8%22%20%2F%3E--></p>', 'jwd.jobtype', true, '', '', 'admin', now());


insert into helpcenter_topic(topicid, description, content, context, active, type, external_link, createdby, createdate) values (33003, 'How is Job Workflow processed?', '<p><!--{cke_protected}%3Cmeta%20charset%3D%22utf-8%22%20%2F%3E--></p><p dir="ltr"><b id="docs-internal-guid-08756746-7fff-0b0e-ffe0-8bdb991d6143"></b>Users can process each workflow in two ways: serial or parallel.</p><ul dir="ltr"><li><strong>Serial workflow</strong>: Process one step to the next and does not step back</li><li><strong>Parallel workflow:</strong> Processes several steps simultaneously</li></ul>', 'jwd.processed', true, '', '', 'admin', now());


insert into helpcenter_topic(topicid, description, content, context, active, type, external_link, createdby, createdate) values (33004, 'What are the various status of job execution?', '<p>The execution workflow has the following status:</p><p><!--{cke_protected}%3Cmeta%20charset%3D%22utf-8%22%20%2F%3E--><br></p><ul><li><b id="docs-internal-guid-d8c2a689-7fff-bcfc-d06d-6feba3666ec0"><span style="font-family: Roboto, sans-serif; font-size: 14px; color: rgb(0, 0, 0);">Pending -&nbsp;</span></b><span style="color: rgb(0, 0, 0);"><span style="font-size: 14px;"><span style="font-family: Roboto,sans-serif;">the job is created, but not started execution. A job stays in pending mode until it&#39;s ready to be run by the system.&nbsp;</span></span></span></li><li><span style="color: rgb(0, 0, 0);"><span style="font-size: 14px;"><span style="font-family: Roboto,sans-serif;"><b id="docs-internal-guid-d8c2a689-7fff-bcfc-d06d-6feba3666ec0">Finished -&nbsp;</b>the job succeeded.</span></span></span></li><li><span style="color: rgb(0, 0, 0);"><span style="font-size: 14px;"><span style="font-family: Roboto,sans-serif;"><b id="docs-internal-guid-d8c2a689-7fff-bcfc-d06d-6feba3666ec0">Error-&nbsp;</b>the job failed.</span></span></span></li><li><span style="color: rgb(0, 0, 0);"><span style="font-size: 14px;"><span style="font-family: Roboto,sans-serif;"><b id="docs-internal-guid-d8c2a689-7fff-bcfc-d06d-6feba3666ec0">Initiated-</b> the job has started execution.</span></span></span></li><li><div style="position: static;"><span style="color: rgb(0, 0, 0);"><span style="font-size: 14px;"><span style="font-family: Roboto,sans-serif;"><strong>INIT</strong><strong>&nbsp;</strong>- The Job has been initiated.&nbsp;</span></span></span></div></li><li><div style="position: static;"><span style="color: rgb(0, 0, 0);"><span style="font-size: 14px;"><span style="font-family: Roboto,sans-serif;"><strong>RUNNING</strong> - Jobs are being processed in the submitted order.</span></span></span></div></li><li><div style="position: static;"><span style="color: rgb(0, 0, 0);"><span style="font-size: 14px;"><span style="font-family: Roboto,sans-serif;"><strong>Waiting</strong> - Submitted Job is on Queue to get executed.</span></span></span></div></li><li><div style="position: static;"><span style="color: rgb(0, 0, 0);"><span style="font-size: 14px;"><span style="font-family: Roboto,sans-serif;"><strong>SUCCESS</strong> - An invoked task is completed successfully.</span></span></span></div></li><li><div style="position: static;"><span style="color: rgb(0, 0, 0);"><span style="font-size: 14px;"><span style="font-family: Roboto,sans-serif;"><strong>ERROR&nbsp;</strong>- The job has failed. Check error message in the Logs.</span></span></span></div></li><li><div style="position: static;"><span style="color: rgb(0, 0, 0);"><span style="font-size: 14px;"><span style="font-family: Roboto,sans-serif;"><strong>Hold&nbsp;</strong>- The initiated job has been kept on hold.&nbsp;</span></span></span></div></li><li><div style="position: static;"><span style="color: rgb(0, 0, 0);"><span style="font-size: 14px;"><span style="font-family: Roboto,sans-serif;"><strong>KILLED</strong> - The initiated job has killed.</span></span></span></div></li><li><div style="position: static;"><span style="font-family: Roboto, sans-serif; font-size: 14px; color: rgb(0, 0, 0);"><strong>PARTIAL SUCCESS</strong> - The initiated job has completed&nbsp;</span><span style="background-color: transparent;"><font color="#000000" face="Roboto, sans-serif">partially.</font></span></div></li></ul>', 'jwd.statusjwf', true, '', '', 'admin', now());


insert into helpcenter_topic(topicid, description, content, context, active, type, external_link, createdby, createdate) values (33005, 'How can I schedule my Job?', '<p><style type="text/css"><!--{cke_protected}{C}%3C!%2D%2Dtd%20%7Bborder%3A%201px%20solid%20%23ccc%3B%7Dbr%20%7Bmso-data-placement%3Asame-cell%3B%7D%2D%2D%3E--></style></p><p><!--{cke_protected}%3Cmeta%20charset%3D%22utf-8%22%20%2F%3E--></p><p dir="ltr"><b id="docs-internal-guid-d21383c8-7fff-5825-be92-f8727dd5dd49">T</b>o schedule a workflow, select a workflow from the job workflow module. Choose the schedule job option from the Job menu. Alternatively, click the schedule icon <b id="docs-internal-guid-d099218a-7fff-eaeb-9154-5df5c278d819"><img data-cke-saved-src="oecss/images/helpcenter/Schedule icon.png" src="oecss/images/helpcenter/Schedule icon.png" style="width: 20px; height: 20px;"></b>. Choose a schedule with time and day to run the job.</p><p dir="ltr"><!--{cke_protected}%3Cmeta%20charset%3D%22utf-8%22%20%2F%3E--><b id="docs-internal-guid-d099218a-7fff-eaeb-9154-5df5c278d819"><img data-cke-saved-src="oecss/images/helpcenter/pointer_hand.png" src="oecss/images/helpcenter/pointer_hand.png" style="width: 20px; height: 20px;"></b><b> Users need to save the workflow design before scheduling.</b></p>', 'jwd.schjob', true, '', '', 'admin', now());


insert into helpcenter_topic(topicid, description, content, context, active, type, external_link, createdby, createdate) values (33006, 'How can I view the history of jobs executed on a workflow?', '<p>To view the history of a particular workflow executed, click the history icon <!--{cke_protected}%3Cmeta%20charset%3D%22utf-8%22%20%2F%3E--><b id="docs-internal-guid-49531450-7fff-2fcd-fb4c-74bcd2672d48"><img data-cke-saved-src="oecss/images/helpcenter/hourglass.png" src="oecss/images/helpcenter/hourglass.png" style="width: 25px; height: 25px;">&nbsp;</b>that will bring you to the jobs tab. Alternatively, you can choose the option from the Jobs menu bar.</p><p><!--{cke_protected}%3Cmeta%20charset%3D%22utf-8%22%20%2F%3E--></p><p><br></p>', 'jwd.historyofj', true, '', '', 'admin', now());


insert into helpcenter_topic(topicid, description, content, context, active, type, external_link, createdby, createdate) values (33007, 'How can I notify users about the status of the workflow?', '<p><span style="color: rgb(0, 0, 0);"><span style="font-size: 14px; font-family: Roboto, sans-serif;">Users are updated about the status of the workflow by sending notification messages.&nbsp;</span></span></p><p><span style="color: rgb(0, 0, 0);"><span style="font-size: 14px; font-family: Roboto, sans-serif;">As a first step, use the expert icon <b id="docs-internal-guid-88f95e42-7fff-9fe0-e005-bdfbc28283ce"><img data-cke-saved-src="oecss/images/helpcenter/invite user.png" src="oecss/images/helpcenter/invite%20user.png" style="width: 20px; height: 20px;" class="fr-fil fr-dib"></b>to add the users to a specific workflow by checking the box by their name then click on update experts.&nbsp;</span></span></p><p><span style="color: rgb(0, 0, 0);"><span style="font-size: 14px; font-family: Roboto, sans-serif;">Next, click the notification button <!--{cke_protected}%3Cmeta%20charset%3D%22utf-8%22%20%2F%3E--><b id="docs-internal-guid-88f95e42-7fff-9fe0-e005-bdfbc28283ce"><img data-cke-saved-src="oecss/images/helpcenter/sent-mail.png" src="oecss/images/helpcenter/sent-mail.png" style="width: 16px; height: 16px;" class="fr-fil fr-dib">&nbsp;</b>and make the selections on when to update(either &quot;send notification on error&quot; <span style="font-style: normal; font-variant-ligatures: normal; font-variant-caps: normal; font-weight: 400; letter-spacing: normal; orphans: 2; text-align: left; text-indent: 0px; text-transform: none; white-space: normal; widows: 2; word-spacing: 0px; -webkit-text-stroke-width: 0px; background-color: rgb(255, 255, 255); text-decoration-thickness: initial; text-decoration-style: initial; text-decoration-color: initial; float: none; position: static; display: inline !important;">and/or</span>&nbsp; &quot;send notification on success&quot; also &quot;<span style="font-style: normal; font-variant-ligatures: normal; font-variant-caps: normal; font-weight: 400; letter-spacing: normal; orphans: 2; text-align: start; text-indent: 0px; text-transform: none; white-space: normal; widows: 2; word-spacing: 0px; -webkit-text-stroke-width: 0px; background-color: rgb(255, 255, 255); text-decoration-thickness: initial; text-decoration-style: initial; text-decoration-color: initial; float: none; position: static; display: inline !important;">Send notification on partial success&quot;</span>.)</span></span></p>', 'jwd.notifications', true, '', '', 'admin', now());


insert into helpcenter_topic(topicid, description, content, context, active, type, external_link, createdby, createdate) values (33008, 'What is a cron job?', '<p><!--{cke_protected}%3Cmeta%20charset%3D%22utf-8%22%20%2F%3E-->A cron job is a scheduled task by itself. Cron jobs can be beneficial to automate workflow to run at a scheduled time.</p>', 'j.schedulecron', true, '', '', 'admin', now());


insert into helpcenter_topic(topicid, description, content, context, active, type, external_link, createdby, createdate) values (33009, 'How do I delete a cron job?', '<ul dir="ltr"><li>Navigate to the job workflow module.</li><li>Select a current workflow with schedule cron.&nbsp;</li><li>Click the <img src="oecss/images/helpcenter/ninedots1.png" id="photo-id" style="width: 25px; height: 25px;" data-cke-saved-src="oecss/images/helpcenter/ninedots1.png">&nbsp;and select&nbsp;<strong>delete </strong>to remove the workflow.</li></ul>', 'jwd.deletecron', true, '', '', 'admin', now());


insert into helpcenter_topic(topicid, description, content, context, active, type, external_link, createdby, createdate) values (33010, 'Can I reschedule the run time on a current job?', '<p>No.&nbsp;Currently, our application does not support to reschedule the runtime-created.</p><p><!--{cke_protected}%3Cmeta%20charset%3D%22utf-8%22%20%2F%3E--></p><p dir="ltr"><!--{cke_protected}%3Cmeta%20charset%3D%22utf-8%22%20%2F%3E--><b id="docs-internal-guid-6278283f-7fff-f2ec-dd36-e273a748067f"><img data-cke-saved-src="oecss/images/helpcenter/stop and note.png" src="oecss/images/helpcenter/stop and note.png" style="width: 25px; height: 25px;"></b><b>&nbsp;The user has to drop the current workflow and recreate it to schedule the time.</b></p>', 'jwd.reschedule', true, '', '', 'admin', now());


insert into helpcenter_topic(topicid, description, content, context, active, type, external_link, createdby, createdate) values (33011, 'How do I create a cron job?', '<p>To create a cron job:</p><p><!--{cke_protected}%3Cmeta%20charset%3D%22utf-8%22%20%2F%3E--></p><ul dir="ltr"><li>Navigate to the job workflow module.</li><li>Select <img src="oecss/images/helpcenter/addnewjobw.png" id="photo-id" style="width: 50px; height: 25px;" data-cke-saved-src="oecss/images/helpcenter/addnewjobw.png">&nbsp;to design and create a new workflow and save <img src="oecss/images/helpcenter/Save-icon.png" id="photo-id" style="width: 25px; height: 25px;" data-cke-saved-src="oecss/images/helpcenter/Save-icon.png">. Alternatively, select an existing workflow to schedule the time.</li><li>Use the schedule icon <img src="oecss/images/helpcenter/schedule1.png" id="photo-id" style="width: 25px; height: 25px;" data-cke-saved-src="oecss/images/helpcenter/schedule1.png">&nbsp;and set a time interval to execute the workflow.</li></ul><p dir="ltr"><!--{cke_protected}%3Cmeta%20charset%3D%22utf-8%22%20%2F%3E--><b id="docs-internal-guid-862f55ae-7fff-36ed-7903-1990f4b5e126"><img data-cke-saved-src="oecss/images/helpcenter/pointer_hand.png" src="oecss/images/helpcenter/pointer_hand.png" style="width: 30px; height: 30px;"></b><b>Your cron job executes the workflow at a particular time, date, and repetition, according to the settings you selected.</b></p>', 'jwd.createcron', true, '', '', 'admin', now());


insert into helpcenter_topic(topicid, description, content, context, active, type, external_link, createdby, createdate) values (33012, 'What are the various Job types in OvalEdge?', '<p><strong>Crawler Connection:&nbsp;</strong>Establish a connection to your data source, and set up a scheduler on the connections to crawl all the schemas within the connection without any restriction. <strong><br>SQL:&nbsp;</strong> <span style="color: rgb(0, 0, 0); font-family: Roboto, sans-serif; font-size: 14px; font-style: normal; font-variant-ligatures: normal; font-variant-caps: normal; font-weight: 400; letter-spacing: normal; orphans: 2; text-align: left; text-indent: 0px; text-transform: none; white-space: normal; widows: 2; word-spacing: 0px; -webkit-text-stroke-width: 0px; background-color: rgb(255, 255, 255); text-decoration-thickness: initial; text-decoration-style: initial; text-decoration-color: initial; float: none; position: static; display: inline !important;">Users can schedule all catalogued queries.</span><span style="color: rgb(0, 0, 0); font-family: Roboto, sans-serif; font-size: 14px;">&nbsp;</span> <strong><br>Crawler:&nbsp;</strong>It is the first action that happens when the OvalEdge data engine integrates and connects to a data source. <strong><br>Profile:&nbsp;</strong>Profiling is a process of connecting a data source to gather statistics and informative summaries about the data elements<strong><br>Profile unprofiled:&nbsp;</strong>the process of connecting a data source to gather the unprofiled portions and generate statistics and informative summaries about the data elements.<br><strong>Crawl Users, Roles &amp; Permissions:&nbsp;<br>Job workflow:&nbsp;</strong>Users can add existing Workflow templates as building blocks of another workflow. <strong><br>Advanced Jobs:&nbsp;</strong>Use this application to create and run an advanced job like building lineage, loading data dictionary, discover relationships.<strong><br>Alert:&nbsp;</strong>Data Alert is an automatic message sent in response to an event<strong><br>Data Quality Rule:</strong> Data quality rule is a set of processes written on a data object to regularly monitor, and verify the quality of data.<strong><br>Certification Policy:&nbsp;</strong>Set of business rules that creates a trust layer on your data object.<strong><br>Process Unprocessed Queries:&nbsp;</strong>Queries that have not been processed on a connection while building lineage.</p><p><!--{cke_protected}%3Cmeta%20charset%3D%22utf-8%22%20%2F%3E--><br></p>', 'jwd.workflowtype', true, '', '', 'admin', now());


insert into helpcenter_topic(topicid, description, content, context, active, type, external_link, createdby, createdate) values (33013, 'What is the purpose of adding or removing Experts?', '<p>An expert is in charge of ensuring the quality of both the actual business data and the corresponding metadata. Notifications on the status of jobs can be set up and sent to the expert(s) identified. Users can add or remove experts based on the requirement.</p>', 'jwd.experts', true, '', '', 'admin', now());


insert into helpcenter_topic(topicid, description, content, context, active, type, external_link, createdby, createdate) values (34000, 'What is a log?', '<p><style type="text/css"><!--{cke_protected}{C}%3C!%2D%2Dtd%20%7Bborder%3A%201px%20solid%20%23ccc%3B%7Dbr%20%7Bmso-data-placement%3Asame-cell%3B%7D%2D%2D%3E--></style>The log is a sequence of steps, recording all the updated activities(events and processes that are executed in the OvalEdge application) of a job.</p><p><!--{cke_protected}%3Cmeta%20charset%3D%22utf-8%22%20%2F%3E--><b id="docs-internal-guid-b1dfbe71-7fff-daf3-20ec-5f5b5627b563"><img data-cke-saved-src="oecss/images/helpcenter/pointer_hand.png" src="oecss/images/helpcenter/pointer_hand.png" style="width: 50px; height: 50px;"></b>Users see the progress of a job through the job logs.</p><p><br></p>', 'j.logs', true, '', '', 'admin', now());


insert into helpcenter_topic(topicid, description, content, context, active, type, external_link, createdby, createdate) values (34002, 'What is Job Ext Id?', '<p>This is a Job ID created when a job is submitted from an external platform like Hadoop, Google Query, etc. If a&nbsp;job is submitted within OvalEdge,&nbsp;then the Ext Job Id matches the Job Step ID.</p>', 'j.jobextid', true, '', '', 'admin', now());


insert into helpcenter_topic(topicid, description, content, context, active, type, external_link, createdby, createdate) values (34003, 'What is Job Step Id?', '<p><!--{cke_protected}%3Cmeta%20charset%3D%22utf-8%22%20%2F%3E-->If the Job contains multiple-step jobs, Step ID is used to distinguish the step jobs.<br></p>', 'j.jobstepid', true, '', '', 'admin', now());


insert into helpcenter_topic(topicid, description, content, context, active, type, external_link, createdby, createdate) values (35000, 'What is UPstream?', '<p>&#8203;&#8203;&#8203;&#8203;&#8203;&#8203;&#8203;<span style="font-size:12pt"><span style="line-height:107%"><span style="font-family:&quot;Times New Roman&quot;,serif"><span style="background:white">The upstream data path </span><img src="oecss/images/helpcenter/upstream1.jpg" id="photo-id" style="width: 25px; height: 25px;" data-cke-saved-src="oecss/images/helpcenter/upstream1.jpg"><span style="background:white">displays the INPUT data assets associated with the selected data object. In other words, it displays the source lineage of the data object selected.</span></span></span></span></p><p><span style="font-size:12pt"><span style="line-height:107%"><span style="font-family:Calibri,sans-serif"><span style="font-family:&quot;Times New Roman&quot;,serif">&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; <img id="Picture_x0020_457" alt="Close icon" style="width: 300px; height: 300px;" data-cke-saved-src="oecss/images/helpcenter/upon.png" src="oecss/images/helpcenter/upon.png"> </span></span></span></span></p>', 'at.upstream', true, '', '', 'admin', now());


insert into helpcenter_topic(topicid, description, content, context, active, type, external_link, createdby, createdate) values (35001, 'What is Downstream?', '<p><style type="text/css"><!--{cke_protected}{C}%3C!%2D%2Dtd%20%7Bborder%3A%201px%20solid%20%23ccc%3B%7Dbr%20%7Bmso-data-placement%3Asame-cell%3B%7D%2D%2D%3E--></style><span style="font-size:12pt"><span style="line-height:107%"><span style="font-family:Calibri,sans-serif"><span style="background:white"><span style="font-family:&quot;Times New Roman&quot;,serif">The downstream data path <img src="oecss/images/helpcenter/downstream1.jpg" id="photo-id" style="width: 25px; height: 25px;" data-cke-saved-src="oecss/images/helpcenter/downstream1.jpg"></span></span><span style="background:white"><span style="font-family:&quot;Times New Roman&quot;,serif">displays the OUTPUT assets associated with the selected data object. In other words, it displays the destination lineage of the data object selected.</span></span></span></span></span></p><p><br></p><p style="margin:0in 0in 0.0001pt 0.5in; text-align:justify"><br></p><p style="margin:0in 0in 8pt 0.5in; text-align:justify"><span style="font-size:12pt"><span style="line-height:107%"><span style="font-family:Calibri,sans-serif"><span style="font-family:&quot;Times New Roman&quot;,serif">&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; <img id="Picture_x0020_458" alt="on lineage" style="width: 300px; height: 300px;" data-cke-saved-src="oecss/images/helpcenter/on.png" src="oecss/images/helpcenter/on.png"> </span></span></span></span></p>', 'at.downstream', true, '', '', 'admin', now());


insert into helpcenter_topic(topicid, description, content, context, active, type, external_link, createdby, createdate) values (35002, 'What is Select columns?', '<p><style type="text/css"><!--{cke_protected}{C}%3C!%2D%2Dtd%20%7Bborder%3A%201px%20solid%20%23ccc%3B%7Dbr%20%7Bmso-data-placement%3Asame-cell%3B%7D%2D%2D%3E--></style>Columns are&nbsp;going to display according to Upstream and Downstream&nbsp;data objects selection.</p>', 'at.selectcolumndef', true, '', '', 'admin', now());


insert into helpcenter_topic(topicid, description, content, context, active, type, external_link, createdby, createdate) values (35003, 'How we get Select columns?', '<p><style type="text/css"><!--{cke_protected}{C}%3C!%2D%2Dtd%20%7Bborder%3A%201px%20solid%20%23ccc%3B%7Dbr%20%7Bmso-data-placement%3Asame-cell%3B%7D%2D%2D%3E--></style>We get all the selet columns by adding the edit column info in the lineage for tables/report/files</p>', 'at.getselectcol', true, '', '', 'admin', now());


insert into helpcenter_topic(topicid, description, content, context, active, type, external_link, createdby, createdate) values (35004, 'How we compare the data by using compare profile result?', '<div><br></div><ul><li>Click the Advanced tool option in the object browser and select Compare profile results.</li><li>Select the database, Schema, table, and choose a date for the primary table.</li><li>Select the database, schema, table, and choose a date for the secondary table to be compared.</li><li>Click the compare dataset.&nbsp;<em>The column names are compared individually and the changes in the column attributes are highlighted.</em></li></ul><p>&nbsp; &nbsp; &nbsp;</p><p style="margin:0in 0in 8pt; text-align:justify"><img src="oecss/images/helpcenter/compareprofile1.png" id="photo-id" style="width: 900px; height: 110px;" data-cke-saved-src="oecss/images/helpcenter/compareprofile1.png"></p><p style="margin:0in 0in 8pt; text-align:justify"><span style="font-size:12pt"><span style="line-height:107%"><span style="font-family:&quot;Times New Roman&quot;,serif">&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; </span></span></span></p><p style="margin:0in 0in 8pt; text-align:justify"><span style="font-size:12pt"><span style="line-height:107%"><span style="font-family:&quot;Times New Roman&quot;,serif">&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; &nbsp;&nbsp;<img src="oecss/images/helpcenter/compareprofile2.png" id="photo-id" style="width: 900px; height: 250px;" data-cke-saved-src="oecss/images/helpcenter/compareprofile2.png"></span></span></span></p><p><!--{cke_protected}%3Cmeta%20charset%3D%22utf-8%22%20%2F%3E--><b id="docs-internal-guid-d17b7e2e-7fff-50a7-7139-4cc5848e3427"><img data-cke-saved-src="oecss/images/helpcenter/pointer_hand.png" src="oecss/images/helpcenter/pointer_hand.png" style="width: 25px; height: 25px;"></b>OvalEdge captures any metadata changes within a 24-hr period, or greater.</p>', 'at.compareprofresu', true, '', '', 'admin', now());


insert into helpcenter_topic(topicid, description, content, context, active, type, external_link, createdby, createdate) values (35005, 'What is analyzed date?', '<p><style type="text/css"><!--{cke_protected}{C}%3C!%2D%2Dtd%20%7Bborder%3A%201px%20solid%20%23ccc%3B%7Dbr%20%7Bmso-data-placement%3Asame-cell%3B%7D%2D%2D%3E--></style>It displays the latest date and time that a specific data object is profiled to compute statistics of new data. If this attribute is empty, you can identify that the object has not been profiled.</p><p></p><p><!--{cke_protected}%3Cmeta%20charset%3D%22utf-8%22%20%2F%3E--></p><p dir="ltr"><!--{cke_protected}%3Cmeta%20charset%3D%22utf-8%22%20%2F%3E--><b id="docs-internal-guid-4e3610f4-7fff-f68e-e385-56bbbc580c78"><img data-cke-saved-src="oecss/images/helpcenter/light_bulb1.png" src="oecss/images/helpcenter/light_bulb1.png" style="width: 30px; height: 30px;"></b><strong>&nbsp;Do you know,&nbsp;you can compare the changes between the latest and previous profile dates of a table? Navigate to Data and Metadata Changes tab in Advanced Tools to see any changes.</strong></p>', 'tb.analyzed', true, '', '', 'admin', now());


insert into helpcenter_topic(topicid, description, content, context, active, type, external_link, createdby, createdate) values (35006, 'What is row Count in compare profile results?', '<p><style type="text/css"><!--{cke_protected}{C}%3C!%2D%2Dtd%20%7Bborder%3A%201px%20solid%20%23ccc%3B%7Dbr%20%7Bmso-data-placement%3Asame-cell%3B%7D%2D%2D%3E--></style>Row count is nothing but Total number of records in selected Table <br></p>', 'at.rowcount', true, '', '', 'admin', now());


insert into helpcenter_topic(topicid, description, content, context, active, type, external_link, createdby, createdate) values (35007, 'How to compare the two schemas?', '<ol><li>Click the Advanced tool option in the object browser and select Compare profile results.</li><li>Select the Database--&gt;Schema--&gt;Table and choose the date of the primary table.</li><li>Select the Database--&gt;Schema--&gt;Table and choose a date for the secondary table to be compared.</li><li>Click the compare dataset. The column names are compared individually and the changes in the column attributes are highlighted.</li></ol>', 'at.howcomparedb', true, '', '', 'admin', now());


insert into helpcenter_topic(topicid, description, content, context, active, type, external_link, createdby, createdate) values (35008, 'What is count in compare schema?', '<p><style type="text/css"><!--{cke_protected}{C}%3C!%2D%2Dtd%20%7Bborder%3A%201px%20solid%20%23ccc%3B%7Dbr%20%7Bmso-data-placement%3Asame-cell%3B%7D%2D%2D%3E--></style>Count is nothing but column count in table<br></p>', 'at.countcomparedb', true, '', '', 'admin', now());


insert into helpcenter_topic(topicid, description, content, context, active, type, external_link, createdby, createdate) values (35009, 'What is SQL type?', '<p><style type="text/css"><!--{cke_protected}{C}%3C!%2D%2Dtd%20%7Bborder%3A%201px%20solid%20%23ccc%3B%7Dbr%20%7Bmso-data-placement%3Asame-cell%3B%7D%2D%2D%3E--></style>To select the queries depending on the type of SQL statements, OvalEdge provides 3 different categories of SQL statement selection namely:<br>&nbsp;1.Select <br>&nbsp;2.DDL And <br>&nbsp;3. DML</p>', 'at.sqltype', true, '', '', 'admin', now());


insert into helpcenter_topic(topicid, description, content, context, active, type, external_link, createdby, createdate) values (35010, 'Who is defined as a Query user?', '<p><style type="text/css"><!--{cke_protected}{C}%3C!%2D%2Dtd%20%7Bborder%3A%201px%20solid%20%23ccc%3B%7Dbr%20%7Bmso-data-placement%3Asame-cell%3B%7D%2D%2D%3E--></style>A Query user is one who created a query. We can sort the queries by using users functionlaity.<br></p>', 'at.queryuser', true, '', '', 'admin', now());


insert into helpcenter_topic(topicid, description, content, context, active, type, external_link, createdby, createdate) values (35016, 'How to create an Alert?', '<p><style type="text/css"><!--{cke_protected}{C}%3C!%2D%2Dtd%20%7Bborder%3A%201px%20solid%20%23ccc%3B%7Dbr%20%7Bmso-data-placement%3Asame-cell%3B%7D%2D%2D%3E--></style></p><p><span style="font-size:12pt"><span style="line-height:107%"><span style="font-family:&quot;Times New Roman&quot;,serif">To create a data alert,</span></span></span></p><ul><li><span style="font-size:12pt"><span style="line-height:107%"><span style="font-family:&quot;Times New Roman&quot;,serif">Click the Advanced Tools option in the object browser and select the Alerts application.</span></span></span></li><li><span style="font-size:12pt"><span style="line-height:107%"><span style="font-family:&quot;Times New Roman&quot;,serif">Click the&nbsp;<img src="oecss/images/helpcenter/alert1.png" id="photo-id" style="width: 50px; height: 25px;" data-cke-saved-src="oecss/images/helpcenter/alert1.png"> &nbsp;icon on the top of the screen.</span></span></span></li><li><span style="font-size:12pt"><span style="line-height:107%"><span style="font-family:&quot;Times New Roman&quot;,serif">Enter an Alert name to identify the alert.</span></span></span></li><li><span style="font-size:12pt"><span style="line-height:107%"><span style="font-family:&quot;Times New Roman&quot;,serif">Enter the Dataset name which is the Query name that has an alert code.</span></span></span></li><li><span style="font-size:12pt"><span style="line-height:107%"><span style="font-family:&quot;Times New Roman&quot;,serif">Enter other attributes like the Subject of this alert and a help text of what this alert is.</span></span></span></li><li><span style="font-size:12pt"><span style="line-height:107%"><span style="font-family:&quot;Times New Roman&quot;,serif">Enter the Message of what action is to be taken when this alert is generated.</span></span></span></li></ul>', 'at.createalert', true, '', '', 'admin', now());


insert into helpcenter_topic(topicid, description, content, context, active, type, external_link, createdby, createdate) values (35017, 'How to assign a user to an alert?', '<p><style type="text/css"><!--{cke_protected}{C}%3C!%2D%2Dtd%20%7Bborder%3A%201px%20solid%20%23ccc%3B%7Dbr%20%7Bmso-data-placement%3Asame-cell%3B%7D%2D%2D%3E--></style></p><p>To invite a user to an alert,</p><ul><li>Click the Advanced Tools option in the object browser and select the Alerts application.</li><li>Choose an alert and click&nbsp;<img src="oecss/images/helpcenter/ninedots1.png" id="photo-id" style="width: 25px; height: 25px;" data-cke-saved-src="oecss/images/helpcenter/ninedots1.png">&nbsp;and select&nbsp;&nbsp;<img src="oecss/images/helpcenter/assign-user1.png" id="photo-id" style="width: 100px; height: 25px;" data-cke-saved-src="oecss/images/helpcenter/assign-user1.png">.</li><li>Select the users from the list to be notified.</li><li>Click update users to assign the selected users to this alert.</li></ul>', 'at.assignalert', true, '', '', 'admin', now());


insert into helpcenter_topic(topicid, description, content, context, active, type, external_link, createdby, createdate) values (35018, 'What is the use of assigning a user to an alert?', '<p>Data alerts help assigned users to be notified when any abnormal changes take place to the selected data object on which alert is defined.</p>', 'at.purposealert', true, '', '', 'admin', now());


insert into helpcenter_topic(topicid, description, content, context, active, type, external_link, createdby, createdate) values (35019, 'How to edit an alert?', '<p>To edit an alert,</p><ul><li>Click the Advanced Tools option in the object browser and select the Alerts application.</li><li>Select an Alert and click ''Edit'' button.</li><li>Edit the attributes and click validate the changes.</li><li>Select Save to store the changes.</li></ul>', 'at.editalert', true, '', '', 'admin', now());


insert into helpcenter_topic(topicid, description, content, context, active, type, external_link, createdby, createdate) values (35020, 'How to delete an alert?', '<p>To delete an alert,</p><ol><li>Click the Advanced Tools option in the object browser and select the Alerts application.</li><li>Select an Alert and click the&nbsp;<img src="oecss/images/helpcenter/ninedots1.png" id="photo-id" style="width: 25px; height: 25px;" data-cke-saved-src="oecss/images/helpcenter/ninedots1.png">&nbsp; and choose ''Delete'' option.</li></ol>', 'at.deletealert', true, '', '', 'admin', now());


insert into helpcenter_topic(topicid, description, content, context, active, type, external_link, createdby, createdate) values (35021, 'How to schedule an alert?', '<p class="MsoNoSpacing" style="margin:0in 0in 0.0001pt">To schedule an alert,</p><p class="MsoNoSpacing" style="margin:0in 0in 0.0001pt"><span style="font-size:12pt"><font face="Times New Roman, serif">1. Navigate the </font><strong style=""><font face="Times New Roman, serif">Administration --&gt;</font><font face="Times New Roman, serif">Job workflow</font></strong><font face="Times New Roman, serif"> feature.</font></span></p><p class="MsoNoSpacing" style="margin:0in 0in 0.0001pt"><span style="font-size:12pt"><span style="font-family:&quot;Times New Roman&quot;,serif">2. Select&nbsp;<img src="oecss/images/helpcenter/addnewjobw.png" id="photo-id" style="width: 100px; height: 25px;" data-cke-saved-src="oecss/images/helpcenter/addnewjobw.png"><b>&nbsp;</b>and choose the type as Alert.</span></span></p><p class="MsoNoSpacing" style="margin:0in 0in 0.0001pt"><span style="font-size:12pt"><span style="font-family:&quot;Times New Roman&quot;,serif"><i>&nbsp;&nbsp; All the alert names will be displayed.</i></span></span></p><p class="MsoNoSpacing" style="margin:0in 0in 0.0001pt"><span style="font-size:12pt"><span style="font-family:&quot;Times New Roman&quot;,serif">3. Drag and drop the specific alert on to the workflow designer.</span></span></p><p class="MsoNoSpacing" style="margin:0in 0in 0.0001pt"><span style="font-size:12pt"><span style="font-family:&quot;Times New Roman&quot;,serif">4. Give a name for the workflow and save. Click the <img src="oecss/images/helpcenter/schedule1.png" id="photo-id" style="width: 25px; height: 25px;" data-cke-saved-src="oecss/images/helpcenter/schedule1.png"> Run button to execute the alert right&nbsp;</span></span><span style="font-family: &quot;Times New Roman&quot;, serif; font-size: 12pt;">away.</span></p><p class="MsoNoSpacing" style="margin:0in 0in 0.0001pt"><span style="font-size:12pt"><span style="font-family:&quot;Times New Roman&quot;,serif"><i>&nbsp;&nbsp; A notification is sent to the assigned user on the successful completion of the job.</i></span></span></p><p class="MsoNoSpacing" style="margin:0in 0in 0.0001pt"><span style="font-size:12pt"><span style="font-family:&quot;Times New Roman&quot;,serif"><i>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; </i><i><img id="Picture_x0020_488" alt="schedule alert" style="width: 700px; height: 335px;" data-cke-saved-src="oecss/images/helpcenter/schealert.png" src="oecss/images/helpcenter/schealert.png">&#8203;&#8203;&#8203;&#8203;&#8203;&#8203;&#8203; </i></span></span></p><p class="MsoNoSpacing" style="margin:0in 0in 0.0001pt"><br></p><p class="MsoNoSpacing" style="margin:0in 0in 0.0001pt"><strong><span style="font-size:12pt"><span style="line-height:107%"><span style="font-family:&quot;Times New Roman&quot;,serif"><img src="oecss/images/helpcenter/pointer_hand.png" id="photo-id" style="width: 25px; height: 25px;" data-cke-saved-src="oecss/images/helpcenter/pointer_hand.png">Only administrators can schedule an alert to be run periodically</span></span></span></strong></p>', 'at.schedulealert', true, '', '', 'admin', now());


insert into helpcenter_topic(topicid, description, content, context, active, type, external_link, createdby, createdate) values (35022, 'What are source data objects?', '<p>In OvalEdge, the data object on which the impact analysis is performed is called the Source Data Object. OvalEdge Impact Analysis can be performed on six different data object types:</p><ul><li>Tables</li><li>Files</li><li>Reports</li><li>Table columns</li><li>File columns</li><li>Report columns</li></ul>', 'at.affectedobjects', true, '', '', 'admin', now());


insert into helpcenter_topic(topicid, description, content, context, active, type, external_link, createdby, createdate) values (35023, 'What are impacted objects?', '<p>In OvalEdge, the data objects that has been impacted by the affected object is called impacted object. The impact can be either on the upstream or downstream level. The impacted objects can be either tables, table columns, file, file column, reports, and report columns.<br></p>', 'at.impactedobjects', true, '', '', 'admin', now());


insert into helpcenter_topic(topicid, description, content, context, active, type, external_link, createdby, createdate) values (35024, 'What does responsible code represent?', '<p>The query/code that is used to build the lineage between a Table A and Table B when we do impact analysis is called responsible code.</p>', 'at.responsiblecode', true, '', '', 'admin', now());


insert into helpcenter_topic(topicid, description, content, context, active, type, external_link, createdby, createdate) values (35025, 'What data transformation types are captured during impact analysis?', '<p>The transformation type gives the type of transformations that are happening between the source and the destination object. Example: The code can be a "Reportfield" or a "Formula Field" or just a "Table link".</p>', 'at.transformationtype', true, '', '', 'admin', now());


insert into helpcenter_topic(topicid, description, content, context, active, type, external_link, createdby, createdate) values (35026, 'What does it mean if your impact analysis is reviewed?', '<p>Reviewing an impact analysis means to validate the impacted objects and the corresponding attributes such as the transformation codes and the responsible codes populating during the process. There are two review status of the impacted objects.</p><p>"REVIEWED"- the impacted objects are validated by a user</p><p>"PENDING"- the impacted objects are not yet validated.</p>', 'at.reviewedstatus', true, '', '', 'admin', now());


insert into helpcenter_topic(topicid, description, content, context, active, type, external_link, createdby, createdate) values (35027, 'What is source Lineage?', '<p>Source Lineage for a selected data object(Table/File/Report), is the data flowing from other data sources to this specific object.</p>', 'at.sourcelineage', true, '', '', 'admin', now());


insert into helpcenter_topic(topicid, description, content, context, active, type, external_link, createdby, createdate) values (35028, 'What is destination Lineage?', '<p>Destination Lineage for a selected data object(Table/File/Report), is the data flowing from this&nbsp;selected data object to other objects.<br></p>', 'at.destlineage', true, '', '', 'admin', now());


insert into helpcenter_topic(topicid, description, content, context, active, type, external_link, createdby, createdate) values (35034, 'What is Class in Advance Job?', '<p><style type="text/css"><!--{cke_protected}{C}%3C!%2D%2Dtd%20%7Bborder%3A%201px%20solid%20%23ccc%3B%7Dbr%20%7Bmso-data-placement%3Asame-cell%3B%7D%2D%2D%3E--></style>The Class is the entity which will take the attributes passed by the user and then start running the process behind the scenes.Basically each Advance Job will have a Class affiliated to it.</p>', 'at.classadvjob', true, '', '', 'admin', now());


insert into helpcenter_topic(topicid, description, content, context, active, type, external_link, createdby, createdate) values (35035, 'What are Attributes in Advance Job ?', '<p>Attribute fields in the advanced jobs requires user to input the data&nbsp;to efficiently run the job. The attributes column in advanced job normally takes values related to connection id from the crawler page, file path,and schema ids. Refer the Advanced Job section and navigate to each job to get the details of the attibutes needed.<br></p>', 'at.attributes', true, '', '', 'admin', now());


insert into helpcenter_topic(topicid, description, content, context, active, type, external_link, createdby, createdate) values (35036, 'What is Type column in Advance Job ?', '<p><style type="text/css"><!--{cke_protected}{C}%3C!%2D%2Dtd%20%7Bborder%3A%201px%20solid%20%23ccc%3B%7Dbr%20%7Bmso-data-placement%3Asame-cell%3B%7D%2D%2D%3E--></style>Type column is a&nbsp;short name that describes each&nbsp;&nbsp;Advance job used.</p>', 'at.advancejobtype', true, '', '', 'admin', now());


insert into helpcenter_topic(topicid, description, content, context, active, type, external_link, createdby, createdate) values (35037, 'How Ovaledge builds Lineage on a table/file/query?', '<p>In OvalEdge, there are various ways to build lineage on a table/file/query.</p><p><strong>Manually</strong>- Lineage can be built by selecting a specific data object and editing the lineage manually.</p><p><strong>Automatically</strong>- Lineage can be automatically built using advanced jobs. Depending on where the lineage information lies, there are custom advanced jobs that can be executed to build lineage.</p><p><em><img src="oecss/images/helpcenter/pointer_hand.png" id="photo-id" style="width: 50px; height: 50px;" data-cke-saved-src="oecss/images/helpcenter/pointer_hand.png">Refer the User manual on advanced jobs section to know more about how to build lineage from SQL queries/Server logs.</em></p>', 'at.calculatelineagetfq', true, '', '', 'admin', now());


insert into helpcenter_topic(topicid, description, content, context, active, type, external_link, createdby, createdate) values (35038, 'How Ovaledge builds Lineage on reports?', '<p>In OvalEdge, there are various ways to build lineage on reports</p><p><strong>Manually</strong>- Lineage can be built by selecting a specific report and editing the lineage manually.</p><p><strong>Automatically</strong>- Lineage can be automatically built using advanced jobs. Depending on where the lineage information lies, there are custom advanced jobs that can be executed to build lineage.</p><p><em><img src="oecss/images/helpcenter/pointer_hand.png" id="photo-id" style="width: 50px; height: 50px;" data-cke-saved-src="oecss/images/helpcenter/pointer_hand.png" class="fr-fil fr-dib">Refer the User manual on advanced jobs section to know more about how to build lineage from BI tools like Tableau, Power BI and SSIS packages.</em></p>', 'at.caluclatelininfo', true, '', '', 'admin', now());


insert into helpcenter_topic(topicid, description, content, context, active, type, external_link, createdby, createdate) values (35039, 'How to edit/add a source lineage?', '<p style="margin:0in 0in 0.0001pt 0.5in; text-align:justify;"><span style="font-size:12pt;"><span style="line-height:107%;"><span style="font-family: Calibri, sans-serif; color: rgb(0, 0, 0);"><span style="background: white; font-size: 14px; font-family: Roboto, sans-serif;">1. Click the <strong>Advanced tool</strong> option in the object browser and select <strong>Lineage Maintenance.</strong></span></span></span></span></p><p style="margin:0in 0in 0.0001pt 0.5in; text-align:justify;"><span style="color: rgb(0, 0, 0);"><span style="font-family: Roboto,sans-serif;"><span style="font-size: 14px;"><span style="line-height:107%;"><span style="background:white;">2. Select a &nbsp;<strong>Table/ File/ Report&nbsp;</strong> option from the radio button<strong>.&nbsp;</strong></span></span></span></span></span></p><p style="margin:0in 0in 0.0001pt 0.5in; text-align:justify;"><span style="color: rgb(0, 0, 0);"><span style="font-family: Roboto,sans-serif;"><span style="font-size: 14px;"><span style="line-height:107%;"><span style="background:white;">3. Enter the <strong>Table/File/Report&nbsp;</strong>name depending on the selection made.&nbsp;</span><em><span style="background:white;">A drop-down menu displays the matching table/ file/chart name. Choose the</span>&nbsp;</em><em><span style="background:white;">appropriate name for further action.</span></em></span></span></span></span></p><p style="margin:0in 0in 0.0001pt 0.5in; text-align:justify;"><span style="color: rgb(0, 0, 0);"><span style="font-family: Roboto,sans-serif;"><span style="font-size: 14px;"><span style="line-height:107%;">4. To add a new source lineage to the <strong>table</strong> selected, click the <strong>+</strong><strong>Associate Source object</strong> in the Source Lineage tab.</span></span></span></span></p><p style="margin:0in 0in 8pt 0.5in; text-align:justify;"><span style="color: rgb(0, 0, 0);"><span style="font-family: Roboto,sans-serif;"><span style="font-size: 14px;"><span style="line-height:107%;">5. Choose a source <strong>Object type</strong> (Table/ File/Report), <strong>Object name,</strong> and a <strong>Reference name</strong> to display on the lineage. (to which the selected table name should be mapped).&nbsp;</span>The <strong>object ID</strong> is automatically created upon Object name selection. The <strong>Ref ID, Reference&nbsp;</strong><strong>type and transformation notes&nbsp;</strong>are optional.</span></span></span></p><p style="margin:0in 0in 8pt; text-align:justify;"><span style="font-size: 14px;"><span style="line-height: 107%; font-family: Roboto, sans-serif; color: rgb(0, 0, 0);"><strong>Example</strong>: The following figure displays a sample source lineage modification to a selected table ADM2014. &nbsp; </span></span></p><p style="margin:0in 0in 8pt; text-align:justify;"><span style="font-size:12pt;"><span style="line-height:107%;"><span style=''font-family:"Times New Roman",serif;''><span style="border:solid windowtext 1.0pt; padding:0in;"><img id="Picture_x0020_789" alt="source lineage3" style="width: 800px; height: 350px;" data-cke-saved-src="oecss/images/helpcenter/sourcelineage6.jpg" src="oecss/images/helpcenter/sourcelineage6.jpg" class="fr-fil fr-dib"> <img id="Picture_x0020_814" alt="source lineage 4" style="width: 720px; height: 236px;" data-cke-saved-src="oecss/images/helpcenter/sourcelineage5.jpg" src="oecss/images/helpcenter/sourcelineage5.jpg" class="fr-fil fr-dib"></span></span></span></span></p>', 'at.editlineageman', true, '', '', 'admin', now());


insert into helpcenter_topic(topicid, description, content, context, active, type, external_link, createdby, createdate) values (35040, 'How to edit/ add destination lineage?', '<p style="margin:0in 0in 0.0001pt 0.5in; text-align:justify;"><span style="font-size:12pt;"><span style="line-height:107%;"><span style="font-family:Calibri,sans-serif;"><span style="background:white;"><span style=''font-family:"Times New Roman",serif;''>1. Click the <strong>Advanced tool</strong> option in the object browser and select <strong>Lineage Maintenance.</strong></span></span></span></span></span></p><p style="margin:0in 0in 0.0001pt 0.5in; text-align:justify;"><span style="font-size:12pt;"><span style="line-height:107%;"><span style="font-family:Calibri,sans-serif;"><span style="background:white;"><span style=''font-family:"Times New Roman",serif;''>2. Select a &nbsp;<strong>Table/ File/ Report&nbsp;</strong>option from the radio button<strong>.&nbsp;</strong></span></span></span></span></span></p><p style="margin:0in 0in 0.0001pt 0.5in; text-align:justify;"><span style="font-size:12pt;"><span style="line-height:107%;"><span style="font-family:Calibri,sans-serif;"><span style="background:white;"><span style=''font-family:"Times New Roman",serif;''>3. Enter the <strong>Table/File/Report</strong> name depending on the selection made</span></span></span></span></span></p><p style="margin-left:.25in; margin:0in 0in 8pt 0.5in; text-align:justify;"><span style="font-size:12pt;"><span style="line-height:107%;"><span style="font-family:Calibri,sans-serif;"><em><span style="background:white;"><span style=''font-family:"Times New Roman",serif;''>&nbsp; &nbsp; A drop-down menu displays the matching table/ file/chart name. Choose the appropriate name for further action.</span></span></em></span></span></span></p><p style="margin:0in 0in 8pt; text-align:justify;"><span style="font-size:12pt;"><span style="line-height:107%;"><span style=''font-family:"Times New Roman",serif;''>&nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp;&nbsp;<img id="Picture_x0020_542" alt="destination lineage1" style="width: 500px; height: 300px;" data-cke-saved-src="oecss/images/helpcenter/deslin1.jpg" src="oecss/images/helpcenter/deslin1.jpg" class="fr-fil fr-dib"></span></span></span>&nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; <span style="font-size:12pt;"><span style="line-height:107%;"><span style=''font-family:"Times New Roman",serif;''>&nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp;&nbsp;</span></span></span></p><p style="margin:0in 0in 0.0001pt 0.5in; text-align:justify;"><span style="font-size:12pt;"><span style="line-height:107%;"><span style="font-family:Calibri,sans-serif;"><span style=''font-family:"Times New Roman",serif;''>4. To add a new destination lineage to the <strong>table</strong> selected, click the <strong>+ Associate Destination Object</strong> in the Destination Lineage tab.</span></span></span></span></p><p style="margin:0in 0in 8pt 0.5in; text-align:justify;"><span style="font-size:12pt;"><span style="line-height:107%;"><span style="font-family:Calibri,sans-serif;"><span style=''font-family:"Times New Roman",serif;''>5. Choose a source <strong>Object type</strong> (Table/ File/Chart), <strong>Object name,</strong> and&nbsp;<strong>Reference name</strong> to display on the lineage. (to which the selected table name should be mapped).&nbsp;</span></span></span></span><span style="font-size:12pt;"><span style="line-height:107%;"><span style=''font-family:"Times New Roman",serif;''>The <strong>object ID</strong> is automatically created upon Object name selection. The <strong>Ref ID, Reference type, and transformation notes</strong> are optional. &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp;&nbsp;</span></span></span></p><p style="margin:0in 0in 8pt; text-align:justify;"><span style="font-size:12pt;"><span style="line-height:107%;"><span style=''font-family:"Times New Roman",serif;''><strong>&nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; Example</strong>: The following figure displays a sample destination lineage modification to a selected table <strong>Contacts</strong></span></span></span></p><p style="margin:0in 0in 8pt; text-align:justify;">&nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; <span style="font-size:12pt;"><span style="line-height:107%;"><span style=''font-family:"Times New Roman",serif;''><span style="border:solid windowtext 1.0pt; padding:0in;"><img id="Picture_x0020_543" alt="destination lineage2" style="width: 300px; height: 150px;" data-cke-saved-src="oecss/images/helpcenter/deslin5.jpg" src="oecss/images/helpcenter/deslin5.jpg" class="fr-fil fr-dib"></span><em>&nbsp; &nbsp; &nbsp;<span style="border:solid windowtext 1.0pt; padding:0in;"><img id="Picture_x0020_560" alt="destination lineage3" style="width: 400px; height: 250px;" data-cke-saved-src="oecss/images/helpcenter/deslin6.jpg" src="oecss/images/helpcenter/deslin6.jpg" class="fr-fil fr-dib"></span></em></span></span></span></p>', 'at.editdestlineage', true, '', '', 'admin', now());


insert into helpcenter_topic(topicid, description, content, context, active, type, external_link, createdby, createdate) values (35041, 'What are the custom jobs available to build Lineage?', '<p>Users can automatically build lineage using the custom advanced jobs. With a single click user can execute these jobs to build lineage for:</p><ul><li>Tableau Reports</li><li>Hive codes</li><li>SQL Server SQL&rsquo;s</li><li>SSIS packages</li><li>Oracle connection</li><li>SAP business objects</li></ul><p><br style="position: static;"><img data-fr-image-pasted="true" src="https://helpcenter.ovaledge.net/oecss/images/helpcenter/pointer_hand.png" data-cke-saved-src="oecss/images/helpcenter/pointer_hand.png" style="box-sizing: border-box; border-width: 0px; border-style: solid; border-color: currentcolor; max-width: 100%; height: 37px; cursor: pointer; padding: 0px 1px; text-align: left; color: rgb(65, 65, 65); font-family: sans-serif; font-size: 14px; font-style: normal; font-variant-ligatures: normal; font-variant-caps: normal; font-weight: 400; letter-spacing: normal; orphans: 2; text-indent: 0px; text-transform: none; white-space: normal; widows: 2; word-spacing: 0px; -webkit-text-stroke-width: 0px; background-color: rgb(255, 255, 255); text-decoration-thickness: initial; text-decoration-style: initial; text-decoration-color: initial; width: 37px;" class="fr-fil fr-dib">For step by step instruction on how to automatically build the lineage, <span style="color: rgb(32, 33, 36); font-family: Roboto, sans-serif; font-size: 14px; font-style: normal; font-variant-ligatures: normal; font-variant-caps: normal; font-weight: 400; letter-spacing: normal; orphans: 2; text-align: start; text-indent: 0px; text-transform: none; white-space: pre-wrap; widows: 2; word-spacing: 0px; -webkit-text-stroke-width: 0px; background-color: rgba(32, 33, 36, 0.04); text-decoration-thickness: initial; text-decoration-style: initial; text-decoration-color: initial; float: none; display: inline !important; position: static;">see the User Manual -&gt; &nbsp;Advanced Tools-&gt; Build Auto Lineage module.</span></p>', 'at.customlineagejob', true, '', '', 'admin', now());


insert into helpcenter_topic(topicid, description, content, context, active, type, external_link, createdby, createdate) values (35042, 'What are the prerequisites to correct the lineage?', '<p>Before manually correcting the failed queries to build lineage, query files from the selected connection should be loaded into OvalEdge. These query files can be loaded and processed to build and understand the relationship and data movement between the source and destination.Use the Load and catalog queries from files to catalog all the queries files.<br></p>', 'at.prerequisiteslineage', true, '', '', 'admin', now());


insert into helpcenter_topic(topicid, description, content, context, active, type, external_link, createdby, createdate) values (35043, 'When and where can users see all the failed queries?', '<p>To see all the failed queries, users have to first import all the query logs to a file and save it in the data lake. Connect to the data lake and catalog all the queries. Run the corresponding advanced job to build automatic lineage depending upon where the queries are originating.&nbsp;<br>If the codes are parsed properly, it generates the lineage between the data connections.<br>During this process some queries that fail to parse correctly will be defined as incorrect queries and are available in the Adjust Lineage module. Users can select the failed queries to manually correct the code and rebuild the lineage.<br></p>', 'at.failedqueries', true, '', '', 'admin', now());


insert into helpcenter_topic(topicid, description, content, context, active, type, external_link, createdby, createdate) values (35044, 'What is Impact title?', '<p>A user friendly title given while creating the impact analysis.</p>', 'at.impacttitle', true, '', '', 'admin', now());


insert into helpcenter_topic(topicid, description, content, context, active, type, external_link, createdby, createdate) values (35045, 'What is downstream with association?', '<p>The downstream association data path of an affected data object (the object on which the impact analysis is conducted) lists all the OUTPUT data elements to the selected data object and of the object/objects that are associated with the selected object. In other words, it lists the destination lineage of the data object selected and its associated object.</p>', 'at.downstreamwithreference', true, '', '', 'admin', now());


insert into helpcenter_topic(topicid, description, content, context, active, type, external_link, createdby, createdate) values (35046, 'What is Upstream with association?', '<p>The upstream with association data path &nbsp;of an affected data object (the object on which the impact analysis is conducted) lists all the INPUT data elements to the selected data object and for the object/objects that are associated with the selected object. In other words, it lists the source lineage of the data object selected and its associated object.<br></p>', 'at.upstreamwithreference', true, '', '', 'admin', now());


insert into helpcenter_topic(topicid, description, content, context, active, type, external_link, createdby, createdate) values (35047, 'What is impact level?', '<p>Impact level is the downstream level upto which the impact analysis is performed. Example: Assume an&nbsp;object added as an affected object,&nbsp; has 5 levels of lineage in its downstream. If the&nbsp; impact level is 2, the impact analysis&nbsp; will identify all the impacted objects&nbsp;upto 2nd level of lineage.</p>', 'at.impactlevel', true, '', '', 'admin', now());


insert into helpcenter_topic(topicid, description, content, context, active, type, external_link, createdby, createdate) values (35048, 'What are the list of tables in this column?', '<p>OvalEdge provides a Schema compare tool to find the differences between two database objects in two schemas. The differences in the compared schema, such as new table names are listed.<br></p>', 'at.tablenamedetail', true, '', '', 'admin', now());


insert into helpcenter_topic(topicid, description, content, context, active, type, external_link, createdby, createdate) values (35049, 'What is Change Count?', '<p>Change count gives the number of changes in the column when two Schemas are compared.</p>', 'at.comparisoncount', true, '', '', 'admin', now());


insert into helpcenter_topic(topicid, description, content, context, active, type, external_link, createdby, createdate) values (35051, 'What is impact type?', '<p>The impact type in the impact analysis can either be a "Transformation" or "Reference"</p>', 'at.impacttype', true, '', '', 'admin', now());


insert into helpcenter_topic(topicid, description, content, context, active, type, external_link, createdby, createdate) values (35052, 'What is job type in Impact Analysis?', '<p>Job type is the type of transformation that has happened for each impacted object from their source to the target object.It depends on the data source connection.Example: For SSIS packages it is "SSIS_Dataflow", for "Qliksense" it is "QVD_SQL". Other examples are "Crystal reports", "BODS_SQL" etc.<br></p>', 'at.jobtypeia', true, '', '', 'admin', now());


insert into helpcenter_topic(topicid, description, content, context, active, type, external_link, createdby, createdate) values (35053, 'What are transformation codes?', '<p><style type="text/css"><!--{cke_protected}{C}%3C!%2D%2Dtd%20%7Bborder%3A%201px%20solid%20%23ccc%3B%7Dbr%20%7Bmso-data-placement%3Asame-cell%3B%7D%2D%2D%3E--></style>Transformation codes are the codes that are based on the column formation.<br></p>', 'iad.trcode', true, '', '', 'admin', now());


insert into helpcenter_topic(topicid, description, content, context, active, type, external_link, createdby, createdate) values (35054, 'What is a Object type?', '<p>The type of object that can be added to as a source or destination&nbsp;lineage such as Table, File and Report.</p>', 'lin.objecttype', true, '', '', 'admin', now());


insert into helpcenter_topic(topicid, description, content, context, active, type, external_link, createdby, createdate) values (35055, 'What is Object_ID?', '<p>Each Table/File/Report in OvalEdge is cataloged and has a unique ID called Object_ID.</p>', 'lin.objectid', true, '', '', 'admin', now());


insert into helpcenter_topic(topicid, description, content, context, active, type, external_link, createdby, createdate) values (35056, 'What is Code type?', '<p>Code types are generally the transformation Queries between the source object and target object.</p>', 'codetype', true, '', '', 'admin', now());


insert into helpcenter_topic(topicid, description, content, context, active, type, external_link, createdby, createdate) values (35057, 'What is Code ID?', '<p>Code ID is a unique id of the query that transforms the source to target object.</p>', 'codeid', true, '', '', 'admin', now());


insert into helpcenter_topic(topicid, description, content, context, active, type, external_link, createdby, createdate) values (35058, 'What is left count?', '<p>Users can compare schemas in two different ways:<br>1. Compare two different schemas on the same date.<br>2.Compare the same schema between two different dates.<br>The left count gives the number of columns that exist in a table from the left schema.<br><strong>Note:&nbsp;</strong>If the columns do not exist on the given date, it displays 0.</p>', 'at.leftcount', true, '', '', 'admin', now());


insert into helpcenter_topic(topicid, description, content, context, active, type, external_link, createdby, createdate) values (35059, 'What is right count?', '<p>Users can compare schemas in two different ways:<br>1. Compare two different schemas on the same date.<br>2.Compare the same schema between two different dates.<br>The right count gives the number of columns that exist in a table from the right schema.<br><strong>Note:&nbsp;</strong>If the columns do not exist on the given date, it displays 0.&nbsp;</p>', 'at.rightcount', true, '', '', 'admin', now());


insert into helpcenter_topic(topicid, description, content, context, active, type, external_link, createdby, createdate) values (35060, 'What is Impact level value?', '<p><span data-sheets-userformat=''{"2":7101,"3":{"1":0},"5":{"1":[{"1":2,"2":0,"5":[null,2,0]},{"1":0,"2":0,"3":3},{"1":1,"2":0,"4":1}]},"6":{"1":[{"1":2,"2":0,"5":[null,2,0]},{"1":0,"2":0,"3":3},{"1":1,"2":0,"4":1}]},"7":{"1":[{"1":2,"2":0,"5":[null,2,0]},{"1":0,"2":0,"3":3},{"1":1,"2":0,"4":1}]},"8":{"1":[{"1":2,"2":0,"5":[null,2,0]},{"1":0,"2":0,"3":3},{"1":1,"2":0,"4":1}]},"10":2,"11":4,"12":0,"14":{"1":2,"2":16711680},"15":"Arial"}'' data-sheets-value=''{"1":2,"2":"This count determines how many levels the\r\nsystem has to discover the impacted objects."}'' style="font-size: 10pt; font-family: Arial; font-weight: normal; font-style: normal; color: rgb(0, 0, 0); position: static;">Impact level value is a count that determines how many levels the system has to discover the impacted objects.</span> </p>', 'at.discoverimpobjects', true, '', '', 'admin', now());


insert into helpcenter_topic(topicid, description, content, context, active, type, external_link, createdby, createdate) values (35061, 'What is Lineage Lookup Schema?', '<p dir="ltr" style="line-height: 1.38; margin-top: 0pt; margin-bottom: 0pt; position: static;"><span style="font-size: 14px; font-family: Roboto, sans-serif; color: rgb(0, 0, 0); background-color: transparent; font-weight: 400; font-style: normal; font-variant: normal; text-decoration: none; vertical-align: baseline; white-space: pre-wrap; position: static;"><strong>Lineage lookup schemas is used to to build the lineage</strong></span></p><ul><li dir="ltr" style="line-height: 1.38; margin-top: 0pt; margin-bottom: 0pt; position: static;"><span style="color: rgb(0, 0, 0);"><span style="font-family: Roboto,sans-serif;"><span style="font-size: 14px;"><span style="background-color: transparent; font-weight: 400; font-style: normal; font-variant: normal; text-decoration: none; vertical-align: baseline; white-space: pre-wrap; position: static;">Navigate to Advanced tools &gt; Build Auto Lineage</span></span></span></span></li></ul><p style="margin-left: 20px;"><span style="color: rgb(0, 0, 0);"><span style="font-family: Roboto,sans-serif;"><span style="font-size: 14px;"><span style="background-color: transparent; font-weight: 400; font-style: normal; font-variant: normal; text-decoration: none; vertical-align: baseline; white-space: pre-wrap; position: static;"><em>A pop-up window of Lineage Lookup Schemas is displayed</em>.</span></span></span></span></p><ul><li dir="ltr" style="line-height: 1.38; margin-top: 0pt; margin-bottom: 0pt; position: static;"><span style="color: rgb(0, 0, 0);"><span style="font-family: Roboto,sans-serif;"><span style="font-size: 14px;"><span style="background-color: transparent; font-weight: 400; font-style: normal; font-variant: normal; text-decoration: none; vertical-align: baseline; white-space: pre-wrap; position: static;">From the drop-down list, select the connection name.&nbsp;</span></span></span></span></li></ul><p style="margin-left: 20px;"><span style="color: rgb(0, 0, 0);"><span style="font-family: Roboto,sans-serif;"><span style="font-size: 14px;"><span style="background-color: transparent; font-weight: 400; font-style: italic; font-variant: normal; text-decoration: none; vertical-align: baseline; white-space: pre-wrap; position: static;">The connection name is the associated name of the database connection from the crawler page&nbsp;</span></span></span></span></p><p style="margin-left: 20px;"><span style="color: rgb(0, 0, 0);"><span style="font-family: Roboto,sans-serif;"><span style="font-size: 14px;"><span style="background-color: transparent; font-weight: 400; font-style: italic; font-variant: normal; text-decoration: none; vertical-align: baseline; white-space: pre-wrap; position: static;">for which queries are written and imported from either a file or server logs.</span></span></span></span></p><ul><li dir="ltr" style="line-height: 1.38; margin-top: 0pt; margin-bottom: 0pt; position: static;"><span style="color: rgb(0, 0, 0);"><span style="font-family: Roboto,sans-serif;"><span style="font-size: 14px;"><span style="background-color: transparent; font-weight: 400; font-style: normal; font-variant: normal; text-decoration: none; vertical-align: baseline; white-space: pre-wrap; position: static;">Server name will be displayed by default.&nbsp;</span></span></span></span></li><li dir="ltr" style="line-height: 1.38; margin-top: 0pt; margin-bottom: 0pt; position: static;"><span style="font-family: Roboto, sans-serif; color: rgb(0, 0, 0);"><span style="font-size: 14px;"><span style="background-color: transparent; font-weight: 400; font-style: normal; font-variant: normal; text-decoration: none; vertical-align: baseline; white-space: pre-wrap; position: static;">Click on the&nbsp;</span><span style="background-color: transparent; font-weight: 700; font-style: normal; font-variant: normal; text-decoration: none; vertical-align: baseline; white-space: pre-wrap; position: static;">OK&nbsp;</span><span style="background-color: transparent; font-weight: 400; font-style: normal; font-variant: normal; text-decoration: none; vertical-align: baseline; white-space: pre-wrap; position: static;">button.</span></span></span></li></ul><p><br style="position: static;"></p><p><br></p>', 'at.lineagelookup', true, '', '', 'admin', now());


insert into helpcenter_topic(topicid, description, content, context, active, type, external_link, createdby, createdate) values (36000, 'What is Profiling and what is its purpose?', '<p><style type="text/css"><!--{cke_protected}{C}%3C!%2D%2Dtd%20%7Bborder%3A%201px%20solid%20%23ccc%3B%7Dbr%20%7Bmso-data-placement%3Asame-cell%3B%7D%2D%2D%3E--></style>Profiling is a process of connecting a data source to gather statistics and informative summaries about data elements. The statistics help in accessing the quality of the data sources before analyzing. Profiling a data source also helps identify relationships and discover patterns between tables at the entity level.<br><!--{cke_protected}%3Cmeta%20charset%3D%22utf-8%22%20%2F%3E--><!--{cke_protected}%3Cmeta%20charset%3D%22utf-8%22%20%2F%3E--><!--{cke_protected}%3Cmeta%20charset%3D%22utf-8%22%20%2F%3E--><b id="docs-internal-guid-ca47fb29-7fff-9f87-de80-654e3d8260e4"><img data-cke-saved-src="oecss/images/helpcenter/pointer_hand.png" src="oecss/images/helpcenter/pointer_hand.png" style="width: 29px; height: 29px;"></b><b>&nbsp;OvalEdge profiling is supported for various databases, however, profiling can not be performed on the reporting &amp; visualization software. </b></p>', 'ad.profiling', true, '', '', 'admin', now());


insert into helpcenter_topic(topicid, description, content, context, active, type, external_link, createdby, createdate) values (36001, 'What does the OvalEdge crawler do?', '<p><style type="text/css"><!--{cke_protected}{C}%3C!%2D%2Dtd%20%7Bborder%3A%201px%20solid%20%23ccc%3B%7Dbr%20%7Bmso-data-placement%3Asame-cell%3B%7D%2D%2D%3E--></style>When an OvalEdge crawler visits a data source, it collects all the data elements (i.e., metadata) and stores it in the OvalEdge data repository. The crawler creates an index for every stored data element, which is later used to explore data. OvalEdge crawlers are scheduled to scan the database regularly, so they always have an up-to-date index of the data element.<br><br></p>', 'ad.crawler', true, '', '', 'admin', now());


insert into helpcenter_topic(topicid, description, content, context, active, type, external_link, createdby, createdate) values (36002, 'What kind of system credentials does OvalEdge users needs to crawl the databases?', '<p>OvalEdge requires users to configure a separate connection for each type of database. The user(s) will need to enter source credentials and database information for each type of connection. Once a data connection is made, click the &#8220;Crawl&#8221; button which begins the crawling process.</p>', 'ad.crawlcredential', true, '', '', 'admin', now());


insert into helpcenter_topic(topicid, description, content, context, active, type, external_link, createdby, createdate) values (36003, 'What kind of user/user_role credentials do OvalEdge user need to crawl the data?', '<p>Users with ONLY ADMIN credentials can crawl the database initially. Additionally, users who are configured a special custom CRAWLER role can&nbsp;crawl the datasources.</p>', 'ad.crawlpermissions', true, '', '', 'admin', now());


insert into helpcenter_topic(topicid, description, content, context, active, type, external_link, createdby, createdate) values (36004, 'Is there a way to set limitations on row count while crawling?', '<p>Yes. There is a way to set a limitation on the number of rows displayed for preview while crawling.<br>Go to the &nbsp;Administration&gt;&gt;Crawlier.&nbsp;Select a database and click <img src="oecss/images/helpcenter/ninedots1.png" id="photo-id" style="width: 25px; height: 25px;" data-cke-saved-src="oecss/images/helpcenter/ninedots1.png">&nbsp;and choose Settings. Users can now edit the row count limit on the Profiler Setting.</p><p><b id="docs-internal-guid-ca47fb29-7fff-9f87-de80-654e3d8260e4"><img data-cke-saved-src="oecss/images/helpcenter/pointer_hand.png" src="oecss/images/helpcenter/pointer_hand.png" style="width: 29px; height: 29px;"></b><strong>Only users who have permission to crawl and re-crawl the database can edit this.&nbsp;</strong></p>', 'ad.crawlersetting', true, '', '', 'admin', now());


insert into helpcenter_topic(topicid, description, content, context, active, type, external_link, createdby, createdate) values (36005, 'How to configure a new database connection?', '<ol><li><span style="font-size: 14px;"><span style="font-family: Roboto, sans-serif; color: rgb(0, 0, 0);">Select the <strong>Administration&gt;&gt;Crawler</strong> module.</span></span></li><li><span style="font-family: Roboto,sans-serif;"><span style="font-size: 14px;"><span style="color: rgb(0, 0, 0);">Click <span style="background-color: transparent; font-weight: 400; font-style: normal; font-variant: normal; text-decoration: none; vertical-align: baseline; white-space: pre-wrap; position: static;">&nbsp;the +New Connection from the crawler window .The dialog box for Manage connections is displayed</span>.</span></span></span></li><li><span style="font-family: Roboto,sans-serif;"><span style="font-size: 14px;"><span style="color: rgb(0, 0, 0);"><span style="background-color: transparent; font-weight: 400; font-style: normal; font-variant: normal; text-decoration: none; vertical-align: baseline; white-space: pre-wrap; position: static;">Select the Database connection type in the below shown Database connections&nbsp;</span>.</span></span></span></li><li><span style="font-family: Roboto,sans-serif;"><span style="font-size: 14px;"><span style="color: rgb(0, 0, 0);">Enter the connection credentials.</span></span></span></li><li><span style="font-family: Roboto,sans-serif;"><span style="font-size: 14px;"><span style="color: rgb(0, 0, 0);">Click the validate button to test the database connection.</span></span></span></li><li><span style="font-size: 14px;"><span style="font-family: Roboto,sans-serif;"><span style="color: rgb(0, 0, 0);"><span style="background-color: transparent; font-weight: 400; font-style: normal; font-variant: normal; text-decoration: none; vertical-align: baseline; white-space: pre-wrap; position: static;">Click the Save button or <em>Save &amp; Configure&nbsp;</em>to finish the configuration of the database connection. A dialog box that the database added successfully is displayed</span></span></span><span style="font-family: Roboto, sans-serif; color: rgb(0, 0, 0);">.</span></span></li></ol>', 'ad.crawlerconf', true, '', '', 'admin', now());


insert into helpcenter_topic(topicid, description, content, context, active, type, external_link, createdby, createdate) values (36006, 'How to edit a database connection?', '<p style="margin:0in;"><span style="background: white;"><font color="#323232" face="Times New Roman"><span style="font-size: 12pt;">Users can edit the database connection details through the </span><span style="font-size: 16px;">Administration crawler</span><span style="font-size: 12pt;">&nbsp;tab.</span></font></span></p><ol><li style="margin: 0in;"><span style="font-size:12.0pt;"><span style=''font-family:"Times New Roman";''><span style="color:#323232;"><span style="background:white;">On the crawler window, select a database from the list and then click&nbsp;<img src="oecss/images/helpcenter/ninedots1.png" id="photo-id" style="width: 25px; height: 25px;" data-cke-saved-src="oecss/images/helpcenter/ninedots1.png" class="fr-fil fr-dib">&nbsp;and choose <strong>Edit Connection</strong>.</span></span></span></span></li><li style="margin: 0in;"><span style="font-size:12.0pt;"><span style=''font-family:"Times New Roman";''><span style="color:#323232;"><span style="background:white;">On the <strong>Edit Database Information</strong> page, make the changes to the connection details.</span></span></span></span></li><li style="margin: 0in;"><span style="font-size:12.0pt;"><span style=''font-family:"Times New Roman";''>Click the <strong>Va</strong></span><span style=''font-family: "Times New Roman"; color: rgb(0, 0, 0);''><strong>lidate </strong></span><span style=''font-family:"Times New Roman";''>button to test the modified database connection.</span></span></li><li style="margin: 0in;"><span style="font-size:12.0pt;"><span style=''font-family:"Times New Roman";''>Click the&nbsp;</span><span style=''font-family: "Times New Roman"; color: rgb(0, 0, 0);''><strong>Save&nbsp;</strong></span><span style=''font-family:"Times New Roman";''>button to finish the configuration of the database connection.</span></span></li></ol>', 'ad.editcrawler', true, '', '', 'admin', now());


insert into helpcenter_topic(topicid, description, content, context, active, type, external_link, createdby, createdate) values (36007, 'How to delete a database connection?', '<p style="margin:0in"><span style="font-size:12.0pt"><span style="font-family:&quot;Times New Roman&quot;"><span style="color:#323232"><span style="background:white">You can delete a database connection from the Administration_Crawler tab. It removes all the tables, columns, relationships, and all the objects in OvalEdge associated with the selected database. Additionally, the database connection index is cleared from the crawler window. Remove a database connection by doing the following:</span></span></span></span></p><ul><li style="margin: 0in;"><span style="font-size:12.0pt"><span style="font-family:&quot;Times New Roman&quot;"><span style="color:#323232">&nbsp;</span></span></span><span style="font-size:12.0pt"><span style="font-family:&quot;Times New Roman&quot;"><span style="color:#323232"><span style="background:white">On the crawler window, select a database from the list and then click <img src="oecss/images/helpcenter/ninedots1.png" id="photo-id" style="width: 25px; height: 25px;" data-cke-saved-src="oecss/images/helpcenter/ninedots1.png">&nbsp;and choose&nbsp;<span style="font-weight:bold">Delete Database.</span></span></span></span></span>&nbsp;<span style="font-size:12.0pt"><span style="font-family:&quot;Times New Roman&quot;"><span style="background:white"><span style="font-style:italic"><span style="color:black">A dialog box to delete the database is displayed</span></span></span></span></span></li><li style="margin: 0in;"><span style="font-size:12.0pt"><span style="font-family:&quot;Times New Roman&quot;"><span style="color:#323232"><span style="background:white">&nbsp;Write the database name to confirm deletion and click </span><span style="background:white"><span style="font-weight:bold">Delete.</span></span></span></span></span></li></ul>', 'ad.deletedb', true, '', '', 'admin', now());


insert into helpcenter_topic(topicid, description, content, context, active, type, external_link, createdby, createdate) values (36008, 'Can we delete a particular database schema?', '<p style="margin:0in;"><span style="font-size:12.0pt;"><span style=''font-family:"Times New Roman";''>Yes, you can delete a particular database schema.</span></span></p><p style="margin:0in;"><span style="font-size:12.0pt;"><span style=''font-family:"Times New Roman";''><span style="color:#323232;"><span style="background:white;">1. On the crawler window, select a database from the list and then click<img src="oecss/images/helpcenter/ninedots1.png" id="photo-id" style="width: 25px; height: 25px;" data-cke-saved-src="oecss/images/helpcenter/ninedots1.png" class="fr-fil fr-dib">&nbsp;and choose </span><span style="background:white;"><span style="font-weight:bold;">Delete Database.</span></span></span></span></span></p><p style="margin:0in;"><span style="font-size:12.0pt;"><span style=''font-family:"Times New Roman";''>&nbsp; &nbsp; <span style="background:white;"><span style="font-style:italic;"><span style="color:black;">A dialog box to delete the database is displayed</span></span></span></span></span></p><p style="margin:0in;"><span style="font-size:12.0pt;"><span style=''font-family:"Times New Roman";''><span style="color:#323232;"><span style="background:white;">2. Choose the Schema radio button and select the Schema name from the list to confirm deletion and click&nbsp;</span><span style="background:white;"><span style="font-weight:bold;">Delete.</span></span></span></span></span></p>', 'ad.deleteschema', true, '', '', 'admin', now());


insert into helpcenter_topic(topicid, description, content, context, active, type, external_link, createdby, createdate) values (36009, 'How to Crawl a database connection?', '<p style="margin-top:0; margin-bottom:0; vertical-align:middle;">To crawl a database connection, follow the below steps:</p><ol style="margin-left:.375in; unicode-bidi:embed; margin-top:0in; margin-bottom:0in; font-family:Calibri; font-size:11.0pt; font-weight:normal; font-style:normal;" type="1"><li style="margin-top:0; margin-bottom:0; vertical-align:middle;" value="1"><span style="font-size:12.0pt;"><span style="background:white;"><span style=''font-family:"Times New Roman";''><span style="font-weight:normal;"><span style="font-style:normal;"><span style="color:#323232;">Select a&nbsp;</span></span></span></span></span></span><span style="font-size:12.0pt;"><span style="background:white;"><span style=''font-family:"Times New Roman";''><span style="font-weight:bold;"><span style="font-style:normal;"><span style="color:#323232;">database</span></span></span></span></span></span><span style="font-size:12.0pt;"><span style="background:white;"><span style=''font-family:"Times New Roman";''><span style="font-weight:normal;"><span style="font-style:normal;"><span style="color:#323232;">&nbsp;from the list.</span></span></span></span></span></span></li><li style="margin-top:0; margin-bottom:0; vertical-align:middle;"><span style="font-size:12.0pt;"><span style="background:white;"><span style=''font-family:"Times New Roman";''><span style="color:#323232;">Select the below tab from the crawler page.<img src="oecss/images/helpcenter/crawlprofile.png" id="photo-id" style="width: 89px; height: 44.5px;" data-cke-saved-src="oecss/images/helpcenter/crawlprofile.png" class="fr-fil fr-dib"></span></span></span></span><span style="font-size:12.0pt;"><span style=''font-family:"Times New Roman";''><span style="color:black;"><span style="background:white;"><span style="font-style:italic;">A dialog box &#39;<strong>Select Important Schema For Profiling and Crawling</strong>&#39; is&nbsp;</span></span><span style="background:white;"><span style="font-style:italic;">displayed.</span></span></span></span></span></li><li style="margin-top:0; margin-bottom:0; vertical-align:middle;" value="3"><span style="font-size:12.0pt;"><span style=''font-family:"Times New Roman";''><span style="font-weight:normal;"><span style="font-style:normal;">Select a single schema name or multiple schema names to&nbsp;</span></span></span></span><span style="font-size:12.0pt;"><span style=''font-family:"Times New Roman";''><span style="font-weight:bold;"><span style="font-style:normal;">crawl</span></span></span></span><span style="font-size:12.0pt;"><span style=''font-family:"Times New Roman";''><span style="font-weight:normal;"><span style="font-style:normal;">.</span></span></span></span></li></ol><p style="margin:0in; margin-left:.75in;"><span style="font-size:12.0pt;"><span style=''font-family:"Times New Roman";''><span style="font-weight:bold;"><span style="font-style:italic;">Note</span></span><span style="font-style:italic;">: To crawl&nbsp;</span><span style="font-weight:bold;"><span style="font-style:italic;">all schemas</span></span><span style="font-style:italic;">, Click the &#39;Select All&#39; checkbox.</span></span></span></p><ol style="margin-left:.375in; unicode-bidi:embed; margin-top:0in; margin-bottom:0in; font-family:Calibri; font-size:11.0pt; font-weight:normal; font-style:normal;" type="1"><li style="margin-top:0; margin-bottom:0; vertical-align:middle;" value="4"><span style="font-size:12.0pt;"><span style=''font-family:"Times New Roman";''><span style="font-weight:normal;"><span style="font-style:normal;">Choose&nbsp;the Crawl option and click the&nbsp;Run</span></span></span></span><span style="font-size:12.0pt;"><span style=''font-family:"Times New Roman";''><span style="font-weight:normal;"><span style="font-style:normal;">&nbsp;button to initiate crawling the database connection immediately.</span></span></span></span></li></ol>', 'ad.crawldb', true, '', '', 'admin', now());


insert into helpcenter_topic(topicid, description, content, context, active, type, external_link, createdby, createdate) values (36010, 'Can we schedule a crawling and profiling to run at a later time?', '<p>Yes, Users can choose to run the Crawler and Profiler at a later time by setting the schedule time and period. OvalEdge scheduler allows users to crawl and profile a particular database/Schema on an hourly/daily/weekly/monthly/yearly basis. To access the scheduler follow these steps:</p><ol><li style="margin-top: 0px; margin-bottom: 0px; vertical-align: middle;"><span style="font-size:12.0pt"><span style="background:white"><span style="font-family:&quot;Times New Roman&quot;"><span style="font-weight:normal"><span style="font-style:normal"><span style="color:#323232">Select a </span></span></span></span></span></span><span style="font-size:12.0pt"><span style="background:white"><span style="font-family:&quot;Times New Roman&quot;"><span style="font-weight:bold"><span style="font-style:normal"><span style="color:#323232">database</span></span></span></span></span></span><span style="font-size:12.0pt"><span style="background:white"><span style="font-family:&quot;Times New Roman&quot;"><span style="font-weight:normal"><span style="font-style:normal"><span style="color:#323232"> from the list.</span></span></span></span></span></span></li><li style="margin-top: 0px; margin-bottom: 0px; vertical-align: middle;"><span style="font-size:12.0pt"><span style="background:white"><span style="font-family:&quot;Times New Roman&quot;"><span style="color:#323232">Click the <img src="oecss/images/helpcenter/crawlprofile.png" id="photo-id" style="width: 50px; height: 25px;" data-cke-saved-src="oecss/images/helpcenter/crawlprofile.png"></span></span></span></span><span style="font-size:12.0pt"><span style="background:white"><span style="font-family:&quot;Times New Roman&quot;"><span style="color:black">&nbsp;button from </span></span></span></span><span style="font-size:12.0pt"><span style="background:white"><span style="font-family:&quot;Times New Roman&quot;"><span style="color:#323232">the crawler page.</span></span></span></span>&nbsp;<span style="font-size:12.0pt"><span style="font-family:&quot;Times New Roman&quot;"><span style="color:black"><span style="background:white"><span style="font-style:italic">The dialog box to</span></span><span style="background:white"><span style="font-weight:bold"><span style="font-style:italic"> select Important schema for crawling and Profiling</span></span></span><span style="background:white"><span style="font-style:italic"> is displayed</span></span></span></span></span></li><li style="margin-top: 0px; margin-bottom: 0px; vertical-align: middle;"><span style="font-size:12.0pt"><span style="font-family:&quot;Times New Roman&quot;"><span style="font-weight:normal"><span style="font-style:normal">Select all to choose all schemas for crawling or select a particular schema name</span></span></span></span></li><li style="margin-top: 0px; margin-bottom: 0px; vertical-align: middle;"><span style="font-size:12.0pt"><span style="font-style:italic"><span style="font-family:&quot;Times New Roman&quot;">Click</span></span></span><span style="font-size:12.0pt"><span style="font-weight:bold"><span style="font-style:italic"><span style="font-family:&quot;Times New Roman&quot;"> Crawl/Profile&nbsp;options.&nbsp;</span></span></span></span><span style="font-size:12.0pt"><span style="font-family:&quot;Times New Roman&quot;"><span style="font-weight:bold"><span style="font-style:italic">Note: </span></span><span style="font-style:italic">Users can choose all schemas or particular schema to profile.</span></span></span></li><li style="margin-top: 0px; margin-bottom: 0px; vertical-align: middle;">Click Schedule and s<span style="font-size:12.0pt"><span style="font-family:&quot;Times New Roman&quot;">elect the day and time interval to perform the crawling and profiling action.</span></span></li><li style="margin-top: 0px; margin-bottom: 0px; vertical-align: middle;"><span style="font-size:12.0pt"><span style="font-family:&quot;Times New Roman&quot;">Click </span></span><span style="font-size:12.0pt"><span style="font-weight:bold"><span style="font-family:&quot;Times New Roman&quot;">Schedule</span></span></span><span style="font-size:12.0pt"><span style="font-family:&quot;Times New Roman&quot;"> to save the schedule.</span></span></li></ol>', 'ad.schedulecrawler', true, '', '', 'admin', now());


insert into helpcenter_topic(topicid, description, content, context, active, type, external_link, createdby, createdate) values (36011, 'What happens when you choose Crawl/Profile option?', '<p style="margin:0in"><span style="font-size:12.0pt"><span style="font-family:&quot;Times New Roman&quot;">A selected database Schema can be profiled during the crawling process by choosing the Crawl options in the crawl/profile tab.</span></span></p><ol type="1" style="margin-left:.375in; unicode-bidi:embed; margin-top:0in; margin-bottom:0in; font-family:Calibri; font-size:11.0pt; font-weight:normal; font-style:normal"><li value="1" style="margin-top:0; margin-bottom:0; vertical-align:middle"><span style="font-size:12.0pt"><span style="background:white"><span style="font-family:&quot;Times New Roman&quot;"><span style="font-weight:normal"><span style="font-style:normal"><span style="color:#323232">Select a </span></span></span></span></span></span><span style="font-size:12.0pt"><span style="background:white"><span style="font-family:&quot;Times New Roman&quot;"><span style="font-weight:bold"><span style="font-style:normal"><span style="color:#323232">database</span></span></span></span></span></span><span style="font-size:12.0pt"><span style="background:white"><span style="font-family:&quot;Times New Roman&quot;"><span style="font-weight:normal"><span style="font-style:normal"><span style="color:#323232"> from the list.</span></span></span></span></span></span></li><li style="margin-top:0; margin-bottom:0; vertical-align:middle"><span style="font-size:12.0pt"><span style="background:white"><span style="font-family:&quot;Times New Roman&quot;"><span style="color:#323232">Click the <img src="oecss/images/helpcenter/crawlprofile.png" id="photo-id" style="width: 50px; height: 25px;" data-cke-saved-src="oecss/images/helpcenter/crawlprofile.png"></span></span></span></span><span style="font-size:12.0pt"><span style="background:white"><span style="font-family:&quot;Times New Roman&quo