insert into helpcenter_topic(topicid, description, content, context, active, type, external_link, createdby, createdate) values (36100, 'How to create a User account?', '<p style="margin-top:0pt; margin-bottom:8pt"><span style="font-size:12.0pt"><span style="font-family:&quot;Times New Roman&quot;">To create a new user account:</span></span></p><p style="margin-top: 0px; margin-bottom: 0px; vertical-align: middle;"><span style="font-size:12.0pt"><span style="font-family:&quot;Times New Roman&quot;"><span style="font-weight:normal"><span style="font-style:normal">1. Expand the </span></span></span></span><span style="font-size:12.0pt"><span style="font-family:&quot;Times New Roman&quot;"><span style="font-weight:bold"><span style="font-style:normal">Administration</span></span></span></span><span style="font-size:12.0pt"><span style="font-family:&quot;Times New Roman&quot;"><span style="font-weight:normal"><span style="font-style:normal"> tab in the object browser and select </span></span></span></span><span style="font-size:12.0pt"><span style="font-family:&quot;Times New Roman&quot;"><span style="font-weight:bold">User Manager</span>.</span></span></p><p style="margin-top: 0px; margin-bottom: 0px; vertical-align: middle;"><span style="font-size:12.0pt"><span style="font-family:&quot;Times New Roman&quot;"><span style="font-weight:normal"><span style="font-style:normal">2. Select the Add New user tab on the top of the screen. </span></span></span></span><span style="font-size:12.0pt"><span style="font-family:&quot;Times New Roman&quot;"><span style="font-style:italic">The New user editing window opens.</span></span></span></p><p style="margin-top: 0px; margin-bottom: 0px; vertical-align: middle;"><span style="font-size:12.0pt"><span style="font-family:&quot;Times New Roman&quot;"><span style="font-weight:normal"><span style="font-style:normal">3. Enter the user information in the following required fields</span></span></span></span></p><ul><li style="margin-top: 0px; margin-bottom: 0px; vertical-align: middle;"><span style="font-size:11.0pt"><span style="font-family:&quot;Times New Roman&quot;"><span style="font-weight:bold"><span style="font-style:italic">User name- </span></span>This is the login name of the user account</span></span></li><li style="margin-top: 0px; margin-bottom: 0px; vertical-align: middle;"><span style="font-size:11.0pt"><span style="font-family:&quot;Times New Roman&quot;"><span style="font-weight:bold"><span style="font-style:italic">Password- </span></span>Specify a password</span></span></li><li style="margin-top: 0px; margin-bottom: 0px; vertical-align: middle;"><span style="font-size:11.0pt"><span style="font-family:&quot;Times New Roman&quot;"><span style="font-weight:bold"><span style="font-style:italic">Email- </span></span>Enter the user e-mail</span></span></li><li style="margin-top: 0px; margin-bottom: 0px; vertical-align: middle;"><span style="font-size:11.0pt"><span style="font-family:&quot;Times New Roman&quot;"><span style="font-weight:bold"><span style="font-style:italic">First name- </span></span>User first name</span></span></li><li style="margin-top: 0px; margin-bottom: 0px; vertical-align: middle;"><span style="font-size:11.0pt"><span style="font-family:&quot;Times New Roman&quot;"><span style="font-weight:bold"><span style="font-style:italic">Last name- </span></span>User last name</span></span></li><li style="margin-top: 0px; margin-bottom: 0px; vertical-align: middle;"><span style="font-size:11.0pt"><span style="font-family:&quot;Times New Roman&quot;"><strong>License type</strong>- choose the account license type</span></span></li><li style="margin-top: 0px; margin-bottom: 0px; vertical-align: middle;"><span style="font-size:11.0pt"><span style="font-family:&quot;Times New Roman&quot;"><span style="font-weight:bold"><span style="font-style:italic">Role-&nbsp; </span></span>Select and assign a user role(group) to the account</span></span></li></ul><p style="margin-top: 0px; margin-bottom: 0px; vertical-align: middle;"><span style="font-size:12.0pt"><span style="font-family:&quot;Times New Roman&quot;"><span style="font-weight:normal"><span style="font-style:normal">4. Enter the optional user information by expanding other fields</span></span></span></span></p><ul><li style="margin-top: 0px; margin-bottom: 0px; vertical-align: middle;"><span style="font-size:11.0pt"><span style="font-family:&quot;Times New Roman&quot;"><span style="font-weight:bold"><span style="font-style:italic">J</span></span></span></span><span style="font-size:11.0pt"><span style="font-family:&quot;Times New Roman&quot;"><span style="font-weight:bold"><span style="font-style:italic">ob Title- </span></span>User Job title in the organization</span></span></li><li style="margin-top: 0px; margin-bottom: 0px; vertical-align: middle;"><span style="font-size:11.0pt"><span style="font-family:&quot;Times New Roman&quot;"><span style="font-weight:bold"><span style="font-style:italic">Personal information- </span></span>User information like address, phone number, and gender</span></span></li></ul><p style="margin-top: 0px; margin-bottom: 0px; vertical-align: middle;"><span style="font-size:12.0pt"><span style="font-family:&quot;Times New Roman&quot;"><span style="font-weight:normal"><span style="font-style:normal">5. Click </span></span></span></span><span style="font-size:12.0pt"><span style="font-family:&quot;Times New Roman&quot;"><span style="font-weight:bold"><span style="font-style:normal">Register</span></span></span></span><span style="font-size:12.0pt"><span style="font-family:&quot;Times New Roman&quot;"><span style="font-weight:normal"><span style="font-style:normal"> to save the new user and return to the User Manager.</span></span></span></span></p>', 'ad.useraccount', true, '', '', 'admin', now());


insert into helpcenter_topic(topicid, description, content, context, active, type, external_link, createdby, createdate) values (36101, 'What are User-roles?', '<p><b><span style="font-size:12.0pt"><span style="line-height:107%"><span style="font-family:&quot;Times New Roman&quot;,serif">User roles</span></span></span></b><span style="font-size:12.0pt"><span style="line-height:107%"><span style="font-family:&quot;Times New Roman&quot;,serif"><span style="color:black"> provide an easy way to grant multiple security permissions to large numbers of users. In OvalEdge, access privileges are assigned to user roles and users are added to these user roles. </span></span></span></span></p><p><span style="font-size:12.0pt"><span style="line-height:107%"><span style="font-family:&quot;Times New Roman&quot;,serif"><span style="color:black"></span></span></span></span><b id="docs-internal-guid-3516c9ac-7fff-2b4c-e5ac-f53474601891"><img data-cke-saved-src="oecss/images/helpcenter/pointer_hand.png" src="oecss/images/helpcenter/pointer_hand.png" style="width: 30px; height: 30px;"></b><span style="font-size:12.0pt"><span style="line-height:107%"><span style="font-family:&quot;Times New Roman&quot;,serif"><span style="color:black">You can apply a user role to a group of users or to an individual user.</span></span></span></span></p><p><span style="font-size:12.0pt"><span style="line-height:107%"><span style="font-family:&quot;Times New Roman&quot;,serif"><span style="color:black"></span></span></span></span><b id="docs-internal-guid-3516c9ac-7fff-2b4c-e5ac-f53474601891"><img data-cke-saved-src="oecss/images/helpcenter/pointer_hand.png" src="oecss/images/helpcenter/pointer_hand.png" style="width: 30px; height: 30px;"></b><span style="font-size:12.0pt"><span style="line-height:107%"><span style="font-family:&quot;Times New Roman&quot;,serif"><span style="color:black">Also, each user can have multiple user roles</span></span></span></span></p>', 'ad.userrole', true, '', '', 'admin', now());


insert into helpcenter_topic(topicid, description, content, context, active, type, external_link, createdby, createdate) values (36102, 'How to edit and modify the User account?', '<p style="margin-top:7.0pt; margin-right:0in; margin-bottom:7.0pt; margin-left:0in; text-align:justify;"><span style="font-size: 14px;"><span style="font-family: Roboto, sans-serif;"><span style="color: black;">Modifying a user allows you to change and alter any information about an existing user.</span></span></span></p><p style="margin-top:7.0pt; margin-right:0in; margin-bottom:7.0pt; margin-left:0in; text-align:justify;"><span style="font-size: 14px;"><span style="font-family: Roboto,sans-serif;"><span style="line-height:107%;"><span style="line-height:107%;">To modify a user</span></span></span></span></p><ul><li class="MsoNoSpacing" style="margin: 0in 0in 0.0001pt;"><span style="font-size: 14px;"><span style="font-family: Roboto,sans-serif;"><span style="tab-stops:list .5in;">Expand the <strong>Administration</strong> tab in the object browser and select&nbsp;<img id="Picture_x0020_97" style="width: 25px; height: 25px;" data-cke-saved-src="oecss/images/helpcenter/usermanager.png" src="oecss/images/helpcenter/usermanager.png" class="fr-fil fr-dib">  <strong>User Manager</strong>.</span></span></span></li><li class="MsoNoSpacing" style="margin: 0in 0in 0.0001pt;"><span style="font-size: 14px;"><span style="font-family: Roboto,sans-serif;"><span style="tab-stops:list .5in;"><span style="color:black;">Select the <strong>user</strong> from the list whose information has to be modified.</span></span></span></span></li><li class="MsoNoSpacing" style="margin: 0in 0in 0.0001pt;"><span style="font-size: 14px;"><span style="font-family: Roboto,sans-serif;"><span style="tab-stops:list .5in;">Click the Edit icon.</span></span></span></li><li class="MsoNoSpacing" style="margin: 0in 0in 0.0001pt;"><span style="font-size: 14px;"><span style="font-family: Roboto,sans-serif;"><span style="tab-stops:list .5in;">Choose and select the <strong>information to be edited</strong> from the options.&nbsp;</span><em>An edit window provides the option to modify the user information.</em></span></span></li><li class="steps" style="margin-top: 4pt; margin-right: 0in; margin-bottom: 4pt;"><span style="font-size:12pt;"><span style="tab-stops:list .5in;"><span style="font-family: Roboto, sans-serif; font-size: 14px;"><span style="color: black;">Click <strong>Submit</strong> to save the changes.</span></span></span></span></li></ul>', 'ad.edituser', true, '', '', 'admin', now());


insert into helpcenter_topic(topicid, description, content, context, active, type, external_link, createdby, createdate) values (36103, 'How to delete a User account?', '<p class="MsoNoSpacing" style="margin:0in 0in 0.0001pt"><span style="font-size:11pt"><span style="font-family:Calibri,sans-serif"><span style="font-size:12.0pt"><span style="font-family:&quot;Times New Roman&quot;,serif">You can delete users that are no longer needed in the OvalEdge application.</span></span></span></span></p><p class="MsoNoSpacing" style="margin:0in 0in 0.0001pt"><span style="font-size:11pt"><span style="line-height:107%"><span style="font-family:Calibri,sans-serif"><span style="font-size:12.0pt"><span style="line-height:107%"><span style="font-family:&quot;Times New Roman&quot;,serif">To delete a user,</span></span></span></span></span></span></p><ol><li class="MsoNoSpacing" style="margin:0in 0in 0.0001pt"><span style="font-size:11pt"><span style="font-family:Calibri,sans-serif"><span style="font-size:12.0pt"><span style="font-family:&quot;Times New Roman&quot;,serif">Expand the <b>Administration</b> tab in the object browser and select </span></span><img id="Picture_x0020_91" style="width: 25px; height: 25px;" data-cke-saved-src="oecss/images/helpcenter/usermanager.png" src="oecss/images/helpcenter/usermanager.png"> <span style="font-size:12.0pt"><span style="font-family:&quot;Times New Roman&quot;,serif">&nbsp;<b>User Manager</b>.</span></span></span></span></li><li class="MsoNoSpacing" style="margin:0in 0in 0.0001pt"><span style="font-size:11pt"><span style="font-family:Calibri,sans-serif"><span style="font-size:12.0pt"><span style="font-family:&quot;Times New Roman&quot;,serif">Select the <b>user </b>from the list to delete it. </span></span></span></span></li><li class="MsoNoSpacing" style="margin:0in 0in 0.0001pt"><span style="font-size:11pt"><span style="font-family:Calibri,sans-serif"><span style="font-size:12.0pt"><span style="font-family:&quot;Times New Roman&quot;,serif">Select the&nbsp;</span></span><img id="Picture_x0020_91" style="width: 25px; height: 25px;" data-cke-saved-src="oecss/images/helpcenter/ninedots1.png" src="oecss/images/helpcenter/ninedots1.png"><span style="font-size:12.0pt"><span style="font-family:&quot;Times New Roman&quot;,serif">&nbsp;<b>nine-dots</b>&nbsp;button.</span></span></span></span></li><li class="MsoNoSpacing" style="margin:0in 0in 0.0001pt"><span style="font-size:11pt"><span style="font-family:Calibri,sans-serif"><span style="font-size:12.0pt"><span style="font-family:&quot;Times New Roman&quot;,serif">Click the&nbsp;<b>Delete</b>&nbsp;<b>User</b> button.</span></span><span style="font-size:12.0pt"><span style="font-family:&quot;Times New Roman&quot;,serif">&nbsp;</span></span><i><span style="font-size:12.0pt"><span style="font-family:&quot;Times New Roman&quot;,serif">A confirmation window appears to confirm the action. </span></span></i></span></span></li><li class="MsoNoSpacing" style="margin:0in 0in 0.0001pt"><span style="font-size:11pt"><span style="font-family:Calibri,sans-serif"><span style="font-size:12.0pt"><span style="font-family:&quot;Times New Roman&quot;,serif">The user is deleted.</span></span></span></span></li></ol>', 'ad.deleteuser', true, '', '', 'admin', now());


insert into helpcenter_topic(topicid, description, content, context, active, type, external_link, createdby, createdate) values (36104, 'How to assign a default User-role to a user?', '<p style="margin-top:7.0pt; margin-right:0in; margin-bottom:7.0pt; margin-left:0in"><span style="font-size:12pt"><span style="font-family:&quot;Times New Roman&quot;,serif"><span style="color:black">You can add a&nbsp;user role to a user account in the User Manager while creating a user account. See steps on&nbsp;how to create a new user. </span></span></span></p><p style="margin-top:7.0pt; margin-right:0in; margin-bottom:7.0pt; margin-left:0in"><span style="font-size:12pt"><span style="font-family:&quot;Times New Roman&quot;,serif"><span style="color:black">To add a User-role to an existing user,</span></span></span></p><ol><li style="list-style-type:none"><ol><li class="MsoNoSpacing" style="margin:0in 0in 0.0001pt; margin-right:0in"><span style="font-size:11pt"><span style="tab-stops:list 1.0in"><span style="font-family:Calibri,sans-serif"><span style="font-size:12.0pt"><span style="font-family:&quot;Times New Roman&quot;,serif">Expand the <b>Administration</b> tab in the object browser and select <b><img id="Picture_x0020_107" style="width: 25px; height: 25px;" data-cke-saved-src="oecss/images/helpcenter/usermanager.png" src="oecss/images/helpcenter/usermanager.png"> &nbsp;User Manager.</b></span></span></span></span></span></li><li class="MsoNoSpacing" style="margin:0in 0in 0.0001pt; margin-right:0in"><span style="font-size:11pt"><span style="tab-stops:list 1.0in"><span style="font-family:Calibri,sans-serif"><span style="font-size:12.0pt"><span style="font-family:&quot;Times New Roman&quot;,serif">Select the <b>Users and Roles </b>tab<b> </b>from the User Manager.</span></span></span></span></span></li><li class="MsoNoSpacing" style="margin:0in 0in 0.0001pt; margin-right:0in"><span style="font-size:11pt"><span style="tab-stops:list 1.0in"><span style="font-family:Calibri,sans-serif"><span style="font-size:12.0pt"><span style="font-family:&quot;Times New Roman&quot;,serif">Choose the <b>User</b> and click on the <b>Available Roles</b>&nbsp;associated with that user.</span></span></span></span></span>&nbsp;<span style="font-size:11pt"><span style="font-family:Calibri,sans-serif"><i><span style="font-size:12.0pt"><span style="font-family:&quot;Times New Roman&quot;,serif">A pop-up window displays the available user roles linked to the selected user.</span></span></i></span></span></li></ol></li><li style="list-style-type:none"><ol start="4"><li class="MsoNoSpacing" style="margin:0in 0in 0.0001pt; margin-right:0in"><span style="font-size:11pt"><span style="tab-stops:list 1.0in"><span style="font-family:Calibri,sans-serif"><span style="font-size:12.0pt"><span style="font-family:&quot;Times New Roman&quot;,serif">Enter the new&nbsp;<b>user role</b>.</span></span></span></span></span>&nbsp;<span style="font-size:11pt"><span style="font-family:Calibri,sans-serif"><i><span style="font-size:12.0pt"><span style="font-family:&quot;Times New Roman&quot;,serif">Alternatively, select the user role from the drop-down menu.</span></span></i></span></span></li></ol></li><li style="list-style-type:none"><ol start="5"><li class="MsoNoSpacing" style="margin:0in 0in 0.0001pt; margin-right:0in"><span style="font-size:11pt"><span style="tab-stops:list 1.0in"><span style="font-family:Calibri,sans-serif"><span style="font-size:12.0pt"><span style="font-family:&quot;Times New Roman&quot;,serif">Choose the new user role&nbsp;and <b>save</b> the changes.</span></span></span></span></span></li></ol></li></ol>', 'ad.defaultur', true, '', '', 'admin', now());


insert into helpcenter_topic(topicid, description, content, context, active, type, external_link, createdby, createdate) values (36105, 'How to Remove User from a User-role?', '<p>To&nbsp;remove a user from User-role,</p><ol><li style="list-style-type: none;"><span style="font-size:11pt"><span style="tab-stops:list 1.0in"><span style="font-family:Calibri,sans-serif"><span style="font-size:12.0pt"><span style="font-family:&quot;Times New Roman&quot;,serif">Expand the <b>Administration</b> tab in the object browser and select <b><img id="Picture_x0020_107" style="width: 25px; height: 25px;" data-cke-saved-src="oecss/images/helpcenter/usermanager.png" src="oecss/images/helpcenter/usermanager.png"> &nbsp;User Manager.</b></span></span></span></span></span></li><li style="list-style-type: none;"><span style="font-size:11pt"><span style="tab-stops:list 1.0in"><span style="font-family:Calibri,sans-serif"><span style="font-size:12.0pt"><span style="font-family:&quot;Times New Roman&quot;,serif">Select the <b>Users and Roles </b>tab<b> </b>from the User Manager.</span></span></span></span></span></li><li style="list-style-type: none;"><span style="font-size:11pt"><span style="tab-stops:list 1.0in"><span style="font-family:Calibri,sans-serif"><span style="font-size:12.0pt"><span style="font-family:&quot;Times New Roman&quot;,serif">Choose the <b>User</b> and click on the <b>Available Roles</b>&nbsp;associated with that user.</span></span></span></span></span>&nbsp;<span style="font-size:11pt"><span style="font-family:Calibri,sans-serif"><i><span style="font-size:12.0pt"><span style="font-family:&quot;Times New Roman&quot;,serif">A pop-up window displays the available user roles linked to the selected user.</span></span></i></span></span></li><li style="list-style-type: none;"><span style="font-size:11pt"><span style="font-family:Calibri,sans-serif"><span style="font-size:12.0pt"><span style="font-family:&quot;Times New Roman&quot;,serif">To delete a role of a user, click the</span></span></span></span><b><img id="Picture_x0020_107" data-cke-saved-src="oecss/images/helpcenter/cross.png" src="oecss/images/helpcenter/cross.png" style="width: 24px; height: 24px;"> </b><span style="font-size:11pt"><span style="font-family:Calibri,sans-serif"><span style="font-size:12.0pt"><span style="font-family:&quot;Times New Roman&quot;,serif">&#8203;&#8203;&#8203;</span></span></span><span style="font-family:Calibri,sans-serif"><span style="font-size:12.0pt"><span style="font-family:&quot;Times New Roman&quot;,serif">sign with the user role and save.</span></span></span></span>&nbsp;</li><li style="list-style-type: none;"><span style="font-size:11pt"><span style="tab-stops:list 1.0in"><span style="font-family:Calibri,sans-serif"><span style="font-size:12.0pt"><span style="font-family:&quot;Times New Roman&quot;,serif">Click&nbsp;to&nbsp;<b>save</b> the changes.</span></span></span></span></span></li></ol>', 'ad.removeur', true, '', '', 'admin', now());


insert into helpcenter_topic(topicid, description, content, context, active, type, external_link, createdby, createdate) values (36106, 'How do I see what users are assigned to a role in OvalEdge?', '<p style="margin:0in;"><span style="font-size:12.0pt;"><span style=''font-family:"Times New Roman";''>To see what users are assigned to a user-role,follow these steps.</span></span></p><ol><li style="margin-top: 0px; margin-bottom: 0px; vertical-align: middle;"><span style="font-size:12.0pt;"><span style=''font-family:"Times New Roman";''><span style="font-weight:normal;"><span style="font-style:normal;">Expand the&nbsp;</span></span></span></span><span style="font-size:12.0pt;"><span style=''font-family:"Times New Roman";''><span style="font-weight:bold;"><span style="font-style:normal;">Administration</span></span></span></span><span style="font-size:12.0pt;"><span style=''font-family:"Times New Roman";''><span style="font-weight:normal;"><span style="font-style:normal;">&nbsp;tab in the object browser and Select&nbsp;</span></span></span></span> <span style=''color: rgb(65, 65, 65); font-family: "Times New Roman"; font-size: 16px; font-style: normal; font-variant-ligatures: normal; font-variant-caps: normal; font-weight: 700; letter-spacing: normal; orphans: 2; text-align: left; text-indent: 0px; text-transform: none; white-space: normal; widows: 2; word-spacing: 0px; -webkit-text-stroke-width: 0px; background-color: rgb(255, 255, 255); text-decoration-thickness: initial; text-decoration-style: initial; text-decoration-color: initial; float: none; display: inline !important; position: static;''>Users and Roles</span> .</li><li style="margin-top: 0px; margin-bottom: 0px; vertical-align: middle;"><span style="font-size:12.0pt;"><span style=''font-family:"Times New Roman";''><span style="font-weight:normal;"><span style="font-style:normal;">Select the&nbsp;</span></span></span></span><span style="font-size:12.0pt;"><span style=''font-family:"Times New Roman";''><span style="font-weight:bold;"><span style="font-style:normal;">Users and Roles&nbsp;</span></span></span></span><span style="font-size:12.0pt;"><span style=''font-family:"Times New Roman";''><span style="font-weight:normal;"><span style="font-style:normal;">tab from the User Manager.</span></span></span></span></li><li style="margin-top: 0px; margin-bottom: 0px; vertical-align: middle;"><span style="font-size:12.0pt;"><span style=''font-family:"Times New Roman";''>Click the search button in&nbsp;</span></span><span style="font-size:12.0pt;"><span style="font-weight:bold;"><span style=''font-family:"Times New Roman";''>Available Roles</span></span></span><span style="font-size:12.0pt;"><span style=''font-family:"Times New Roman";''>&nbsp;and enter the User-role.</span></span></li><li style="margin-top: 0px; margin-bottom: 0px; vertical-align: middle;"><span style="font-size:12.0pt;"><span style=''font-family:"Times New Roman";''>The search action will filter all the matching roles. List of all the users assigned to the user-roles are listed.</span></span></li></ol>', 'ad.assignus', true, '', '', 'admin', now());


insert into helpcenter_topic(topicid, description, content, context, active, type, external_link, createdby, createdate) values (36107, 'What is License Type?', '<p>License Type is the permission which we can provide to the users. OvalEdge has updated role management with three different license types:</p><ol><li>Author - &nbsp;can either have metadata read or write permission.</li><li>Reader - &nbsp;can have only metadata read permission. Additionally, Reader users can be given either Data No access/ Data Preview/ Data Read access.&nbsp;</li><li>Reader Preview - &nbsp;can have only Metadata Read permission.&nbsp;</li></ol>', 'ad.licensetype', true, '', '', 'admin', now());


insert into helpcenter_topic(topicid, description, content, context, active, type, external_link, createdby, createdate) values (36108, 'How to add Remote User in the OvalEdge application?', '<p><strong><span style="font-size: 14px; font-family: Roboto, sans-serif; color: rgb(0, 0, 0);">To add Remote User in the OvalEdge application,</span></strong></p><ul><li><span style="color: rgb(0, 0, 0);"><span style="font-size: 14px;"><span style="font-family: Roboto,sans-serif;">Expand the Administration tab in the object browser and select <strong>&nbsp;Users and Roles.&nbsp;</strong></span></span></span></li><li><span style="color: rgb(0, 0, 0);"><span style="font-size: 14px;"><span style="font-family: Roboto,sans-serif;">Select the Remote User tab, next from the drop-down menu select the available connection.</span></span></span></li><li><span style="color: rgb(0, 0, 0);"><span style="font-size: 14px;"><span style="font-family: Roboto,sans-serif;">Click on the <strong>+ Add Remote User&nbsp;</strong>button. &nbsp;<em>A pop-up window is displayed.&nbsp;</em></span></span></span></li><li><div style="position: static;"><span style="color: rgb(0, 0, 0);"><span style="font-size: 14px;"><span style="font-family: Roboto,sans-serif;">Enter the Remote User Name (Email ID)&nbsp;</span></span></span></div></li><li><div style="position: static;"><span style="color: rgb(0, 0, 0);"><span style="font-size: 14px;"><span style="font-family: Roboto,sans-serif;">Enter the Password&nbsp;</span></span></span></div></li><li><div style="position: static;"><span style="color: rgb(0, 0, 0);"><span style="font-size: 14px;"><span style="font-family: Roboto,sans-serif;">Confirm the password</span></span></span></div></li><li><div style="position: static;"><span style="color: rgb(0, 0, 0);"><span style="font-size: 14px;"><span style="font-family: Roboto,sans-serif;">From drop-down list select remote role</span></span></span></div></li><li><div style="position: static;"><span style="color: rgb(0, 0, 0);"><span style="font-size: 14px;"><span style="font-family: Roboto,sans-serif;">Enter the description about the remote user.</span></span></span></div></li><li><div style="position: static;"><span style="color: rgb(0, 0, 0);"><span style="font-size: 14px;"><span style="font-family: Roboto,sans-serif;">Click on the <strong>Add User</strong> button.&nbsp;</span></span></span></div></li></ul><div style="position: static;"><span style="font-family: Roboto, sans-serif; font-size: 14px; color: rgb(0, 0, 0);"><br style="position: static;"></span></div><p><br></p>', 'ad.addremoteuser', true, '', '', 'admin', now());


insert into helpcenter_topic(topicid, description, content, context, active, type, external_link, createdby, createdate) values (36109, 'How to create a team in OvalEdge?', '<p><span style="font-family: Roboto, sans-serif; font-size: 14px; color: rgb(0, 0, 0);"><strong>To create a Team,</strong></span></p><ul><li><span style="font-family: Roboto, sans-serif; font-size: 14px; color: rgb(0, 0, 0);">Navigate to&nbsp;</span><span style="font-family: Roboto, sans-serif; font-size: 14px; color: rgb(0, 0, 0);">Administration &gt; User and Roles &gt; Team tab</span></li><li><span style="font-family: Roboto, sans-serif; font-size: 14px; color: rgb(0, 0, 0);">Click on the <strong>Create Team</strong> button. A pop-up window is displayed.</span></li><li><span style="font-family: Roboto, sans-serif; font-size: 14px; color: rgb(0, 0, 0);">Enter the team name and click on the <strong>Save</strong> button.<br style="position: static;">Note:&nbsp;</span></li></ul><p>Team name should created by adding prefix with Hashtag(#). If any any team name created with missing hashtag(#), the system automatically adds it.&nbsp;</p><p><span style="font-family: Roboto, sans-serif; font-size: 14px; color: rgb(0, 0, 0);">Click on on the edit icon from user columns to add users. <br style="position: static;"></span> </p>', 'ad.createteam', true, '', '', 'admin', now());


