insert into helpcenter_topic(topicid, description, content, context, active, type, external_link, createdby, createdate) values (36200, 'How to create a new user role?', '<p style="margin:0in;"><span style=''font-size: 14px; font-family: "Helvetica Neue", sans-serif; color: rgb(0, 0, 0); background-color: transparent; font-weight: 400; font-style: normal; font-variant: normal; text-decoration: none; vertical-align: baseline; white-space: pre-wrap; position: static;''>Admin needs to pr</span><span style="font-size: 14px; font-family: Roboto, sans-serif; color: rgb(0, 0, 0); background-color: transparent; font-weight: 400; font-style: normal; font-variant: normal; text-decoration: none; vertical-align: baseline; white-space: pre-wrap; position: static;">edefine</span><span style=''font-size: 14px; font-family: "Helvetica Neue", sans-serif; color: rgb(0, 0, 0); background-color: transparent; font-weight: 400; font-style: normal; font-variant: normal; text-decoration: none; vertical-align: baseline; white-space: pre-wrap; position: static;''>&nbsp;the role by giving the following information:</span></p><ol><li><span style="font-size: 14px;"><span style="color: rgb(0, 0, 0);"><span style=''font-family: "Helvetica Neue", sans-serif; background-color: transparent; font-weight: 400; font-style: normal; font-variant: normal; text-decoration: none; vertical-align: baseline; white-space: pre-wrap; position: static;''>Role Name - It must be the meaningful name given to any role.</span></span></span></li><li><span style="font-size: 14px;"><span style="color: rgb(0, 0, 0);"><span style=''font-family: "Helvetica Neue", sans-serif; background-color: transparent; font-weight: 400; font-style: normal; font-variant: normal; text-decoration: none; vertical-align: baseline; white-space: pre-wrap; position: static;''>License Type - It can be either Author / Reader / Reader Preview</span></span></span></li><li><span style="font-size: 14px;"><span style="color: rgb(0, 0, 0);"><span style='