insert into helpcenter_topic(topicid, description, content, context, active, type, external_link, createdby, createdate) values (36202, 'What are the different data permissions that affect data access?', '<p style="margin:0in"><span style="font-size:12.0pt"><span style="font-family:&quot;Times New Roman&quot;">Data permissions apply a level of security on the actual data so that end-users have limited access to data objects. Data permissions along with the Metadata permissions control the whole meta and data management. There are 4 different data permissions in OvalEdge.</span></span><span style="font-size:12.0pt"><span style="font-family:&quot;Times New Roman&quot;">&nbsp;</span></span></p><div><table valign="top" style="border-collapse:collapse; border-style:solid; border-color:#a3a3a3; border-width:1pt; border:undefined" title="" summary="" class=" cke_show_border"><tbody><tr><td style="border-style:solid; border-color:#a3a3a3; border-width:1pt; vertical-align:top; width:1.7451in; padding:4pt 4pt 4pt 4pt"><p style="margin:0in"><span style="font-size:12.0pt"><span style="font-family:&quot;Times New Roman&quot;"><span style="font-weight:bold">Permissions</span></span></span></p></td><td style="border-style:solid; border-color:#a3a3a3; border-width:1pt; vertical-align:top; width:5.8333in; padding:4pt 4pt 4pt 4pt"><p style="margin:0in"><span style="font-size:12.0pt"><span style="font-family:&quot;Times New Roman&quot;"><span style="font-weight:bold">Affected Actions</span></span></span></p></td></tr><tr><td style="border-style:solid; border-color:#a3a3a3; border-width:1pt; vertical-align:top; width:1.7451in; padding:4pt 4pt 4pt 4pt"><p style="margin:0in"><span style="font-size:12.0pt"><span style="font-family:&quot;Times New Roman&quot;">DATA_NOACCESS</span></span></p></td><td style="border-style:solid; border-color:#a3a3a3; border-width:1pt; vertical-align:top; width:5.8333in; padding:4pt 4pt 4pt 4pt"><p style="margin:0in"><span style="font-size:12.0pt"><span style="font-family:&quot;Times New Roman&quot;">On Databases/Tables/Files/Reports/Queries: Has no rights to see the data</span></span></p><p style="margin:0in"><span style="font-size:12.0pt"><span style="font-family:&quot;Times New Roman&quot;">On Tables/Columns: Cannot see Top Values</span></span></p></td></tr><tr><td style="border-style:solid; border-color:#a3a3a3; border-width:1pt; vertical-align:top; width:1.7451in; padding:4pt 4pt 4pt 4pt"><p style="margin:0in"><span style="font-size:12.0pt"><span style="font-family:&quot;Times New Roman&quot;">DATA_PREVIEW</span></span></p></td><td style="border-style:solid; border-color:#a3a3a3; border-width:1pt; vertical-align:top; width:5.8631in; padding:4pt 4pt 4pt 4pt"><p style="margin:0in"><span style="font-size:12.0pt"><span style="font-family:&quot;Times New Roman&quot;">On Databases/Tables: Has rights </span></span><span style="font-size:11.0pt"><span style="font-family:Arial">to see sample data and profiled results</span></span></p><p style="margin:0in"><span style="font-size:12.0pt"><span style="font-family:&quot;Times New Roman&quot;">On Files/Folders: Can view the contents</span></span></p><p style="margin:0in"><span style="font-size:12.0pt"><span style="font-family:&quot;Times New Roman&quot;">On Queries: Can only view existing queries and charts but cannot execute queries</span></span></p><p style="margin:0in"><span style="font-size:12.0pt"><span style="font-family:&quot;Times New Roman&quot;">On Lineage/Relationships: Cannot edit manually</span></span></p><p style="margin:0in"><span style="font-size:12.0pt"><span style="font-family:&quot;Times New Roman&quot;">On Reports: Can view the data in the reports but cannot subscribe to a personal page or use as references</span></span></p></td></tr><tr><td style="border-style:solid; border-color:#a3a3a3; border-width:1pt; vertical-align:top; width:1.7451in; padding:4pt 4pt 4pt 4pt"><p style="margin:0in"><span style="font-size:12.0pt"><span style="font-family:&quot;Times New Roman&quot;">DATA_READ</span></span></p></td><td style="border-style:solid; border-color:#a3a3a3; border-width:1pt; vertical-align:top; width:5.8333in; padding:4pt 4pt 4pt 4pt"><p style="margin:0in"><span style="font-size:12.0pt"><span style="font-family:&quot;Times New Roman&quot;">On Databases/Tables: Has rights to</span></span><span style="font-size:11.0pt"><span style="font-family:Arial"> create a table and download data, but cannot&nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp;Write/Update</span></span></p><p style="margin:0in"><span style="font-size:12.0pt"><span style="font-family:&quot;Times New Roman&quot;">On Queries: Will allow users to Write a query on tables. Users can perform only&nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp;SELECT statements</span></span></p><p style="margin:0in"><span style="font-size:12.0pt"><span style="font-family:&quot;Times New Roman&quot;">On Reports: C</span></span><span style="font-size:11.0pt"><span style="font-family:Arial">an see the content of charts, reports, and can subscribe to the personal page and use as references in Data Stories</span></span></p></td></tr><tr><td style="border-style:solid; border-color:#a3a3a3; border-width:1pt; vertical-align:top; width:1.7451in; padding:4pt 4pt 4pt 4pt"><p style="margin:0in"><span style="font-size:12.0pt"><span style="font-family:&quot;Times New Roman&quot;">DATA_WRITE</span></span></p></td><td style="border-style:solid; border-color:#a3a3a3; border-width:1pt; vertical-align:top; width:5.8333in; padding:4pt 4pt 4pt 4pt"><p style="margin:0in"><span style="font-size:12.0pt"><span style="font-family:&quot;Times New Roman&quot;">On Databases/ Tables: Has rights to edit/Write/Update table contents.</span></span></p><p style="margin:0in"><span style="font-size:12.0pt"><span style="font-family:&quot;Times New Roman&quot;">On Queries: Can execute and write queries to INSERT/UPDATE/DELETE data.</span></span></p><p style="margin:0in"><span style="font-size:12.0pt"><span style="font-family:&quot;Times New Roman&quot;">On Reports: Can create new reports</span></span></p><p style="margin:0in"><span style="font-size:12.0pt"><span style="font-family:&quot;Times New Roman&quot;">On Files/Folders: Can Edit/Delete a File and change a File using Extract Transform&nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp;Load</span></span></p></td></tr></tbody></table></div>', 'ad.dataperm', true, '', '', 'admin', now());


insert into helpcenter_topic(topicid, description, content, context, active, type, external_link, createdby, createdate) values (36203, 'What is the default meta and data permission of a new user-role?', '<p>When a new user-role is created a default METAREAD and DATA_NOACCESS is assigned.</p>', 'ad.defaultmdperm', true, '', '', 'admin', now());


insert into helpcenter_topic(topicid, description, content, context, active, type, external_link, createdby, createdate) values (36204, 'How to manage User-role permissions on data objects?', '<p>User-role permissions can be set and managed on each data object&nbsp;<strong>(Databases/Tables/Columns/Files/Report Groups/Report/DAG Tags)</strong>&nbsp;through the security module.</p><p><strong>Example</strong>:&nbsp;<font color="#3276b1" face="Times New Roman, serif"><span style="font-size: 16px;"><b>To assign a user role to a particular database:</b></span></font></p><ol><li style="text-align: justify; margin: 0in 0in 0.0001pt;" class="MsoNoSpacing"><span style="font-size:11pt"><span style="font-family:Calibri,sans-serif"><span style="font-size:12.0pt"><span style="font-family:&quot;Times New Roman&quot;,serif">Expand the <b>Administration</b> tab in the object browser and select</span></span></span></span><b><img id="Picture_x0020_107" data-cke-saved-src="oecss/images/helpcenter/security1.png" src="oecss/images/helpcenter/security1.png"></b><span style="font-size:11pt"><span style="font-family:Calibri,sans-serif"><span style="font-size:12.0pt"><span style="font-family:&quot;Times New Roman&quot;,serif">&nbsp;<b>Security</b>.</span></span></span></span></li><li style="text-align: justify; margin: 0in 0in 0.0001pt;" class="MsoNoSpacing"><span style="font-size:11pt"><span style="font-family:Calibri,sans-serif"><span style="font-size:12.0pt"><span style="font-family:&quot;Times New Roman&quot;,serif">Select the <b>Databases</b> tab from the security menu bar. </span></span></span></span><span style="font-size:11pt"><span style="font-family:Calibri,sans-serif"><i><span style="font-size:12.0pt"><span style="font-family:&quot;Times New Roman&quot;,serif">This is the default data object in which users have to first set security permissions. </span></span></i></span></span></li><li style="text-align: justify; margin: 0in 0in 0.0001pt;" class="MsoNoSpacing"><span style="font-size:11pt"><span style="font-family:Calibri,sans-serif"><span style="font-size:12.0pt"><span style="font-family:&quot;Times New Roman&quot;,serif">Choose a <b>database </b>and click on the <b>Available Roles.&nbsp;</b>Alternatively, you can select a database and click the&nbsp;<img src="oecss/images/helpcenter/ninedots1.png" id="photo-id" style="width: 25px; height: 25px;" data-cke-saved-src="oecss/images/helpcenter/ninedots1.png"> nine dots and choose Update Roles.&nbsp;</span></span><i><span style="font-size:12.0pt"><span style="font-family:&quot;Times New Roman&quot;,serif">A pop-up menu to manage roles is displayed.</span></span></i></span></span></li><li style="text-align: justify; margin: 0in 0in 0.0001pt;" class="MsoNoSpacing"><span style="font-size:11pt"><span style="font-family:Calibri,sans-serif"><span style="font-size:12.0pt"><span style="font-family:&quot;Times New Roman&quot;,serif">Choose and select a <b>User Role</b> from the list. Modify the Meta and Data permissions on the user role to access the selected database.&nbsp;</span></span></span></span><span style="font-size:11pt"><span style="font-family:Calibri,sans-serif"><i><span style="font-size:12.0pt"><span style="font-family:&quot;Times New Roman&quot;,serif">The abbreviations for the user role set is displayed as a superscript next to the User role selected. </span></span></i><b><span style="font-size:12.0pt"><span style="font-family:&quot;Times New Roman&quot;,serif">Example</span></span></b><span style="font-size:12.0pt"><span style="font-family:&quot;Times New Roman&quot;,serif">: QAUSER<sup><span style="color:#00b050">MR</span><span style="color:#ed7d31">DR</span></sup>.&nbsp;</span></span></span></span></li><li style="text-align: justify; margin: 0in 0in 0.0001pt;" class="MsoNoSpacing"><span style="font-size:11pt"><span style="font-family:Calibri,sans-serif"><span style="font-size:12.0pt"><span style="font-family:&quot;Times New Roman&quot;,serif">Click <b>OK</b> to add the user role.&nbsp;</span></span></span></span><span style="font-size:11pt"><span style="font-family:Calibri,sans-serif"><i><span style="font-size:12.0pt"><span style="font-family:&quot;Times New Roman&quot;,serif">Role added successfully message is displayed.</span></span></i></span></span></li></ol>', 'ad.manageur', true, '', '', 'admin', now());


insert into helpcenter_topic(topicid, description, content, context, active, type, external_link, createdby, createdate) values (36205, 'How to Secure Application from unauthorized users?', '<p style="text-align:justify; margin:0in 0in 8pt"><span style="font-size:11pt"><span style="line-height:107%"><span style="font-family:Calibri,sans-serif"><span style="font-size:12.0pt"><span style="line-height:107%"><span style="font-family:&quot;Times New Roman&quot;,serif">OvalEdge provides options to limit the application modules, users might be able to gain access running in their environment. <b>OvalEdge administrator</b> has the rights to block and remove the access of certain applications they do not want particular user roles to run. In this way, you can provide access to selective applications to particular users.</span></span></span></span></span></span></p><p style="text-align:justify; margin:0in 0in 8pt"><strong>To block an application:</strong></p><ol><li style="text-align: justify; margin: 0in 0in 0.0001pt;" class="MsoNoSpacing"><span style="font-size:11pt"><span style="font-family:Calibri,sans-serif"><span style="font-size:12.0pt"><span style="font-family:&quot;Times New Roman&quot;,serif">Expand the <b>Administration</b> tab in the object browser and Select&nbsp;</span></span></span></span><b><img id="Picture_x0020_107" data-cke-saved-src="oecss/images/helpcenter/security1.png" src="oecss/images/helpcenter/security1.png" style="width: 26px; height: 26px;"></b><span style="font-size:11pt"><span style="font-family:Calibri,sans-serif"><span style="font-size:12.0pt"><span style="font-family:&quot;Times New Roman&quot;,serif"><b>Security</b>.</span></span></span></span></li><li style="text-align: justify; margin: 0in 0in 0.0001pt;" class="MsoNoSpacing"><span style="font-size:11pt"><span style="font-family:Calibri,sans-serif"><span style="font-size:12.0pt"><span style="font-family:&quot;Times New Roman&quot;,serif">Select the <strong>A</strong><b>pplications</b> tab from the security menu bar. </span></span></span></span></li><li style="text-align: justify; margin: 0in 0in 0.0001pt;" class="MsoNoSpacing"><span style="font-size:11pt"><span style="font-family:Calibri,sans-serif"><span style="font-size:12.0pt"><span style="font-family:&quot;Times New Roman&quot;,serif">Click <img src="oecss/images/helpcenter/plus1.png" id="photo-id" style="width: 25px; height: 25px;" data-cke-saved-src="oecss/images/helpcenter/plus1.png">&nbsp;to&nbsp;<b>Add a new application</b>.&nbsp;</span></span></span></span><span style="font-size:11pt"><span style="font-family:Calibri,sans-serif"><i><span style="font-size:12.0pt"><span style="font-family:&quot;Times New Roman&quot;,serif">A pop-up window to set restrictions on a new application is displayed.</span></span></i></span></span></li><li style="text-align: justify; margin: 0in 0in 0.0001pt;" class="MsoNoSpacing"><span style="font-size:11pt"><span style="line-height:106%"><span style="font-family:Calibri,sans-serif"><span style="font-size:12.0pt"><span style="line-height:106%"><span style="font-family:&quot;Times New Roman&quot;,serif">Enter the <b>application name</b>. </span></span></span></span></span></span><span style="font-size:11pt"><span style="line-height:106%"><span style="font-family:Calibri,sans-serif"><b><i><span style="font-size:12.0pt"><span style="line-height:106%"><span style="font-family:&quot;Times New Roman&quot;,serif">Example:&nbsp;</span></span></span></i></b><span style="font-size:12.0pt"><span style="line-height:106%"><span style="font-family:&quot;Times New Roman&quot;,serif">To restrict access to the Data Story tab, Enter <b>Data Story.</b></span></span></span></span></span></span></li><li style="text-align: justify; margin: 0in 0in 0.0001pt;" class="MsoNoSpacing"><span style="font-size:11pt"><span style="line-height:106%"><span style="font-family:Calibri,sans-serif"><span style="font-size:12.0pt"><span style="line-height:106%"><span style="font-family:&quot;Times New Roman&quot;,serif">Select the <b>application path</b>&nbsp;from the dropdown and click <b>Save.</b></span></span></span></span></span></span>&nbsp;<span style="font-size:11pt"><span style="line-height:106%"><span style="font-family:Calibri,sans-serif"><span style="font-size:12.0pt"><span style="line-height:106%"><span style="font-family:&quot;Times New Roman&quot;,serif">&nbsp;</span></span></span></span></span></span><span style="font-size:11pt"><span style="line-height:106%"><span style="font-family:Calibri,sans-serif"><b><i><span style="font-size:12.0pt"><span style="line-height:106%"><span style="font-family:&quot;Times New Roman&quot;,serif">Example</span></span></span></i></b><span style="font-size:12.0pt"><span style="line-height:106%"><span style="font-family:&quot;Times New Roman&quot;,serif">: To restrict the Data Story feature tab access, Enter <b>/#nav/story</b></span></span></span></span></span></span></li><li style="text-align: justify; margin: 0in 0in 0.0001pt;" class="MsoNoSpacing"><span style="font-size:11pt"><span style="line-height:106%"><span style="font-family:Calibri,sans-serif"><span style="font-size:12.0pt"><span style="line-height:106%"><span style="font-family:&quot;Times New Roman&quot;,serif">Only user-roles in the Available role column can view this application.</span></span></span></span></span></span></li></ol>', 'ad.applicationsecure', true, '', '', 'admin', now());


insert into helpcenter_topic(topicid, description, content, context, active, type, external_link, createdby, createdate) values (36206, 'How to enable column level security?', '<h4 style="margin:2pt 0in 0.0001pt"><font color="#3276b1" face="Times New Roman, serif"><span style="font-size: 16px;"><b>To enable column level security:</b></span></font></h4><ol><li class="MsoNoSpacing" style="text-align:justify; margin:0in 0in 0.0001pt"><span style="font-size:11pt"><span style="font-family:Calibri,sans-serif"><span style="font-size:12.0pt"><span style="font-family:&quot;Times New Roman&quot;,serif">Expand the <b>Administration</b> tab in the object browser and select&nbsp;</span></span></span></span><b><img id="Picture_x0020_107" data-cke-saved-src="oecss/images/helpcenter/security1.png" src="oecss/images/helpcenter/security1.png">&nbsp;</b><span style="font-size:11pt"><span style="font-family:Calibri,sans-serif"><span style="font-size:12.0pt"><span style="font-family:&quot;Times New Roman&quot;,serif"><b>Security</b>.</span></span></span></span></li><li class="MsoNoSpacing" style="text-align:justify; margin:0in 0in 0.0001pt"><span style="font-size:11pt"><span style="font-family:Calibri,sans-serif"><span style="font-size:12.0pt"><span style="font-family:&quot;Times New Roman&quot;,serif">Select the <b>Columns</b> tab from the security menu bar. </span></span></span></span></li><li class="MsoNoSpacing" style="text-align:justify; margin:0in 0in 0.0001pt"><span style="font-size:11pt"><span style="font-family:Calibri,sans-serif"><span style="font-size:12.0pt"><span style="font-family:&quot;Times New Roman&quot;,serif">Choose a <b>database</b>, <b>schema name,</b>&nbsp;<strong>table, and column name</strong> from the list.&nbsp;</span></span><i><span style="font-size:12.0pt"><span style="font-family:&quot;Times New Roman&quot;,serif">All the columns in the selected table are displayed with a masking column and restricting column option.</span></span></i></span></span></li><li class="MsoNoSpacing" style="text-align:justify; margin:0in 0in 0.0001pt"><span style="font-size:11pt"><span style="font-family:Calibri,sans-serif"><span style="font-size:12.0pt"><span style="font-family:&quot;Times New Roman&quot;,serif">Click and Select the <b>Mask Column</b> or&nbsp;<b>Restrict Column</b> associated with the column name&nbsp;to assign a column restriction.</span></span></span></span>&nbsp;&nbsp;<span style="font-size:11pt"><span style="font-family:Calibri,sans-serif"><i><span style="font-size:12.0pt"><span style="font-family:&quot;Times New Roman&quot;,serif">A&nbsp;</span></span></i></span></span><b><img id="Picture_x0020_107" data-cke-saved-src="oecss/images/helpcenter/tickmark.png" src="oecss/images/helpcenter/tickmark.png" style="width: 25px; height: 25px;"></b><span style="font-size:11pt"><span style="font-family:Calibri,sans-serif"><i><span style="font-size:12.0pt"><span style="font-family:&quot;Times New Roman&quot;,serif">&nbsp;</span></span></i><i><span style="font-size:12.0pt"><span style="font-family:&quot;Times New Roman&quot;,serif">mark is enabled on the column/ columns selected and is restricted for access by other user roles. The <b>allowed roles</b> to access this column are displayed to the right.</span></span></i></span></span></li></ol><p style="text-align:justify; margin:0in 0in 0.0001pt" class="MsoNoSpacing"><img src="oecss/images/helpcenter/pointer_hand.png" id="photo-id" style="width: 25px; height: 25px;" data-cke-saved-src="oecss/images/helpcenter/pointer_hand.png">&nbsp; To enable column level masking and restriction, you must first choose the Column security on its table available in the <strong>Security&gt;&gt;Table</strong> tab.</p>', 'rma.entercolumnssecurity', true, '', '', 'admin', now());
insert into helpcenter_relation(topicid, childid) values(36206, 36207);


insert into helpcenter_topic(topicid, description, content, context, active, type, external_link, createdby, createdate) values (36207, 'How do I mask or restrict a column from certain User-roles?', '<p style="text-align:justify; margin:0in 0in 0.0001pt" class="MsoNoSpacing"><span style="font-size:11pt"><span style="font-family:Calibri,sans-serif"><span style="font-size:12.0pt"><span style="font-family:&quot;Times New Roman&quot;,serif">To mask or restrict a column:</span></span></span></span></p><ol><li style="text-align: justify; margin: 0in 0in 0.0001pt;" class="MsoNoSpacing"><span style="font-size:11pt"><span style="font-family:Calibri,sans-serif"><span style="font-size:12.0pt"><span style="font-family:&quot;Times New Roman&quot;,serif">Choose a <b>column name</b>. </span></span></span></span></li><li style="text-align: justify; margin: 0in 0in 0.0001pt;" class="MsoNoSpacing"><span style="font-size:11pt"><span style="font-family:Calibri,sans-serif"><span style="font-size:12.0pt"><span style="font-family:&quot;Times New Roman&quot;,serif">Click and select the <b>Mask Column</b> and <b>Restrict Column</b> associated with the column name.&nbsp;</span></span><i><span style="font-size:12.0pt"><span style="font-family:&quot;Times New Roman&quot;,serif">A&nbsp;</span></span></i></span></span><b><img id="Picture_x0020_107" data-cke-saved-src="oecss/images/helpcenter/tickmark.png" src="oecss/images/helpcenter/tickmark.png"></b><span style="font-size:11pt"><span style="font-family:Calibri,sans-serif"><i><span style="font-size:12.0pt"><span style="font-family:&quot;Times New Roman&quot;,serif">mark is enabled on the column/ columns selected and is restricted for access by other user roles. </span></span></i></span></span></li><li class="MsoNoSpacing" style="text-align: justify; margin: 0in 0in 0.0001pt;"><font face="Times New Roman, serif"><span style="font-size: 16px;">Add User-roles to the right of the column to mask/restrict the column for these user-roles.</span></font></li></ol>', 'ad.maskrestrict', true, '', '', 'admin', now());


insert into helpcenter_topic(topicid, description, content, context, active, type, external_link, createdby, createdate) values (36208, 'How to add a Domain?', '<p><span style="font-size:11pt"><span style="line-height:normal"><span style="font-family:Calibri,sans-serif"><span style="font-size:12.0pt"><span style="font-family:&quot;Times New Roman&quot;,serif"><span style="color:black"><strong>Domains</strong> are created to&nbsp;</span></span></span></span></span></span><span style="font-size:11pt"><span style="line-height:normal"><span style="font-family:Calibri,sans-serif"><span style="font-size:12.0pt"><span style="font-family:&quot;Times New Roman&quot;,serif"><span style="color:black">give permission to other users to access the data objects,&nbsp;Business Glossary, and&nbsp;PII&#8217;s by assigning users through User roles. Additionally, Domains are created for writing data stories on specific topics grouped under similar business functions.</span></span></span></span></span></span></p><p><strong><span style="font-size:11pt"><span style="line-height:normal"><span style="font-family:Calibri,sans-serif"><span style="font-size:12.0pt"><span style="font-family:&quot;Times New Roman&quot;,serif"><span style="color:black">To add&nbsp;Domain:</span></span></span></span></span></span></strong></p><ol><li><span style="font-size:11pt"><span style="font-family:Calibri,sans-serif"><span style="font-size:12.0pt"><span style="font-family:&quot;Times New Roman&quot;,serif">Expand the <b>Administration</b> tab in the object browser and Select&nbsp;</span></span></span></span><b><img id="Picture_x0020_107" data-cke-saved-src="oecss/images/helpcenter/security1.png" src="oecss/images/helpcenter/security1.png"></b><span style="font-size:11pt"><span style="font-family:Calibri,sans-serif"><span style="font-size:12.0pt"><span style="font-family:&quot;Times New Roman&quot;,serif"><b>Security</b>.</span></span></span></span></li><li><span style="font-size:11pt"><span style="font-family:Calibri,sans-serif"><span style="font-size:12.0pt"><span style="font-family:&quot;Times New Roman&quot;,serif">Select the <b>Domains</b> tab from the security menu bar. </span></span></span></span></li><li style="text-align: justify; margin: 0in 0in 0.0001pt;" class="MsoNoSpacing"><span style="font-size:11pt"><span style="font-family:Calibri,sans-serif"><span style="font-size:12.0pt"><span style="font-family:&quot;Times New Roman&quot;,serif">Click the&nbsp;<img src="oecss/images/helpcenter/plus1.png" id="photo-id" style="width: 25px; height: 25px;" data-cke-saved-src="oecss/images/helpcenter/plus1.png">&nbsp;to&nbsp;<b>Add domain</b>.&nbsp;</span></span></span></span><span style="font-size:11pt"><span style="font-family:Calibri,sans-serif"><i><span style="font-size:12.0pt"><span style="font-family:&quot;Times New Roman&quot;,serif">A dialogue box opens for user input.</span></span></i></span></span></li><li style="text-align: justify; margin: 0in 0in 0.0001pt;" class="MsoNoSpacing"><span style="font-size:11pt"><span style="font-family:Calibri,sans-serif"><span style="font-size:12.0pt"><span style="font-family:&quot;Times New Roman&quot;,serif">Enter the <b>domain name, domain description, and select the default&nbsp;Steward and Reviewer </b>from the drop-down list.&nbsp;</span></span></span></span><span style="font-size:11pt"><span style="font-family:Calibri,sans-serif"><span style="font-size:12.0pt"><span style="font-family:&quot;Times New Roman&quot;,serif">For information about<i> </i>domain Steward and Reviewer, See other Q &amp; A''s in this module.</span></span></span></span></li><li style="text-align: justify; margin: 0in 0in 0.0001pt;" class="MsoNoSpacing"><span style="font-size:11pt"><span style="font-family:Calibri,sans-serif"><span style="font-size:12.0pt"><span style="font-family:&quot;Times New Roman&quot;,serif">Click <b>Save </b>to save the domain name and return to the domain security page.</span></span></span></span></li></ol><p><b id="docs-internal-guid-ca47fb29-7fff-9f87-de80-654e3d8260e4"><img data-cke-saved-src="oecss/images/helpcenter/pointer_hand.png" src="oecss/images/helpcenter/pointer_hand.png" style="width: 29px; height: 29px;"></b><span style="font-size:11pt"><span style="line-height:normal"><span style="font-family:Calibri,sans-serif"><span style="font-size:12.0pt"><span style="font-family:&quot;Times New Roman&quot;,serif"><span style="color:black">Domains can only be created by <b>OVALEDGE_ADMIN</b> through the Security feature.</span></span></span></span></span></span></p>', 'ad.addgc', true, '', '', 'admin', now());
