insert into helpcenter_topic(topicid, description, content, context, active, type, external_link, createdby, createdate) values (36209, 'How to delete a Domain?', '<p style="text-align:justify; margin:0in 0in 8pt"><font color="#3276b1" face="Times New Roman, serif"><span style="font-size: 16px;"><b>To delete a domain:</b></span></font></p><ol><li style="text-align: justify; margin: 0in 0in 8pt;"><p><span style="font-size:11pt"><span style="font-family:Calibri,sans-serif"><span style="font-size:12.0pt"><span style="font-family:&quot;Times New Roman&quot;,serif">Expand the <b>Administration</b> tab in the object browser and Select&nbsp;</span></span></span></span><b><img id="Picture_x0020_107" data-cke-saved-src="oecss/images/helpcenter/security1.png" src="oecss/images/helpcenter/security1.png"></b><span style="font-size:11pt"><span style="font-family:Calibri,sans-serif"><span style="font-size:12.0pt"><span style="font-family:&quot;Times New Roman&quot;,serif"><b>Security</b>.</span></span></span></span></p></li><li style="text-align: justify; margin: 0in 0in 8pt;"><p><span style="font-size:11pt"><span style="font-family:Calibri,sans-serif"><span style="font-size:12.0pt"><span style="font-family:&quot;Times New Roman&quot;,serif">Select the <b>Domains </b>tab from the security menu bar. </span></span></span></span></p></li><li style="text-align: justify; margin: 0in 0in 8pt;"><p><span style="font-size:11pt"><span style="font-family:Calibri,sans-serif"><span style="font-size:12.0pt"><span style="font-family:&quot;Times New Roman&quot;,serif">Select the&nbsp;<b>Domain </b>name.</span></span></span></span></p></li><li style="text-align: justify; margin: 0in 0in 8pt;"><p><span style="font-size:11pt"><span style="font-family:Calibri,sans-serif"><span style="font-size:12.0pt"><span style="font-family:&quot;Times New Roman&quot;,serif">Click <img src="oecss/images/helpcenter/trash1.png" id="photo-id" style="width: 25px; height: 25px;" data-cke-saved-src="oecss/images/helpcenter/trash1.png">&nbsp;to delete<b>&nbsp;Domain</b>.&nbsp;</span></span></span></span><span style="font-size:11pt"><span style="font-family:Calibri,sans-serif"><i><span style="font-size:12.0pt"><span style="font-family:&quot;Times New Roman&quot;,serif">A pop-up dialogue box asking for user permission to delete the domain&nbsp;is&nbsp;displayed. Select YES to remove the domain.</span></span></i></span></span></p></li></ol>', 'ad.deletedomain', true, '', '', 'admin', now());


insert into helpcenter_topic(topicid, description, content, context, active, type, external_link, createdby, createdate) values (36210, 'How to create a Channel for group messaging?', '<h4 style="margin:2pt 0in 0.0001pt"><font color="#3276b1" face="Times New Roman, serif"><span style="font-size: 16px;">To add a new Channel:</span></font><br></h4><ol><li style="text-align: justify; margin: 0in 0in 0.0001pt;" class="MsoNoSpacing"><span style="font-size:11pt"><span style="font-family:Calibri,sans-serif"><span style="font-size:12.0pt"><span style="font-family:&quot;Times New Roman&quot;,serif">Expand the <b>Administration</b> tab in the object browser and Select </span></span></span></span><b><img id="Picture_x0020_107" data-cke-saved-src="oecss/images/helpcenter/security1.png" src="oecss/images/helpcenter/security1.png"></b><span style="font-size:11pt"><span style="font-family:Calibri,sans-serif"><span style="font-size:12.0pt"><span style="font-family:&quot;Times New Roman&quot;,serif"><b>Security</b>.</span></span></span></span></li><li style="text-align: justify; margin: 0in 0in 0.0001pt;" class="MsoNoSpacing"><span style="font-size:11pt"><span style="font-family:Calibri,sans-serif"><span style="font-size:12.0pt"><span style="font-family:&quot;Times New Roman&quot;,serif">Select the <b>Communication Channel&nbsp;</b>tab from the security menu bar. </span></span></span></span></li><li style="text-align: justify; margin: 0in 0in 0.0001pt;" class="MsoNoSpacing"><span style="font-size:11pt"><span style="font-family:Calibri,sans-serif"><span style="font-size:12.0pt"><span style="font-family:&quot;Times New Roman&quot;,serif">Click <b>Add Channel</b>.&nbsp;</span></span></span></span><span style="font-size:11pt"><span style="font-family:Calibri,sans-serif"><i><span style="font-size:12.0pt"><span style="font-family:&quot;Times New Roman&quot;,serif">A dialogue box opens for user input.</span></span></i></span></span></li><li style="text-align: justify; margin: 0in 0in 0.0001pt;" class="MsoNoSpacing"><span style="font-size:11pt"><span style="font-family:Calibri,sans-serif"><span style="font-size:12.0pt"><span style="font-family:&quot;Times New Roman&quot;,serif">Enter a <b>Channel</b> (Group) name. Click <b>OK</b> to save the new channel.&nbsp;</span></span></span></span><span style="font-size:11pt"><span style="font-family:Calibri,sans-serif"><i><span style="font-size:12.0pt"><span style="font-family:&quot;Times New Roman&quot;,serif">Start the channel name with the&nbsp;<b>#</b> channel name.</span></span></i></span></span></li></ol>', 'ad.createch', true, '', '', 'admin', now());


insert into helpcenter_topic(topicid, description, content, context, active, type, external_link, createdby, createdate) values (36211, 'What happens when you delete a channel?', '<p>When a channel gets deleted, other&nbsp;users who have this channel access&nbsp;will lose the ability to send and receive messages&nbsp;to users on this channel.</p><p><b id="docs-internal-guid-ca47fb29-7fff-9f87-de80-654e3d8260e4"><img data-cke-saved-src="oecss/images/helpcenter/pointer_hand.png" src="oecss/images/helpcenter/pointer_hand.png" style="width: 29px; height: 29px;"></b>Only user-roles with ADMIN rights can delete or edit a Channel.</p>', 'ad.deletech', true, '', '', 'admin', now());


insert into helpcenter_topic(topicid, description, content, context, active, type, external_link, createdby, createdate) values (36212, 'What is a Data Asset Group tag?', '<p><!--{cke_protected}%3Cmeta%20charset%3D%22utf-8%22%20%2F%3E--><b id="docs-internal-guid-65023866-7fff-2eca-3700-4e427e00a813"></b></p><p>Data Asset Group Tag is created to transfer and maintain&nbsp;the security, ownership, and control of specific&nbsp;data assets.&nbsp;<br><strong>Example:</strong> Two different applications (SalesForce &amp; Oracle Apps), both might contain Customers and Purchasing data. As they are two different applications, their databases are different, and traditionally the security and ownership are managed at the database level.&nbsp;&nbsp;Using OvalEdge DAG tags, ownership can be divided and transferred.&nbsp;Customers'' data can be owned by the VP of Sales while the Purchasing data can be owned by the Chief Procurement Officer.&nbsp;</p><p><strong>Manage Ownership</strong>: When you enable this, the Owner of the DAG can be made&nbsp;responsible for granting any data access requested&nbsp;on a data object within this DAG.<br><strong>Manage Stewardship:</strong> When you enable this, the Steward of the DAG can be made&nbsp;responsible for granting any Content change&nbsp;requested&nbsp;on a data object within this DAG.<br><strong>Manage Security:</strong> If the Manage Security option is not selected, you would not enable the Security Groups(available user roles on that DAG) of the DAG, but carry the database/Schema security group only.&nbsp;<br>If the Manage Security option is selected while creating a DAG, the Security Groups(i.e Available user roles to that DAG) will be having access to the data objects associated with that DAG.</p><b></b>', 'ad.dagt', true, '', '', 'admin', now());


insert into helpcenter_topic(topicid, description, content, context, active, type, external_link, createdby, createdate) values (36213, 'How to create a DAG tag?', '<p>To Create a DAG tag:</p><ol><li class="MsoNoSpacing" style="text-align:justify; margin:0in 0in 0.0001pt"><span style="font-size:11pt"><span style="font-family:Calibri,sans-serif"><span style="font-size:12.0pt"><span style="font-family:&quot;Times New Roman&quot;,serif">Expand the <b>Administration</b> tab in the object browser and select&nbsp;</span></span></span></span><b><img id="Picture_x0020_107" data-cke-saved-src="oecss/images/helpcenter/security1.png" src="oecss/images/helpcenter/security1.png"></b><span style="font-size:11pt"><span style="font-family:Calibri,sans-serif"><span style="font-size:12.0pt"><span style="font-family:&quot;Times New Roman&quot;,serif"><b>Security</b>.</span></span></span></span></li><li class="MsoNoSpacing" style="text-align:justify; margin:0in 0in 0.0001pt"><span style="font-size:11pt"><span style="font-family:Calibri,sans-serif"><span style="font-size:12.0pt"><span style="font-family:&quot;Times New Roman&quot;,serif">Select the <b>Data Asset Group&nbsp;</b>tab from the security menu bar. </span></span></span></span></li><li class="MsoNoSpacing" style="text-align:justify; margin:0in 0in 0.0001pt"><span style="font-size:11pt"><span style="font-family:Calibri,sans-serif"><span style="font-size:12.0pt"><span style="font-family:&quot;Times New Roman&quot;,serif">Select <img src="oecss/images/helpcenter/plus1.png" id="photo-id" style="width: 25px; height: 25px;" data-cke-saved-src="oecss/images/helpcenter/plus1.png">&nbsp;to&nbsp;Add Data Asset Group.</span></span></span></span></li><li class="MsoNoSpacing" style="text-align:justify; margin:0in 0in 0.0001pt"><span style="font-size:11pt"><span style="font-family:Calibri,sans-serif"><span style="font-size:12.0pt"><span style="font-family:&quot;Times New Roman&quot;,serif">Select a Domain name and enter the attributes to create a DAG tag. </span></span></span></span>For information about Owner and Steward and Approver, See other faqs in this module.</li><li class="MsoNoSpacing" style="text-align:justify; margin:0in 0in 0.0001pt">Enter the Save button to create a DAG tag.</li><li class="MsoNoSpacing" style="text-align:justify; margin:0in 0in 0.0001pt">Assign User-roles to make the users in the associated roles access the DAG tag.</li></ol><p style="text-align:justify; margin:0in 0in 0.0001pt" class="MsoNoSpacing"><b id="docs-internal-guid-e58253ec-7fff-f2f4-2aa6-00b4961466ea"><img data-cke-saved-src="oecss/images/helpcenter/pointer_hand.png" src="oecss/images/helpcenter/pointer_hand.png" style="width: 30px; height: 30px;"></b><strong>DAG Tag is accessible through the TAGS module.</strong></p><p><b id="docs-internal-guid-e58253ec-7fff-f2f4-2aa6-00b4961466ea"><img data-cke-saved-src="oecss/images/helpcenter/pointer_hand.png" src="oecss/images/helpcenter/pointer_hand.png" style="width: 30px; height: 30px;"></b><strong>Only the user role with ADMIN access can create a DAG tag. To create a DAG tag, the user needs to create a&nbsp;DOMAIN.</strong></p>', 'ad.createdag', true, '', '', 'admin', now());


insert into helpcenter_topic(topicid, description, content, context, active, type, external_link, createdby, createdate) values (36214, 'What happens when you delete a DAG tag?', '<p>When you delete a DAG tag, all the data assets added to this gets deleted and the DAG tag is removed from the application. The ownership and stewardiship will be reverted back to the schema level.</p>', 'ad.deletedag', true, '', '', 'admin', now());


insert into helpcenter_topic(topicid, description, content, context, active, type, external_link, createdby, createdate) values (36215, 'Who has the authority to create users and user-roles and manage them?', '<p><span style="font-size:12.0pt"><span style="line-height:107%"><span style="font-family:&quot;Times New Roman&quot;,serif">Only</span></span></span><b><span style="font-size:12.0pt"><span style="line-height:107%"><span style="font-family:&quot;Times New Roman&quot;,serif"> OvalEdge Administrators</span></span></span></b><span style="font-size:12.0pt"><span style="line-height:107%"><span style="font-family:&quot;Times New Roman&quot;,serif"> (OE_ADMIN) has the overall rights to configure users, user roles and to provide right access to manage them.</span></span></span><br></p>', 'ad.authority', true, '', '', 'admin', now());


insert into helpcenter_topic(topicid, description, content, context, active, type, external_link, createdby, createdate) values (36216, 'What is a default data permission?', '<p>The data permissions assigned to a user role at the time of creating the role are the default data permissions.In OvalEdge, when you enable the Default role checkbox, the role carries the configured data permissions for all the New Data Objects crawled.</p>', 'ad.defaultdatapermission', true, '', '', 'admin', now());


insert into helpcenter_topic(topicid, description, content, context, active, type, external_link, createdby, createdate) values (36217, 'What is a default role?', '<p>In OvalEdge, when you enable the Default Role checkbox, the role becomes the default for the New Data Objects. That means, when a new data object is crawled in OvalEdge, the Default role and the users within that role are automatically given access to that data object.<br></p>', 'ad.defaultrole', true, '', '', 'admin', now());


insert into helpcenter_topic(topicid, description, content, context, active, type, external_link, createdby, createdate) values (36218, 'Who is considered as default admin?', '<p>Default Admins are like super users in OvalEdge.The role that has system generated OE_ADMIN role is considered to be the default admin of the appliation.</p>', 'ad.defaultadmin', true, '', '', 'admin', now());


insert into helpcenter_topic(topicid, description, content, context, active, type, external_link, createdby, createdate) values (36219, 'What are reflect changes to tables?', '<p>When the administrator enables this checkbox, the changes get applied to all the data objects of that database. That means the setting gets applied to all the Tables under that database.<br></p>', 'ad.reflectchangestotable', true, '', '', 'admin', now());


insert into helpcenter_topic(topicid, description, content, context, active, type, external_link, createdby, createdate) values (36220, 'What are available roles?', '<p>Available roles are the allowed user roles who has access on the associated data objects.<br></p>', 'ad.availableroles', true, '', '', 'admin', now());


insert into helpcenter_topic(topicid, description, content, context, active, type, external_link, createdby, createdate) values (36221, 'What is an application path?', '<p>OvalEdge administrator has the rights to block certain application modules they do not want particular user roles to access. The application path is the path of the application from the URL(everything after the # symbol in the URL).Example:Data Story feature tab access, Enter /#nav/story<br></p>', 'ad.applicationpath', true, '', '', 'admin', now());


insert into helpcenter_topic(topicid, description, content, context, active, type, external_link, createdby, createdate) values (36222, 'What is the application name?', '<p>The name of the application module or submodule that must be&nbsp;blocked.</p>', 'ad.applicationname', true, '', '', 'admin', now());


insert into helpcenter_topic(topicid, description, content, context, active, type, external_link, createdby, createdate) values (36223, 'What are default data associations in a domain?', '<p>Data association preferences are the details that are added to a business term. Before creating a term, users should select and configure the default data association preferences for each Domain. The selected preferences are automatically chosen while creating domain terms.<br></p>', 'ad.defaultdataassociation', true, '', '', 'admin', now());


insert into helpcenter_topic(topicid, description, content, context, active, type, external_link, createdby, createdate) values (36224, 'What is manage Ownership at DAG level?', '<p>When you enable this, the Owner of the DAG can be made&nbsp;responsible for granting any data access requested&nbsp;on a data object within this DAG.</p>', 'ad.manageownership', true, '', '', 'admin', now());


insert into helpcenter_topic(topicid, description, content, context, active, type, external_link, createdby, createdate) values (36225, 'What is manage Stewarship at DAG level?', '<p>When you enable this, the Steward of the DAG can be made&nbsp;responsible for granting any Content change&nbsp;requested&nbsp;on a data object within this DAG.</p>', 'ad.managestewardship', true, '', '', 'admin', now());


insert into helpcenter_topic(topicid, description, content, context, active, type, external_link, createdby, createdate) values (36226, 'What is manage the security at DAG level?', '<p>If the Manage Security option is not selected, you would not enable the Security Groups(available user roles on that DAG) of the DAG, but carry the database/Schema security group only.&nbsp;<br>If the Manage Security option is selected while creating a DAG, the Security Groups(i.e Available user roles to that DAG) will be having access to the data objects associated with that DAG.</p>', 'ad.managesecurity', true, '', '', 'admin', now());


insert into helpcenter_topic(topicid, description, content, context, active, type, external_link, createdby, createdate) values (36227, 'What is domain category,subcategory and its user responsibilities?', '<p>In OvalEdge, a Domain is a high-level business unit of an organization. In an Organization there can be multiple domains in which business entities like Data assets, Business Glossary, PII&#8217;s are defined. Each domain can implement its own hierarchy that is independent of structures in other domains.</p><p>Similarly, categories and subcategories can also be created for a specific domain in the same way (Configure Categories). This way you can categorize all your business glossary terms and also the underlying data objects in a hierarchical structure under each domain.</p><p>There can be different data stewards who can be configured to manage the terms that are created in each hierarchical level. A new term created at a domain level can be managed by the domain steward and a new term created at a category level can be managed by the category steward if configured.</p>', 'ad.domcatsubres', true, '', '', 'admin', now());


insert into helpcenter_topic(topicid, description, content, context, active, type, external_link, createdby, createdate) values (36228, 'What are domain classification and why do we need them?', '<p>Data classification is an essential part of a successful data security use case. In order to simplify the process of identifying and protecting their sensitive data, Organizations have to implement easy to use data classifications.&nbsp;<br>Each domain can contain certain classifications.&nbsp;<br>Data Classification is a process of consistently categorizing data based on specific and pre-defined criteria so that this data can be efficiently and effectively protected.<br>Classifcation can be driven by governance, company compliance, regulation(HIPAA, or CCPA), protection of intellectual property, or perhaps most importantly, by the need &nbsp;to simplify your security strategy. It helps from the data loss prevention by protecting it.<br></p>', 'ad.domclassbenifit', true, '', '', 'admin', now());


insert into helpcenter_topic(topicid, description, content, context, active, type, external_link, createdby, createdate) values (36229, 'What is a Global Classification Domain?', '<p>All other domains will inherit the Classifications from this Domain. This when selected will overwrite the classifications of all other domains if configured earlier. This Domain cannot be used to create a DAG/Term. Only one domain can be set as Global Classification Domain.<br></p>', 'ad.globalclassification', true, '', '', 'admin', now());


insert into helpcenter_topic(topicid, description, content, context, active, type, external_link, createdby, createdate) values (36230, 'How to enter the path for files and folders?
', '<p><strong><span style="font-family: Roboto, sans-serif; font-size: 14px; color: rgb(0, 0, 0);">To enter the path for files and folders,</span></strong></p><ul><li><div style="position: static;"><span style="color: rgb(0, 0, 0);"><span style="font-size: 14px;"><span style="font-family: Roboto,sans-serif;">Navigate to Administration &gt; Security &gt; Folders</span></span></span></div></li><li><span style="color: rgb(0, 0, 0);"><span style="font-size: 14px;"><span style="font-family: Roboto,sans-serif;">Click the <strong>+&nbsp;</strong>icon, <em>a pop-up window is displayed.</em></span></span></span></li><li><div style="position: static;"><span style="color: rgb(0, 0, 0);"><span style="font-size: 14px;"><span style="font-family: Roboto,sans-serif;">Select the <span style="font-style: normal; font-variant-ligatures: normal; font-variant-caps: normal; font-weight: 400; letter-spacing: normal; orphans: 2; text-align: left; text-indent: 0px; text-transform: none; white-space: normal; widows: 2; word-spacing: 0px; -webkit-text-stroke-width: 0px; background-color: rgb(255, 255, 255); text-decoration-thickness: initial; text-decoration-style: initial; text-decoration-color: initial; float: none; position: static; display: inline !important;">Data Lake connection</span> from the drop-down list.</span></span></span></div></li><li style="box-sizing: border-box; border-width: 0px; border-style: solid; border-color: currentcolor; margin-top: 0px; margin-right: 0px; margin-bottom: 0px; color: rgb(65, 65, 65); font-family: sans-serif; font-size: 14px; font-style: normal; font-variant-ligatures: normal; font-variant-caps: normal; font-weight: 400; letter-spacing: normal; orphans: 2; text-align: left; text-indent: 0px; text-transform: none; white-space: normal; widows: 2; word-spacing: 0px; -webkit-text-stroke-width: 0px; background-color: rgb(255, 255, 255); text-decoration-thickness: initial; text-decoration-style: initial; text-decoration-color: initial; position: static;"><span style="color: rgb(0, 0, 0);"><span style="font-size: 14px;"><span style="font-family: Roboto,sans-serif;">Enter Folder name which is catalogued can be added as a path.&nbsp;</span></span></span></li><li style="box-sizing: border-box; border-width: 0px; border-style: solid; border-color: currentcolor; margin-top: 0px; margin-right: 0px; margin-bottom: 0px; color: rgb(65, 65, 65); font-family: sans-serif; font-size: 14px; font-style: normal; font-variant-ligatures: normal; font-variant-caps: normal; font-weight: 400; letter-spacing: normal; orphans: 2; text-align: left; text-indent: 0px; text-transform: none; white-space: normal; widows: 2; word-spacing: 0px; -webkit-text-stroke-width: 0px; background-color: rgb(255, 255, 255); text-decoration-thickness: initial; text-decoration-style: initial; text-decoration-color: initial; position: static;"><span style="color: rgb(0, 0, 0);"><span style="font-size: 14px;"><span style="font-family: Roboto,sans-serif;">Note: By default system will take /</span></span></span></li><li><span style="font-family: Roboto, sans-serif; font-size: 14px; color: rgb(0, 0, 0);">Click on the <strong>Save&nbsp;</strong>button</span></li></ul>', 'ad.enterpath', true, '', '', 'admin', now());


insert into helpcenter_topic(topicid, description, content, context, active, type, external_link, createdby, createdate) values (36300, 'What are the ways that tasks can be scheduled in OvalEdge?', '<p>In OvalEdge various tasks can be scheduled in:</p><ul><li>Crawler page when crawling and profiling a Database/Schema</li><li>Jobworkflow Page</li><li>Data Quality Rule</li><li>Governance Policies&nbsp;</li></ul>', 'ad.tasksschedule', true, '', '', 'admin', now());


insert into helpcenter_topic(topicid, description, content, context, active, type, external_link, createdby, createdate) values (36301, 'What happens when you reload a schedule?', '<p style="margin:0in 0in 8pt; text-align:justify"><span style="font-size:12pt"><span style="line-height:107%"><span style="font-family:&quot;Times New Roman&quot;,serif">Any inactive schedule can be converted as active schedules by selecting the reload schedule.</span></span></span></p><p style="margin:0in 0in 8pt; text-align:justify"><span style="font-size:12pt"><span style="line-height:107%"><span style="font-family:&quot;Times New Roman&quot;,serif">To reload a Schedule:</span></span></span></p><ul><li style="margin: 0in 0in 0.0001pt;" class="MsoNoSpacing"><span style="font-size:12pt"><span style="font-family:&quot;Times New Roman&quot;,serif">Select the Administration tab and hover to choose and expand the options.</span></span></li><li style="margin: 0in 0in 0.0001pt;" class="MsoNoSpacing"><span style="font-size:12pt"><span style="font-family:&quot;Times New Roman&quot;,serif">Select the <b>&#8220;Schedule&#8221; </b>module<b>. </b></span></span></li><li style="margin: 0in 0in 0.0001pt;" class="MsoNoSpacing"><span style="font-size:12pt"><span style="line-height:106%"><span style="font-family:Calibri,sans-serif"><span style="font-family:&quot;Times New Roman&quot;,serif">Click the checkbox and select a schedule description.</span></span></span></span></li><li style="margin: 0in 0in 0.0001pt;" class="MsoNoSpacing"><span style="font-size:12pt"><span style="line-height:106%"><span style="font-family:Calibri,sans-serif"><span style="font-family:&quot;Times New Roman&quot;,serif">Click the&nbsp;<img src="oecss/images/helpcenter/ninedots1.png" id="photo-id" style="width: 25px; height: 25px;" data-cke-saved-src="oecss/images/helpcenter/ninedots1.png">&nbsp;and select&nbsp;<b>&#8220;Reload Schedule</b>&#8221;.</span></span></span></span></li><li style="margin: 0in 0in 0.0001pt;" class="MsoNoSpacing"><span style="font-size:12pt"><span style="line-height:106%"><span style="font-family:Calibri,sans-serif"><span style="font-family:&quot;Times New Roman&quot;,serif">A job to reload the selected schedule is submitted successfully. </span></span></span></span></li></ul>', 'ad.reloadschedule', true, '', '', 'admin', now());


insert into helpcenter_topic(topicid, description, content, context, active, type, external_link, createdby, createdate) values (36400, 'How to add a custom configuration?', '<p>To add a custom configuration:</p><ul><li>Log in with the default Admin ID and Password&nbsp;</li><li>Navigate to <strong>Administration&#8594;Configuration</strong>. The Configuration page summarizes all the different configurations that a user needs to create or edit before using OvalEdge. You can click&nbsp;<img src="oecss/images/helpcenter/customconf.png" id="photo-id" style="width: 150px; height: 25px;" data-cke-saved-src="oecss/images/helpcenter/customconf.png">&nbsp;</li><li>Select and set the values for the configuration keys within OvalEdge.</li></ul><p>Once you have completed, <strong>Reload the configuration</strong> for the changes to be affected. In some cases, you need to restart the OvalEdge Server for the configuration to take effect.</p>', 'ad.customconfig', true, '', '', 'admin', now());


insert into helpcenter_topic(topicid, description, content, context, active, type, external_link, createdby, createdate) values (36401, 'What is the maximum number of jobs that can be run parallel in OvalEdge?', '<p>As a best practice, there can be maximum of 5 jobs that can be run parallely in OvalEdge.</p>', 'ad.maxjobs', true, '', '', 'admin', now());


insert into helpcenter_topic(topicid, description, content, context, active, type, external_link, createdby, createdate) values (36402, 'Why we need drivers and what is jar path?', '<p>A Driver is used to connect a particular database server that helps to (parse the java) string objects to its native SQL and vice versa. In OvalEdge application we add all JDBC drivers jars needed in a file and add the file location in ovaledge.jarpath.<br><strong>Note:</strong> JDBC makes it possible to establish a connection with a data source, send queries and update statements, and process the results.<br></p>', 'ad.driverjarpath', true, '', '', 'admin', now());


insert into helpcenter_topic(topicid, description, content, context, active, type, external_link, createdby, createdate) values (36403, 'What is ovaledge.base.URL?', '<p>&#8203;&#8203;&#8203;&#8203;&#8203;&#8203;&#8203;<b id="docs-internal-guid-9e9765fc-7fff-9f7c-b857-0689ea728b6c">O</b>valedge.base.URL is the web address of your OvalEdge application.</p><p><!--{cke_protected}%3Cmeta%20charset%3D%22utf-8%22%20%2F%3E--></p>', 'ad.baseurl', true, '', '', 'admin', now());


insert into helpcenter_topic(topicid, description, content, context, active, type, external_link, createdby, createdate) values (36404, 'What are the default system generated roles by ovaledge?', '<p><!--{cke_protected}%3Cmeta%20charset%3D%22utf-8%22%20%2F%3E--><b id="docs-internal-guid-95a4b89f-7fff-0e7d-04f7-e01b6da03e9f">T</b>here are two&nbsp;Default roles that are system generated by OvalEdge at the time of installation.</p><ul dir="ltr"><li><b id="docs-internal-guid-95a4b89f-7fff-0e7d-04f7-e01b6da03e9f">O</b><strong>E_ADMIN</strong>: OE_ADMIN is the super admin for the app and have all the admin rights to create and configure users and user roles and grant permissions.</li><li><b id="docs-internal-guid-95a4b89f-7fff-0e7d-04f7-e01b6da03e9f">OE_PUBLIC: </b>User which registered through open URL. They get the OE_PUBLIC role by default, this can be modified later.</li></ul>', 'ad.defaultconfigur', true, '', '', 'admin', now());


insert into helpcenter_topic(topicid, description, content, context, active, type, external_link, createdby, createdate) values (36405, 'How to manage the Super admin credential in the ovaledge app ?', '<p><b>To manage the super admin credential:</b></p><ul><li>Log in with the default ADMIN ID&nbsp;and PASSWORD&nbsp;into the application.</li><li>Navigate to the&nbsp;OvalEdge Administration module_Configuration tab.</li><li>Choose the&nbsp;Key name &#8220;ovaledge.admin.principle&#8221; and change the username for admin. Click&nbsp;Save.</li><li>Choose the Key name &nbsp;&#8220;ovaledge.admin.credential&#8221; and change the password &#8220;XXXXXXX&#8221; which is super admin user password. Click Save.</li></ul>', 'ad.managesuperadmin', true, '', '', 'admin', now());


insert into helpcenter_topic(topicid, description, content, context, active, type, external_link, createdby, createdate) values (36406, 'How to forward OvalEdge notifications to user email?', '<p>The following SMTP server configurations needs to be setup to forward OvalEdge notifications to individual user email.</p><ul><li>ovaledge.smtp.server&nbsp;&nbsp; &nbsp;</li><li>ovaledge.smtp.port</li><li>ovaledge.smtp.mode&nbsp;&nbsp; &nbsp;</li><li>ovaledge.email.username&nbsp;&nbsp; &nbsp;</li><li>ovaledge.email.password&nbsp;&nbsp; &nbsp;</li><li>ovaledge.email.fromEmail</li></ul>', 'ad.emailnotify', true, '', '', 'admin', now());


insert into helpcenter_topic(topicid, description, content, context, active, type, external_link, createdby, createdate) values (36407, 'What is Data Retention?', '<p><span style="font-size: 14px; font-family: Roboto, sans-serif; color: rgb(0, 0, 0); background-color: transparent; font-weight: 400; font-style: normal; font-variant: normal; text-decoration: none; vertical-align: baseline; white-space: pre-wrap; position: static;">The Data Retention period is to archive the logs for over a period of time, it could be a month, or a year. Once the retention period for a particular data set expires, it can be deleted or moved. Users have the option to archive the logs in the CSV or SQL format. Audit archive page in configuration displays the current row count, days to retention, archive before delete, CSV, SQL and last archive date.</span></p>', 'ad.dataretention', true, '', '', 'admin', now());


insert into helpcenter_topic(topicid, description, content, context, active, type, external_link, createdby, createdate) values (36408, 'What is Configuration Workflow?', '<p><span style="color: rgb(0, 0, 0);"><span style="font-size: 14px; font-family: Roboto, sans-serif;">The <strong>configuration</strong> button allows administration (Admin User) to design their approval workflow assigned to the user roles.&nbsp;</span></span></p><ul><li><span style="color: rgb(0, 0, 0);"><span style="font-size: 14px;"><span style="font-family: Roboto,sans-serif;">To configure navigate to Administration &gt; Security &gt; Approval Workflow &gt; Configuration</span></span></span></li><li><span style="color: rgb(0, 0, 0);"><span style="font-size: 14px;"><span style="font-family: Roboto,sans-serif;">Click on the <strong>Configuration</strong> button, the pop-up window is displayed to select <strong>Request Type</strong> and <strong>Approver List</strong>.</span></span></span></li><li><span style="color: rgb(0, 0, 0);"><span style="font-size: 14px;"><span style="font-family: Roboto,sans-serif;">The user may select from drop-down list of <strong>Request Type</strong> such as&nbsp;</span></span></span></li></ul><p dir="ltr" style="line-height: 1.38; margin-top: 0pt; margin-bottom: 0pt; position: static; margin-left: 20px;"><span style="color: rgb(0, 0, 0);"><span style="font-size: 14px;"><span style="font-family: Roboto,sans-serif;"><span style="background-color: transparent; font-weight: 400; font-style: normal; font-variant: normal; text-decoration: none; vertical-align: baseline; white-space: pre-wrap; position: static;">Access</span></span></span></span></p><p dir="ltr" style="line-height: 1.38; margin-top: 0pt; margin-bottom: 0pt; position: static; margin-left: 20px;"><span style="color: rgb(0, 0, 0);"><span style="font-size: 14px;"><span style="font-family: Roboto,sans-serif;"><span style="background-color: transparent; font-weight: 400; font-style: normal; font-variant: normal; t