insert into helpcenter_topic(topicid, description, content, context, active, type, external_link, createdby, createdate) values (36500, 'What are custom field definition vs custom field code values?', '<p><strong>Custom Field Definitions</strong>- This is the tab where users create and define different types of custom fields.(Text/code/number/date)</p><p><strong>Custom Field Code values</strong>- <span style="font-size:12.0pt"><span style="line-height:107%"><span style="font-family:&quot;Times New Roman&quot;,serif">These are similar to Picklist fields, where users can define the custom field code&nbsp;values.&nbsp;To define multiple Code Custom Field values use this page.</span></span></span></p>', 'cf.defcodes', true, '', '', 'admin', now());


insert into helpcenter_topic(topicid, description, content, context, active, type, external_link, createdby, createdate) values (36501, 'How to create custom field definitions?', '<p style="margin:0in 0in 8pt; text-align:justify"><font face="Times New Roman, serif"><span style="font-size: 16px;"></span></font><span style="font-size:12.0pt"><span style="line-height:107%"><span style="font-family:&quot;Times New Roman&quot;,serif">OvalEdge supports different Custom field types. Before creating a custom field, a user must choose the right field type for their data. </span></span></span>For detailed steps on how to create a custom field definition, see the user manual Create custom fields.<font face="Times New Roman, serif"><span style="font-size: 16px;"></span></font></p><p style="margin:0in 0in 0.0001pt 0.5in; text-align:justify"><br></p>', 'cf.def', true, '', '', 'admin', now());


insert into helpcenter_topic(topicid, description, content, context, active, type, external_link, createdby, createdate) values (36502, 'How to create custom field codes?', '<p>OvalEdge supports different Custom field types. Before creating a custom field, a user must choose the right field type for their data. For detailed steps on how to create a custom field definition, see the user manual Create custom fields.</p>', 'cf.codes', true, '', '', 'admin', now());


insert into helpcenter_topic(topicid, description, content, context, active, type, external_link, createdby, createdate) values (36503, 'How to edit the custom field values?', '<p style="margin:0in 0in 8pt; text-align:justify"><span style="font-size:12pt"><span style="line-height:107%"><span style="font-family:&quot;Times New Roman&quot;,serif">Custom field values can only be edited if the custom fields are enabled to be edited. To edit the values of a Custom field,</span></span></span></p><ul><li style="list-style-type: none;"><span style="font-size:12pt"><span style="line-height:106%"><span style="tab-stops:list .25in"><span style="font-family:Calibri,sans-serif"><span style="font-family:&quot;Times New Roman&quot;,serif">Select the Specific data object to seeing the custom fields.</span></span></span></span></span></li><li style="list-style-type: none;"><span style="font-size:12pt"><span style="line-height:106%"><span style="tab-stops:list .25in"><span style="font-family:Calibri,sans-serif"><span style="font-family:&quot;Times New Roman&quot;,serif">Enter the new value and click <img id="Picture_x0020_587" style="width: 25px; height: 25px;" data-cke-saved-src="oecss/images/helpcenter/Save-icon.png" src="oecss/images/helpcenter/Save-icon.png"> &nbsp;to <b>Save</b> the value.</span></span></span></span></span></li><li style="list-style-type: none;"><span style="font-size:12pt"><span style="line-height:106%"><span style="tab-stops:list .25in"><span style="font-family:Calibri,sans-serif"><span style="font-family:&quot;Times New Roman&quot;,serif">Select the fields that have an <span style="color:#4472c4">Edit</span> option.</span></span></span></span></span></li></ul>', 'cf.values', true, '', '', 'admin', now());


insert into helpcenter_topic(topicid, description, content, context, active, type, external_link, createdby, createdate) values (36504, 'What is the Editable/Viewable option in creating custom fields?', '<p style="margin:0in 0in 8pt; text-align:justify"><span style="font-size:12pt"><span style="background:white"><span style="line-height:normal"><span style="font-family:&quot;Times New Roman&quot;,serif"><span style="color:black">When you create a custom field label, by default the values of the field labels aren''t visible or editable. Users who have permission to create the custom field labels can decide whether the field value should be visible and editable or only visible to other users.</span></span></span></span></span></p><p style="margin:0in 0in 8pt; text-align:justify"><span style="font-size:12pt"><span style="background:white"><span style="line-height:normal"><span style="font-family:&quot;Times New Roman&quot;,serif"><span style="color:black">If a Custom field label is enabled as:</span></span></span></span></span></p><ul><li class="MsoNoSpacing" style="margin:0in 0in 0.0001pt"><span style="font-size:12pt"><span style="tab-stops:list .5in"><span style="font-family:&quot;Times New Roman&quot;,serif"><strong>Visible-</strong> Users can only read the custom field label.</span></span></span></li><li class="MsoNoSpacing" style="margin:0in 0in 0.0001pt"><span style="font-size:12pt"><span style="tab-stops:list .5in"><span style="font-family:&quot;Times New Roman&quot;,serif"><strong>Editable</strong>- Users can see and edit the custom field label values.</span></span></span></li></ul>', 'cf.Editview', true, '', '', 'admin', now());


insert into helpcenter_topic(topicid, description, content, context, active, type, external_link, createdby, createdate) values (36505, 'How are custom fields displayed in the data object?', '<p><span style="font-size:12.0pt"><span style="line-height:107%"><span style="font-family:&quot;Times New Roman&quot;,serif">Navigate to the particular object type in the data catalog module. You can view custom fields as additional fields on the right-hand corner of the page. Click the information icon to view the custom fields. The fields that can be edited are displayed as <span style="color:#4472c4">Edit</span>. </span></span></span>&#8203;&#8203;&#8203;&#8203;&#8203;&#8203;&#8203;</p>', 'cf.fielddisplay', true, '', '', 'admin', now());


insert into helpcenter_topic(topicid, description, content, context, active, type, external_link, createdby, createdate) values (36506, 'On what data objects can custom fields/additional fields be created?', '<p style="text-align:justify; margin:0in 0in 0.0001pt" class="MsoNoSpacing"><span style="font-size:12pt"><span style="tab-stops:list .5in"><span style="font-family:&quot;Times New Roman&quot;,serif">The object type on which the custom fields are created depends on the selected connection type.</span></span></span></p><p class="MsoNoSpacing" style="margin-left:.5in; text-align:justify; margin:0in 0in 0.0001pt"><span style="font-size:12pt"><span style="font-family:&quot;Times New Roman&quot;,serif"><b>Example:</b> If the connection type is an S3 connection, then custom fields are created on files and file columns. For more information on each object type see <span style="color:#4472c4">Custom object types.&nbsp;</span></span></span><span style="font-size:12pt"><span style="line-height:normal"><span style="tab-stops:list .5in"><span style="font-family:Calibri,sans-serif"><span style="font-family:&quot;Times New Roman&quot;,serif">Users can create custom fields for a specific object type(Table/column/Report/Report field/File/File column/Query/Packages) or on all of the object types.</span></span></span></span></span></p>', 'cf.dataobjects', true, '', '', 'admin', now());


insert into helpcenter_topic(topicid, description, content, context, active, type, external_link, createdby, createdate) values (36507, 'What are the different Custom field types?', '<p>There are three different areas in which a custom field can be created on the connection, in the business glossary and tag. when the connection is selected, custom fields can be created on a default basis and applied across all the connections metadata or on a specific report/report column/table/table column or query.<br></p>', 'cf.fieldtype', true, '', '', 'admin', now());


insert into helpcenter_topic(topicid, description, content, context, active, type, external_link, createdby, createdate) values (36508, 'What are the field labels?', '<p>How the field label will be viewed in the application.<br></p>', 'cf.fieldlabel', true, '', '', 'admin', now());


insert into helpcenter_topic(topicid, description, content, context, active, type, external_link, createdby, createdate) values (36509, 'What activities can a user audit?', '<p>A user can audit the following activities:</p><ul><li>Audit User (login)</li><li>Audit User role</li><li>Audit Role Permission</li><li>Audit Term</li><li>Audit Tag</li><li>Audit Crawler</li></ul>', 'at.audittrailact', true, '', '', 'admin', now());


insert into helpcenter_topic(topicid, description, content, context, active, type, external_link, createdby, createdate) values (36600, 'How to create advanced jobs using attributes?', '<p style="margin-bottom: 0cm; line-height: normal; position: static;"><span style="color: rgb(0, 0, 0);">There are certain tasks within OvalEdge that need advanced algorithms to be used to get the desired results. The Advanced job feature enables the user to undertake tasks that cannot be accomplished using a traditional job.</span></p><p style="margin-bottom: 0cm; line-height: normal; position: static;"><span style="color: rgb(0, 0, 0);"><br></span></p><p style="margin-bottom: 0cm; line-height: normal; position: static;"><span style="color: rgb(0, 0, 0);">A new advanced job can be created by entering the following details:</span></p><p style="margin-bottom: 0cm; line-height: normal; position: static;"><span style="color: rgb(0, 0, 0);"><br></span></p><ol start="1" style="margin-top: 0cm; position: static;" type="1"><li style="color: black; margin-bottom: 0cm; line-height: normal; vertical-align: baseline; position: static;"><span style="color: rgb(0, 0, 0);">Name: Advanced job name</span></li><li style="color: black; margin-bottom: 0cm; line-height: normal; vertical-align: baseline; position: static;"><span style="color: rgb(0, 0, 0);">Type: It is used to define the type of advanced job Ex: Load/ Upload/ Create.</span></li><li style="color: black; margin-bottom: 0cm; line-height: normal; vertical-align: baseline; position: static;"><span style="color: rgb(0, 0, 0);">Class: It is package that will perform a task or job</span></li><li style="color: black; margin-bottom: 0cm; line-height: normal; vertical-align: baseline; position: static;"><span style="color: rgb(0, 0, 0);">Help: It gives information about the advanced job</span></li><li style="color: black; margin-bottom: 0cm; line-height: normal; vertical-align: baseline; position: static;"><span style="color: rgb(0, 0, 0);">Attribute Label: It represents what inputs need to be entered</span></li><li style="color: black; margin-bottom: 0cm; line-height: normal; vertical-align: baseline; position: static;"><span style="color: rgb(0, 0, 0);">Attributes: Actual parameters to pass for a job</span><span style="color: rgb(0, 0, 0);"></span></li></ol>', 'ad.newadvjob', true, '', '', 'admin', now());


insert into helpcenter_topic(topicid, description, content, context, active, type, external_link, createdby, createdate) values (37000, 'Basic Navigation of OvalEdge', '<p><b id="docs-internal-guid-d6797937-7fff-4e14-ccf1-d777be10fb80">T</b>his guide helps users navigate through the available data or to find and explore data from all systems in your organization.&nbsp;</p><p><!--{cke_protected}%3Cmeta%20charset%3D%22utf-8%22%20%2F%3E--></p><p dir="ltr"><!--{cke_protected}%3Cmeta%20charset%3D%22utf-8%22%20%2F%3E--></p><p dir="ltr"><!--{cke_protected}%3Cmeta%20charset%3D%22utf-8%22%20%2F%3E--></p><p dir="ltr"><b id="docs-internal-guid-d6797937-7fff-4e14-ccf1-d777be10fb80">T</b>he first step is the <em><strong>user login</strong></em>.</p><p dir="ltr"><b id="docs-internal-guid-d6797937-7fff-4e14-ccf1-d777be10fb80">U</b>se your <em><strong>username</strong></em> and <em><b>password</b>&nbsp;</em>to log in.</p><p dir="ltr"><b id="docs-internal-guid-d6797937-7fff-4e14-ccf1-d777be10fb80">P</b>ress on the Forgot my Password button to reset your password. Once you log in, you see a left menu and a top navigation bar.</p><h3 dir="ltr"><b id="docs-internal-guid-d6797937-7fff-4e14-ccf1-d777be10fb80">Left Menu</b></h3><p dir="ltr"><b id="docs-internal-guid-d6797937-7fff-4e14-ccf1-d777be10fb80">T</b>he Left Menu provides access to various applications within OvalEdge.&nbsp;</p><p dir="ltr"><!--{cke_protected}%3Cmeta%20charset%3D%22utf-8%22%20%2F%3E--><b id="docs-internal-guid-029225ae-7fff-8580-1327-0ad8c8987da9"><img data-cke-saved-src="oecss/images/helpcenter/pointer_hand.png" src="oecss/images/helpcenter/pointer_hand.png" style="width: 30px; height: 30px;"></b><b> You know that you can press on the right arrow <img data-cke-saved-src="oecss/images/helpcenter/right-arrow.jpg" src="oecss/images/helpcenter/right-arrow.jpg" style="width: 37px; height: 30px;"> to expand and collapse the left menu.</b></p><h3 dir="ltr"><b id="docs-internal-guid-d6797937-7fff-4e14-ccf1-d777be10fb80">Top Menu bar</b></h3><p dir="ltr"><b id="docs-internal-guid-d6797937-7fff-4e14-ccf1-d777be10fb80">U</b>se the Top menu as a search or gateway to favorite applications.</p><h3 dir="ltr"><b id="docs-internal-guid-d6797937-7fff-4e14-ccf1-d777be10fb80">Bookmark</b></h3><p dir="ltr"><b id="docs-internal-guid-d6797937-7fff-4e14-ccf1-d777be10fb80">Y</b>ou can bookmark your favorite application, table, or report by navigating to that page and clicking the bookmark icon oecss/images/helpcenter/bookmark.png.&nbsp;</p><p dir="ltr"><!--{cke_protected}%3Cmeta%20charset%3D%22utf-8%22%20%2F%3E--><b id="docs-internal-guid-05791869-7fff-c748-19c0-db5aae3c8d64"><img data-cke-saved-src="oecss/images/helpcenter/pointer_hand.png" src="oecss/images/helpcenter/pointer_hand.png" style="width: 30px; height: 30px;"></b><b> You can pin oecss/images/helpcenter/push-pin.png your favorite bookmarks on the browser header and use it as an extended favorites bar for easy navigation.</b></p><h3 dir="ltr"><b id="docs-internal-guid-d6797937-7fff-4e14-ccf1-d777be10fb80">Quick Search Bar</b></h3><p dir="ltr"><b id="docs-internal-guid-d6797937-7fff-4e14-ccf1-d777be10fb80">Y</b>ou can search for any table, column, file, or report using the quick search bar. In case the results are not available, you can perform a detailed search using Smart-Catalog application.&nbsp;</p><h3 dir="ltr"><b id="docs-internal-guid-d6797937-7fff-4e14-ccf1-d777be10fb80">Profile</b></h3><p dir="ltr"><b id="docs-internal-guid-d6797937-7fff-4e14-ccf1-d777be10fb80">T</b>his is an essential application for all the users to update your password, profile picture, or phone number.&nbsp;</p><p dir="ltr"><!--{cke_protected}%3Cmeta%20charset%3D%22utf-8%22%20%2F%3E--><b id="docs-internal-guid-d592669e-7fff-46e3-035a-b8cb3f3030b7"><img data-cke-saved-src="oecss/images/helpcenter/pointer_hand.png" src="oecss/images/helpcenter/pointer_hand.png" style="width: 30px; height: 30px;"></b><b> Adding a profile picture is recommended because it provides 3 times more visibility about you in your company.</b></p><h3 dir="ltr"><b id="docs-internal-guid-d6797937-7fff-4e14-ccf1-d777be10fb80">Messages</b></h3><p dir="ltr"><b id="docs-internal-guid-d6797937-7fff-4e14-ccf1-d777be10fb80">T</b>he Message application displays all of your messages. The messages displayed in bold require your attention as they are specifically directed towards you.&nbsp;</p><p dir="ltr"><!--{cke_protected}%3Cmeta%20charset%3D%22utf-8%22%20%2F%3E--><b id="docs-internal-guid-92d1fc8d-7fff-7e6a-04f4-4a0474e84ebb"><img data-cke-saved-src="oecss/images/helpcenter/pointer_hand.png" src="oecss/images/helpcenter/pointer_hand.png" style="width: 30px; height: 30px;"></b><b> You can press a unique identifier to sign in your messages to get to the entire data catalog.</b></p><h3 dir="ltr"><b id="docs-internal-guid-d6797937-7fff-4e14-ccf1-d777be10fb80">Dashboard</b></h3><p dir="ltr"><b id="docs-internal-guid-d6797937-7fff-4e14-ccf1-d777be10fb80">T</b>he Dashboard application visually provides you all the insights about your company.</p><p><br></p><p dir="ltr"><b id="docs-internal-guid-d6797937-7fff-4e14-ccf1-d777be10fb80">Learn more about the basic navigation in this video:</b><!--{cke_protected}%3Cmeta%20charset%3D%22utf-8%22%20%2F%3E--><!--{cke_protected}%3Cmeta%20charset%3D%22utf-8%22%20%2F%3E--><b id="docs-internal-guid-c430bb37-7fff-3fac-abb8-ace6372c2b40">&lt;https://vimeo.com/349670501&gt;</b></p><p dir="ltr"><a data-cke-saved-href="https://vimeo.com/349670501" target="<iframesrc=&quot;https://player.vimeo.com/video/349670501&quot;width=&quot;640&quot;height=&quot;360&quot;frameborder=&quot;0&quot;allow=&quot;autoplay;fullscreen&quot;allowfullscreen></iframe><p><ahref=&quot;https://vimeo.com/349670501&quot;>OvalEdgeBasicNavigation</a>from<ahref=&quot;https://vimeo.com/user101095356&quot;>ovaledge</a>on<ahref=&quot;https://vimeo.com&quot;>Vimeo</a>.</p>" href="https://vimeo.com/349670501">https://vimeo.com/349670501</a></p><p dir="ltr"><br></p><p dir="ltr"><b id="docs-internal-guid-d6797937-7fff-4e14-ccf1-d777be10fb80">Embed URL here&lt;<a data-cke-saved-href="https://vimeo.com/351240320" href="https://vimeo.com/351240320">https://vimeo.com/351240320</a>&gt;</b></p>', 'gs.basicnav', true, '', '', 'admin', now());


insert into helpcenter_topic(topicid, description, content, context, active, type, external_link, createdby, createdate) values (37001, 'Research/Find/Explore Data', '<p><!--{cke_protected}%3Cmeta%20charset%3D%22utf-8%22%20%2F%3E--></p><p><!--{cke_protected}%3Cmeta%20charset%3D%22utf-8%22%20%2F%3E--></p><h3 dir="ltr"><!--{cke_protected}%3Cmeta%20charset%3D%22utf-8%22%20%2F%3E--></h3><h3 dir="ltr"><b id="docs-internal-guid-57679e3a-7fff-4221-c624-da9c7f57afc3">Navigate Data Functionally-(Tags)</b></h3><p dir="ltr"><b id="docs-internal-guid-57679e3a-7fff-4221-c624-da9c7f57afc3">U</b>se the tag application to get to the data quickly. Click the subject area you are interested in and keep exploring. Tag applications have a tree structure. Once the data is displayed, you would see the statistics and all the data objects associated with it.&nbsp;</p><h3 dir="ltr"><b id="docs-internal-guid-57679e3a-7fff-4221-c624-da9c7f57afc3">Navigate/ Research /Explore Data Technically -(Smart Catalog)</b></h3><p dir="ltr"><b id="docs-internal-guid-57679e3a-7fff-4221-c624-da9c7f57afc3">U</b>se Smart Catalog application to navigate to the data using databases, schema, files, infographics like Tableau, Power BI reports. You can use various ways to research data. Either you can type the keyword in Explore All or research based on the specifics tabs.&nbsp;</p><p dir="ltr"><b id="docs-internal-guid-57679e3a-7fff-4221-c624-da9c7f57afc3">I</b>nfographics application is where you can search all the Tableau, Power BI, or any other kind of reports, charts, or dashboards.</p><p dir="ltr"><b id="docs-internal-guid-57679e3a-7fff-4221-c624-da9c7f57afc3">D</b>ata Story application is the entire knowledge base for your data. You can create wiki kind of documentation in data story.</p><p dir="ltr"><b id="docs-internal-guid-57679e3a-7fff-4221-c624-da9c7f57afc3">C</b>hanges tab lets you explore all the changes in the data. Using this, you can research what data elements are changed and compared between dates.&nbsp;</p><p dir="ltr"><b id="docs-internal-guid-57679e3a-7fff-4221-c624-da9c7f57afc3">M</b>etadata lets you understand the latest changes in the metadata of the entire table lists. Sort the data by table or column change date to understand the changes.&nbsp;</p><p dir="ltr"><b id="docs-internal-guid-57679e3a-7fff-4221-c624-da9c7f57afc3"></b><!--{cke_protected}%3Cmeta%20charset%3D%22utf-8%22%20%2F%3E--><b id="docs-internal-guid-2e5b54be-7fff-cec6-3841-46f921be85c6"><img data-cke-saved-src="oecss/images/helpcenter/pointer_hand.png" src="oecss/images/helpcenter/pointer_hand.png" style="width: 30px; height: 30px;"></b><b> Click on the table to compare the metadata changes.&nbsp;</b></p><h3 dir="ltr"><b id="docs-internal-guid-57679e3a-7fff-4221-c624-da9c7f57afc3">Navigate/ Explore Data -( Glossary of Terms)</b></h3><p dir="ltr"><b id="docs-internal-guid-57679e3a-7fff-4221-c624-da9c7f57afc3">W</b>hen you implement glossary of terms, you can also explore data using business terms. Click on Glossary of Terms Application and search your desired business term. When you locate the business term, you can read more about the term, its usage, and all the details and see other related terms.&nbsp;</p><p dir="ltr"><b id="docs-internal-guid-57679e3a-7fff-4221-c624-da9c7f57afc3"></b><!--{cke_protected}%3Cmeta%20charset%3D%22utf-8%22%20%2F%3E--><b id="docs-internal-guid-cf0316d3-7fff-01bf-2d56-007b6a8850e5"><img data-cke-saved-src="oecss/images/helpcenter/pointer_hand.png" src="oecss/images/helpcenter/pointer_hand.png" style="width: 30px; height: 30px;"></b><b>You can also view associated data dictionary.&nbsp;</b></p><p dir="ltr"><br><b id="docs-internal-guid-cd8d3b71-7fff-0579-fdc2-9dbeb3c692a1"><b id="docs-internal-guid-57679e3a-7fff-4221-c624-da9c7f57afc3">Learn more about how to&nbsp;research/find/explore the data in this video:</b></b></p><p dir="ltr">&lt;https://vimeo.com/351240320&gt;</p><p dir="ltr">&#8203;&#8203;&#8203;&#8203;&#8203;&#8203;&#8203;</p><p dir="ltr"><br></p><p dir="ltr"><br></p>', 'gs.resfinexp', true, '', '', 'admin', now());


insert into helpcenter_topic(topicid, description, content, context, active, type, external_link, createdby, createdate) values (37002, 'Understanding Data', '<p><br></p><p><!--{cke_protected}%3Cmeta%20charset%3D%22utf-8%22%20%2F%3E--></p><p dir="ltr"><br></p><p dir="ltr"><!--{cke_protected}%3Cmeta%20charset%3D%22utf-8%22%20%2F%3E--></p><p dir="ltr"><b id="docs-internal-guid-4da26ea8-7fff-464a-2bbb-b69cf48ec5fc">O</b>nce you click on a table, file, infographics, or query, you can see many details about the specific data object and its relationship with other objects. OvalEdge provides more information about a selected object by which you can quickly understand the data using various data statistics. OvalEdge also provides a comprehensive view of all kinds of relationships like references, entity-relationship, and pattern relationship.</p><p dir="ltr"><!--{cke_protected}%3Cmeta%20charset%3D%22utf-8%22%20%2F%3E--><b id="docs-internal-guid-ebab9f1a-7fff-4757-6d05-267469770080"><img data-cke-saved-src="oecss/images/helpcenter/light_bulb1.png" src="oecss/images/helpcenter/light_bulb1.png" style="width: 30px; height: 30px;"></b><b> Hover and click on the question mark to reach help on any page across the application.</b></p><p dir="ltr"><!--{cke_protected}%3Cmeta%20charset%3D%22utf-8%22%20%2F%3E--><b id="docs-internal-guid-3013e200-7fff-84af-d647-29b438dd5a42"><img data-cke-saved-src="oecss/images/helpcenter/pointer_hand.png" src="oecss/images/helpcenter/pointer_hand.png" style="width: 30px; height: 30px;"></b><b> Whenever you see blue color text or label, this means you can click on it to understand more.&nbsp;</b></p><p dir="ltr"><!--{cke_protected}%3Cmeta%20charset%3D%22utf-8%22%20%2F%3E--><b id="docs-internal-guid-bd961074-7fff-c37f-ee0b-c9074db0a153"><img data-cke-saved-src="oecss/images/helpcenter/pointer_hand.png" src="oecss/images/helpcenter/pointer_hand.png" style="width: 30px; height: 30px;"></b><b>When you are writing content, you can click on @ sign to add links to other data objects and ask questions</b></p><p><br></p><h3 dir="ltr"><b id="docs-internal-guid-4da26ea8-7fff-464a-2bbb-b69cf48ec5fc">Statistics / Profiled Results</b></h3><p dir="ltr"><b id="docs-internal-guid-4da26ea8-7fff-464a-2bbb-b69cf48ec5fc">P</b>rofiled results provide a way to understand data quickly. You can see various statistics like top 50 values, various counts of every field.&nbsp;</p><h3 dir="ltr"><b id="docs-internal-guid-4da26ea8-7fff-464a-2bbb-b69cf48ec5fc">Top Users</b></h3><p dir="ltr"><b id="docs-internal-guid-4da26ea8-7fff-464a-2bbb-b69cf48ec5fc">W</b>hen you have a question about the data, you can see the insights who are the right user who can answer your question.&nbsp;</p><h3 dir="ltr"><b id="docs-internal-guid-4da26ea8-7fff-464a-2bbb-b69cf48ec5fc">Endorsements</b></h3><p dir="ltr"><b id="docs-internal-guid-4da26ea8-7fff-464a-2bbb-b69cf48ec5fc">W</b>hen you are using some report, or table in your analysis, you should endorse that table so that others can see various endorsements.&nbsp;</p><h3 dir="ltr"><b id="docs-internal-guid-4da26ea8-7fff-464a-2bbb-b69cf48ec5fc">Entity Relationships</b></h3><p dir="ltr"><b id="docs-internal-guid-4da26ea8-7fff-464a-2bbb-b69cf48ec5fc">E</b>ntity relationships are direct relationships between various tables. Users identify these relationships by two scores, Similarity Score and Join Score.</p><p dir="ltr"><b id="docs-internal-guid-4da26ea8-7fff-464a-2bbb-b69cf48ec5fc">Example: </b>When similarity score is trending towards 100, it means these two tables are duplicate. When join score is trending towards 100, it means the tables have a perfect primary and foreign key relationship.&nbsp;</p><h3 dir="ltr"><b id="docs-internal-guid-4da26ea8-7fff-464a-2bbb-b69cf48ec5fc">Lineage</b></h3><p dir="ltr"><b id="docs-internal-guid-4da26ea8-7fff-464a-2bbb-b69cf48ec5fc">U</b>sing lineage tab, you can understand where the data is coming from and how it''s processed and where it is moving. OvalEdge also shows the lineage at the column level by clicking the circle icon between two main objects.&nbsp;&nbsp;</p><h3 dir="ltr"><b id="docs-internal-guid-4da26ea8-7fff-464a-2bbb-b69cf48ec5fc">References</b></h3><p dir="ltr"><b id="docs-internal-guid-4da26ea8-7fff-464a-2bbb-b69cf48ec5fc">W</b>hen any other data object uses the table in the documentation, query, it gets shown in references.&nbsp;</p><h3 dir="ltr"><b id="docs-internal-guid-4da26ea8-7fff-464a-2bbb-b69cf48ec5fc">Collect Data specific to your project</b></h3><p dir="ltr"><b id="docs-internal-guid-4da26ea8-7fff-464a-2bbb-b69cf48ec5fc">W</b>hen you are working for a specific project and want to collect data from the entire organization, you can use Project application. You can create and share the project with your colleagues. Make a project as your default project. Now anywhere in your OvalEdge data catalog, you can click on Project icon to add data to your project. You can also add comments specific to your project.&nbsp;</p><h3 dir="ltr"><b id="docs-internal-guid-4da26ea8-7fff-464a-2bbb-b69cf48ec5fc">Collaborate / Ask questions about data</b></h3><p dir="ltr"><b id="docs-internal-guid-4da26ea8-7fff-464a-2bbb-b69cf48ec5fc">O</b>valEdge allows you to ask questions about the data from anyone in the organization. However, you would like to ask a question from either its designated steward, owner, or a user who is using that data object. Using OvalEgde, you can understand the users who are mostly using that data object. It provides three numbers, Views, Queries, and Questions responses.&nbsp;</p><p dir="ltr"><!--{cke_protected}%3Cmeta%20charset%3D%22utf-8%22%20%2F%3E--><b id="docs-internal-guid-078f9ce7-7fff-1390-4330-4b5a465a969f"><img data-cke-saved-src="oecss/images/helpcenter/pointer_hand.png" src="oecss/images/helpcenter/pointer_hand.png" style="width: 30px; height: 30px;"></b><b> To ask a question, select a user, and type your question. You can add more users or data objects by a @ sign.&nbsp;</b></p><p dir="ltr">Learn more about how OvalEdge helps you to Understand your data in this video: &lt;https://vimeo.com/351260903&gt;&#8203;&#8203;&#8203;&#8203;&#8203;&#8203;&#8203;</p><p><br></p><p dir="ltr"><b id="docs-internal-guid-4da26ea8-7fff-464a-2bbb-b69cf48ec5fc">Learn more about how OvalEdge helps you to Understand your data in this video: &lt;https://vimeo.com/351260903&gt;</b></p>', 'gs.understanddata', true, '', '', 'admin', now());


insert into helpcenter_topic(topicid, description, content, context, active, type, external_link, createdby, createdate) values (37003, 'Analyze / Query Data', '<p><br></p><p><!--{cke_protected}%3Cmeta%20charset%3D%22utf-8%22%20%2F%3E--></p><p dir="ltr"><b id="docs-internal-guid-5fda2cdd-7fff-4869-9c98-3f677c4f34a4">W</b>hen profiled results are not sufficient to give you detailed information about the data and your data related question is not answered, use the Query Sheet to build a query, or write a query. OvalEdge allows non-SQL users to write SQL using step by step.&nbsp;</p><p><b id="docs-internal-guid-5fda2cdd-7fff-4869-9c98-3f677c4f34a4">Learn more about how to Query Analysis in this video:&lt;https://vimeo.com/351275661&gt;</b></p>', 'gs.analyzeandquery', true, '', '', 'admin', now());


insert into helpcenter_topic(topicid, description, content, context, active, type, external_link, createdby, createdate) values (38000, 'What is a business description?', '<p><!--{cke_protected}%3Cmeta%20charset%3D%22utf-8%22%20%2F%3E--></p><p dir="ltr">Descriptions are&nbsp;helpful for users to understand the data objects.&nbsp;You can manually enter the business description or you can bulk upload the business descriptions for all the data objects from a&nbsp;file format (.csv). You can download the template from <strong>Advance Tools &gt; Load metadata from the files</strong> module to enter and load&nbsp;the business descriptions from a file.</p><p><!--{cke_protected}%3Cmeta%20charset%3D%22utf-8%22%20%2F%3E--><b id="docs-internal-guid-574867a9-7fff-8dc5-8dbd-c0936ef001d9"><img data-cke-saved-src="oecss/images/helpcenter/pointer_hand.png" src="oecss/images/helpcenter/pointer_hand.png" style="width: 30px; height: 30px;"></b><b>&nbsp;Data stewards and&nbsp;users with Meta Write permission on a data object has the right to edit the business description of that object.&nbsp;</b></p>', 'bg.busidesc', true, '', '', 'admin', now());
