insert into helpcenter_topic(topicid, description, content, context, active, type, external_link, createdby, createdate) values (50083, 'What do the number and Graphs on the homepage represent?', '<p>&#8203;&#8203;&#8203;&#8203;&#8203;&#8203;&#8203;<b id="docs-internal-guid-7ce85783-7fff-8cc8-3320-22aa1fd64388">T</b>he number displays the total count of different data objects like Tables, Reports,&nbsp;Files, and Folders associated with particular user login credentials.&nbsp;</p><p><!--{cke_protected}%3Cmeta%20charset%3D%22utf-8%22%20%2F%3E--></p><p dir="ltr"><b id="docs-internal-guid-7ce85783-7fff-8cc8-3320-22aa1fd64388">T</b>he graphical representation displays the&nbsp;</p><ul dir="ltr"><li><em><b id="docs-internal-guid-7ce85783-7fff-8cc8-3320-22aa1fd64388">Tables</b></em><b>-</b>represents the total number of tables grouped by different databases</li><li>&#8203;&#8203;&#8203;&#8203;&#8203;&#8203;&#8203;<b id="docs-internal-guid-7ce85783-7fff-8cc8-3320-22aa1fd64388"><em>Reports-&nbsp;</em></b>the total number of charts associated with each domain</li><li><b id="docs-internal-guid-7ce85783-7fff-8cc8-3320-22aa1fd64388"><em>Files and Folders</em>-</b>the total number of files and folders in the entire data lake</li></ul>', 'sq.graphs', true, '', '', 'admin', now());


insert into helpcenter_topic(topicid, description, content, context, active, type, external_link, createdby, createdate) values (50084, 'What are database objects in OvalEdge?', '<p>&#8203;&#8203;&#8203;&#8203;&#8203;&#8203;&#8203;<b id="docs-internal-guid-3341c215-7fff-134f-26f4-5149392433d1">O</b>valEdge database objects includes tables,&nbsp;files, and reports.</p><p><!--{cke_protected}%3Cmeta%20charset%3D%22utf-8%22%20%2F%3E--></p>', 'sq.dbobjects', true, '', '', 'admin', now());


insert into helpcenter_topic(topicid, description, content, context, active, type, external_link, createdby, createdate) values (50085, 'What is a content change?', '<p>Contents are the metadata descriptions. Users can&nbsp;request to change&nbsp;the data object contents such as the&nbsp;business description/ associated tags/ terms of a Table, Column, File, Reports, or Business Glossaries. The content in the description and the added tags and terms are important to explore the data and find relationships between them.</p>', 'sq.contentaccess', true, '', '', 'admin', now());
