insert into helpcenter_topic(topicid, description, content, context, active, type, external_link, createdby, createdate) values (50089, 'What is the difference between DAG TAG and Custom Tags?', '<p>&#8203;&#8203;&#8203;&#8203;&#8203;&#8203;&#8203;<b id="docs-internal-guid-21c03ae6-7fff-2cbf-eca7-efa0d6b2535d">G</b>enerally, <strong>Data asset group tags ( DAG Tags)</strong> are used for providing the Ownership, Stewardship, and Authors for associated data objects.</p><p><!--{cke_protected}%3Cmeta%20charset%3D%22utf-8%22%20%2F%3E--></p><p dir="ltr"><img data-cke-saved-src="oecss/images/helpcenter/pointer_hand.png" src="oecss/images/helpcenter/pointer_hand.png" style="width: 20px; height: 20px;">A data object&nbsp;can be assigned only one DAG Tag at a time.</p><p dir="ltr"><strong>Custom&nbsp;Tags</strong> are created by the user for the purpose of organizing a data object in the OvalEdge application. Also, Customized Tags can be formed in the hierarchy for ease of data research, exploring relationships and generating recommendations through algorithms.</p>', 'sq.dagtct', true, '', '', 'admin', now());


insert into helpcenter_topic(topicid, description, content, context, active, type, external_link, createdby, createdate) values (50090, 'How to Multitag a data object?', '<p><!--{cke_protected}%3Cmeta%20charset%3D%22utf-8%22%20%2F%3E--><b id="docs-internal-guid-995baba4-7fff-c369-9797-f2527a5951dc">T</b>o Multi tag a data object:</p><ul><li>Select the Tag that the user wants to tag multiple data objects.</li><li>Click on the nine dots and select the Multi Tagging Option.</li><li>Click the <strong>Recommend tags</strong> button and <strong>Run the AI recommendation</strong> job.<em> A job gets submitted. Only the user role with the Tag role can run this recommendation.</em></li><li>Once the Job completes, refresh the page and the system will show you tag suggestions to tag the objects.</li></ul>', 'sq.multitag', true, '', '', 'admin', now());


insert into helpcenter_topic(topicid, description, content, context, active, type, external_link, createdby, createdate) values (50091, 'What are the most commonly used tags in OvalEdge?', '<p>OvalEdge has 2 different types of tags: <br></p><ul><li>Custom tags are user defined tags that can be re-used in more than one data object to organize the data. <br></li><li>Data Asset Group Tag(DAG Tag) that are unique tags created to Organize data and assign the tagged data assets to specific group of people for analysis.<br></li></ul>', 'sq.tagtype', true, '', '', 'admin', now());


insert into helpcenter_topic(topicid, description, content, context, active, type, external_link, createdby, createdate) values (50092, 'How to adjust the Column relationships manually?', '<p dir="ltr"><b id="docs-internal-guid-1e2bc7f3-7fff-375a-96c5-b6dd86fcab3a">U</b>sers can adjust the column relationships manually through the following steps:&nbsp;</p><ul dir="ltr"><li>Go to the Data catalog module</li><li>Select a table.</li><li>Choose the Column details tab to list all the columns corresponding to the table.</li><li>Click the Relationships and Choose a column.</li><li>Select options to adjust relationships.</li><li>Input the attributes to establish a relationship manually to another column.&nbsp;</li></ul>', 'sq.adjustrelations', true, '', '', 'admin', now());


insert into helpcenter_topic(topicid, description, content, context, active, type, external_link, createdby, createdate) values (50093, 'What are the different locations where users can download data?', '<p><b id="docs-internal-guid-7ef4a6eb-7fff-e48c-eb51-511c2a06d41f">U</b>sers can download data from the Query sheet, Data catalog_Table, Data catalog_Files and, File Manager.</p><p><!--{cke_protected}%3Cmeta%20charset%3D%22utf-8%22%20%2F%3E--></p><p dir="ltr"><b id="docs-internal-guid-3516c9ac-7fff-2b4c-e5ac-f53474601891"><img data-cke-saved-src="oecss/images/helpcenter/pointer_hand.png" src="oecss/images/helpcenter/pointer_hand.png" style="width: 30px; height: 30px;"></b>Users must&nbsp;have minimum Meta Read and Data Preview permissions to download the data.</p>', 'sq.downloaddata', true, '', '', 'admin', now());


insert into helpcenter_topic(topicid, description, content, context, active, type, external_link, createdby, createdate) values (50094, 'What tasks are available in OvalEdge nine box drop-down?', '<p>There is a set of tasks that can be performed by selecting the&nbsp;<img src="oecss/images/helpcenter/ninedots1.png" id="photo-id" style="width: 25px; height: 25px;" data-cke-saved-src="oecss/images/helpcenter/ninedots1.png" class="fr-fil fr-dib">nine-dots drop-down menu. The tasks vary for each application module selected. The below table lists all the tasks available on modules.</p><div><div><table class=" cke_show_border" style="border-collapse:collapse; border-style:solid; border-color:#a3a3a3; border-width:1pt; border:undefined;" summary="" title="" valign="top"><tbody><tr><td style="border-style:solid; border-color:#a3a3a3; border-width:1pt; vertical-align:top; width:1.2979in; padding:4pt 4pt 4pt 4pt;"><p style="margin:0in;"><strong><span style="font-size:11.0pt;"><span style="font-family:Calibri;">Module</span></span></strong></p></td><td style="border-style:solid; border-color:#a3a3a3; border-width:1pt; vertical-align:top; width:2.9722in; padding:4pt 4pt 4pt 4pt;"><p style="margin:0in;"><strong><span style="font-size:11.0pt;"><span style="font-family:Calibri;">Tasks</span></span></strong></p></td><td style="border-style:solid; border-color:#a3a3a3; border-width:1pt; vertical-align:top; width:4.2965in; padding:4pt 4pt 4pt 4pt;"><p style="margin:0in;"><strong><span style="font-size:11.0pt;"><span style="font-family:Calibri;">Description</span></span></strong></p></td></tr><tr><td style="border-style:solid; border-color:#a3a3a3; border-width:1pt; vertical-align:top; width:1.2979in; padding:4pt 4pt 4pt 4pt;"><p style="margin:0in;"><span style="font-size:11.0pt;"><span style="font-family:Calibri;">Data Catalog</span></span></p></td><td style="border-style:solid; border-color:#a3a3a3; border-width:1pt; vertical-align:top; width:2.9722in; padding:4pt 4pt 4pt 4pt;"><p s